css:
@-moz-document
 url-prefix(http://)
,url-prefix(https://)
,url-prefix(file://)
,url-prefix(chm:file://)
{
div,p, li, blockquote, td 
{
   font-size: 16px !important;
   font-family: Tahoma,sans-serif !important;
   line-height: 1.1 !important;
/** /     color: black !important; /**/
}
h1,h2,h3,h4, a, span { font-family: Tahoma,sans-serif !important; }

/* i hate justified text */  
 *[align="justify"]
,*[style*="justify"]
{
   text-align: left !important}
}
