css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("isohunt.com") {

body,
td {
background-color: #bec7cf !important}

HTML[lang=en],
TD[class=row4],
TD[class=navbkg],
TD[class=ihHead] {
background-color: #87a2bc !important}

TD[width="84%"] {
background-color: #bec7cf !important}

SPAN[class=aTab],
LI {
color: #004480 !important}

.SL_zone,                            /*sponsors1*/
.SERP_zone,                          /*sponsors2*/
.Listing,                            /*sponsors3*/
div#ad,                              /*ads tex*/
a[href*="banner"],                   /*banners text*/
img[src*="logo"],                    /*logo*/
img[src*="banner"],                  /*banners ads*/
img[src*="cpxinteractive"],          /*front page ads*/
script[src*="ad"],                   /*ads script*/
iframe[src*="iframead"],             /*iframes ads*/
div[style="border: 1px solid orange; padding: 5px;"]   /*ads*/
,HTML > BODY > A > IMG
,IMG[height="90"][width="120"]
,IFRAME[height="600"][frameborder="0"][width="160"]
,IFRAME[height="90"][frameborder="0"]
{display:none!important}
}
