css:
@-moz-document domain("netvibes.com") {
html, body, div, p, form {font-family: "tahoma"!important; font-size: 13px !important;unicode-bidi:embed} 

a {color: #0078ae !important; }
a:hover {color: #75b2d5 !important; background:none !important; border-bottom: 1px dotted #ddd !important;}

.moduleHeader .title a { color: #f5f5f5 !important; text-decoration: none !important; font-size: 14px !important; font-variant: small-caps;  }

span.unread {color: #cdcdcd !important;}
span.nv-treeview-unread {direction: ltr !important; text-align:left!important;}
span {font-family: "tahoma";unicode-bidi: embed!important;direction:rtl}
span.entry-title-text {font-size:12px !important;font-weight: bold;}

#top{ margin-top: -23px !important;}
#top:hover{ margin-top: 0px !important;}
#topLinks{height:0 !important;}
#footer {display:none!important}

.nv-pager a.next, .nv-pager a.prev {display: none !important}

#divTabs ul li.selected {border: none !important;}

#divTabsOuter {border-top: none !important;}

td.linkBookmark a, td.linkBookmark a:hover, td.linkBookmark a:visited {color: #0078ae !important; border-bottom: none !important;}

td.linkBookmark a:hover {color: #75b2d5 !important;}

.bookmarks .links .linkBookmark {
background: transparent url(http://cdn.netvibes.com/themes/fluo/img/bullet.png?v=135) no-repeat scroll left center !important;}




#splash {text-align:center;margin:0 auto;}
body #header #topLinks a { color:#AAAAAA }
body #pageTitle { color:#AAAAAA }
body #header #aNewTab { color:#AAAAAA }
#header { background-image: url(http://cdn.netvibes.com/img/headers/ornament.png); background-repeat: repeat; background-position: top center }
body { background-color: #E6E6E6 }
#pageTitle { display: none; }
}
