@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.freerepublic.com") {

div#header strong { display: none !important; }
 
div.meta { display: none !important; }

body.index-page h3{
    font-size : medium !important;
    margin-bottom : 0 !important;
    margin-top : 0.5em !important; }

body.index-page ul.listing div.text {
    margin-top : 0em !important; }
    
body.index-page ul.listing div.content {
    margin-left : 0 !important; }

p a { display: none !important; }

div.n2 { display: none !important; }

div.a2 { display: none !important; }

a { outline: 0 !important; }

}
