css:
@-moz-document domain('workflowy.com') {
BODY
{
	padding: 0;
	margin: 0;
	font-size: 16px;
	position: relative;
	font-family: Helvetica;
	color: #FFFFFF !important;
	background: #1e1e1e !important;
}


/*remove line between breadcrumbs and thingy.*/
.selected {
border-top: none;
}

/*hide add button*/
.addButton {
  color:#121212;
}

/*change breadcrumb arrows*/
.parentArrow:before{
  content:'Â·';
    }
 

/*green background!*/
.children .project.task{
        background:-webkit-linear-gradient(top, #009900, #008800);
background:-moz-linear-gradient(top, #009900, #008800);
  padding:10px 0 !important;
      border-bottom:#007700 1px solid;
      border-top:#00aa00 1px solid;
      }
    

/*yellow background!*/
.children .project{
        background:-webkit-linear-gradient(top, #449900, #348900);
background:-moz-linear-gradient(top, #449900, #348900);
  padding:10px 0 !important;
      border-bottom:#337700 1px solid;
      border-top:#44aa00 1px solid;
      }

/*no background when open!*/
.children .project.open{
        background:none;
  padding:10px 0 !important;
      border-bottom:none;
      border-top:none;
      }

/*no background when selected!*/
.children .project.selected,  .project.parent{
        background:none;
  padding:10px 0 !important;
      border-bottom:none;
      border-top:none;
      }          

.project.parent, .project.open.parent {
  padding:0 !important;
    }
.children .project.open .children {
      border-left:none;       
                  }

.children .project.open>.notes {
      padding-bottom:10px;       
                  }

.highlight
{
	height: 100%;
	position: absolute;
	width: 100%;
  	left:0;
    padding:0;
  	top:0;
  border-radius:0;
}

.moving>.highlight, .highlighted>.highlight
{
	background: rgba(255,255,255,.2) !important;
}
  
.moving>.highlight, .highlighted.moveHovered>.highlight
{
	background: rgba(255,255,255,.2) !important;
}

.noteEditor > textarea{  
	padding: 0;
	margin: 0;
	position: relative;
	color: rgba(255,255,255,.5) !important;    
}


.notes > .content{
    color: rgba(255,255,255,.5) !important;
}

.page {
    max-width:1000px !important;
    position:relative;
    background: #121212 !important;

}


.parent > .name > .content
{
    color: #009900 !important;
}





a.bullet{
    color: rgba(0,0,0,0) !important;
  	text-shadow:none;
  	background:none; /*no circles for stuff with content*/
}





span.plus{
    color: #cfcfcf !important;
}

span.text{
    color: #cfcfcf !important;
}


div.top.edge{
    display: none;
}

div.bottom.edge{
    display: none;
}

div.left.edge{
    display: none;
}

div.right.edge{
    display: none;
}

DIV.top.right.corner
{
    display: none;
}

DIV.top.left.corner
{
    display: none;
}

DIV.bottom.right.corner
{
    display: none;
}

DIV.bottom.left.corner
{
    display: none;
}
div#share_buttons{
    display: none;
}
}
