@namespace url(http://www.w3.org/1999/xhtml);


/************************************************************** 
 *	PrÃ©fixe de l'URL (si vous passez par une autre adresse, modifiez la ligne)
 **************************************************************/ 
@-moz-document url-prefix("http://www.centaurius.net/alpha/index.php?page=forum") {


/**************************************************************
 *	PARTIES FONCEES (gris foncÃ©)
 **************************************************************/
.TopicUser
{
background-image:none!important;
background-color:#5F5F5F!important;
color:black!important;
}

/**************************************************************
 *	PARTIES CLAIRES (gris clair)
 **************************************************************/
.TopicPost
{
background-image:none!important;
background-color:#8F8F8F!important;
color:black!important;
}

/************************************************************** 
 *	BLOCS 'Quote' & 'Code'
 **************************************************************/
.forQuote
{
background-color:#5F5F5F!important;
color:white!important;
border-color:black!important;
}
.forCode
{
background-color:white!important;
color:black!important;
border-color:grey!important;
}

/**************************************************************
 *	POST-IT & ANNONCES (rouge)
 **************************************************************/
.Section h6
{
color:#8F0000!important;
font-weight:bold!important;
}

/************************************************************** 
 *	LIENS (blanc)
 **************************************************************/
.TopicUser a,
.TopicPost a
{
color:white!important;
}
.button,
.LibButton
{
color:white!important;
padding:5px!important;
border-style:solid!important;
border-width:1px!important;
border-color:grey!important;
background-color:#353849!important;
-moz-border-radius:5px;
}

/************************************************************** 
 *	LIENS SURVOLES
 **************************************************************/
.TopicUser a:hover,
.TopicPost a:hover
{
color:white!important;
text-decoration:underline!important;
}
.button:hover,
.LibButton:hover
{
color:#65A6DC!important;
background-color:black!important;
text-decoration:none!important;
border-color:#65A6DC!important;
}


}
