css:
@-moz-document url-prefix("http://penny-arcade.com/report"), url-prefix("http://www.penny-arcade.com/report") {
#main {
	background: #101010 !important;
}

body {
	background: #101010 !important;
  	font-family: Helvetica, Arial, Verdana, sans-serif;
    font-weight: normal;
  	color: #999 !important;
}

#article .copy h1,
#article .copy h2,
#article .copy h3,
#article .copy h4,
#article .copy h5,
#article .copy h6 {
  	font-family: Helvetica, Arial, Verdana, sans-serif;
    font-weight: bold;
	color: #fff !important;
}

#article .img .credit {
	background: #0d0d0d !important;
	color: #454545 !important;
}

#article .img {
	background: #101010 !important;
}

.headlines ul li {
	background: #101010 !important;
}
.headlines li a {
	color: #999 !important;
}
.headlines li p,
.related li {background: #101010 !important;}

.related a {
	color: #ddd !important;
}
#nav {background: #101010 !important;}
#nav li a {
	color: #eee !important;
}
#articleNav {
	background: #101010 !important;
}
#articleNav .content {
	background: #101010 !important;
}
#articleNav .btnCmnts {
	border-right: 2px solid #333 !important;
}


#articleNav .meta li {
	color: #eee !important;
}

#hero {
	background: #101010 !important;
}

#article .copy {
 	font-size: 17px;
}
#article .copy blockquote {
	background: #101010 !important;
}
#article .copy > ul > li,
#article .copy > ol > li {
	background: #101010 !important;
}

#article .copy .caption,
#article .copy .caption {
	background: #101010 !important;
}
#article .copy #game {
	background: #101010 !important;
}
#article .copy #game .meta {
	background: #404040 !important;
}
#article .copy #game ul {
	border-bottom: 2px solid #333 !important;
}



#articles > li {
	background: #101010 !important;
}
#articles .text h2 a {
	display: block;
	color: #ddd !important;
}
#articles .img.old {background: #101010 !important;}

.slides #heroNav li {
	background: #101010 !important;
}

.slides .hero .text {
	background: #101010 !important;
}
.slides .hero .text h1 a {
	color: #ddd !important;
}
.slides #heroNav li a {
	color: #888 !important;
}
.slides #heroNav li.activeSlide,
.slides #heroNav li.activeSlide a,
.slides #heroNav li:hover,
.slides #heroNav li a:hover {
	color: #999 !important;
}

#categories {
	background: #101010 !important;
}
#category li li {
	background: #101010 !important;
}
#category li li a {
	color: #ddd !important;
}

#brandNews li li a {
	color: #ddd !important;	
}
#brandNews li li strong {
	color: #ddd !important;
}

.cutInfo {
	background: #101010 !important;
}
.cutInfo .img.old {background: #101010 !important;}
.cutInfo.the-cut {
	background: #101010 !important;
}
.cutInfo.the-cut li {
	color: #999 !important;
}

#header {
	background: #101010 !important;
}

#header h1 a {
  	background: url(http://hw1.pa-cdn.com/par/img/sprite_main2.png) -728px -37px no-repeat !important;
	float: left;
	display: inline;
	width: 212px;
	height: 51px;
	clear: both;
}

#articleNav .share {
	border-right: 2px solid #333 !important;
	border-left:  2px solid #333 !important;
}


#article .copy p strong {
	font-family: Helvetica, Arial, Verdana, sans-serif;
	font-weight: bold;
	font-style: normal;
}
#article .copy p em {
	font-family: Helvetica, Arial, Verdana, sans-serif;
	font-style: italic;
	font-weight: normal;
}
#article .copy p em strong,
#article .copy p strong em {
	font-family: Helvetica, Arial, Verdana, sans-serif;
	font-style: italic;
	font-weight: bold;
}
#article .copy q {
	font-family: Helvetica, Arial, Verdana, sans-serif;
    font-style: italic;
}

.feature .btnCmnts a {background: #f05a29 url(http://i.imgur.com/CpjGFN4.png) 0 0 no-repeat;}
.news .btnCmnts a    {background: #2b93bf url(http://i.imgur.com/CpjGFN4.png) 0 0 no-repeat;}
.play .btnCmnts a    {background: #d23535 url(http://i.imgur.com/CpjGFN4.png) 0 0 no-repeat;}
.review .btnCmnts a  {background: #66347d url(http://i.imgur.com/CpjGFN4.png) 0 0 no-repeat;}
.the-cut .btnCmnts a {background: #8ca82f url(http://i.imgur.com/CpjGFN4.png) 0 0 no-repeat;}
.feature#articleNav .btnCmnts a {background: #f05a29 url(http://i.imgur.com/CpjGFN4.png) 0 0 no-repeat;}
.news#articleNav .btnCmnts a    {background: #2b93bf url(http://i.imgur.com/CpjGFN4.png) 0 0 no-repeat;}
.play#articleNav .btnCmnts a    {background: #d23535 url(http://i.imgur.com/CpjGFN4.png) 0 0 no-repeat;}
.review#articleNav .btnCmnts a  {background: #66347d url(http://i.imgur.com/CpjGFN4.png) 0 0 no-repeat;}
.the-cut#articleNav .btnCmnts a {background: #8ca82f url(http://i.imgur.com/CpjGFN4.png) 0 0 no-repeat;}

#article .copy .caption p {
	font-family: Helvetica, Arial, Verdana, sans-serif;
	background: #101010;
	color: #eee;
}

}

@-moz-document url-prefix("http://forums.penny-arcade.com/") {
.EmbedPAReport .Item {
  background-color: #101010 !important;
}

.EmbedPAReport .Item .Message {
  color: #999 !important;
  font-family: Helvetica, Arial, Verdana, sans-serif;
  font-size: 14px;

}

.EmbedPAReport a {
  color: #f04911 !important;
}

.EmbedPAReport .EmbedCommentForm {
  background-color: #101010 !important;
}

.EmbedPAReport h2 {
  background-color: #111 !important;
  border-bottom: 1px solid #000 !important;
  color: #999 !important;
}

.EmbedPAReport .PACommentHeading .OrganizeComments {
  color: #999 !important;
}

.EmbedPAReport .EmbedCommentForm .TextBox {
  background-color: #101010 !important;
  color: #999 !important;
}
.EmbedPAReport .Pager {
  background-color: #101010 !important;
}


}
