css:
@-moz-document domain('feedly.com'){

/*Fonts and Theme*/

*{ font-family: Arial!important;}

/*layouts*/

#feedlyFrame{ padding-top: 30px!important;width: 99%!important;}
#feedlyPage,#feedlyPart,#mainArea{ width: 100% !important;}
#sideArea,#aboutArea{ display: none!important;}
#mainBar{ margin-right: 15px!important;}
#feedlyPageHeader{ 
 /*display: none!important;*/
 margin-right: 0!important;
 }
.popup{ margin-top: 8px; }

#feedlyPart0.area{
 padding-right: 30px!important; 
 }
 
/*article*/

h2{
 text-transform: capitalize!important;
 }

.lastModified{
 color: #999!important;
 }

.entryHolder {
 width: 100%!important;
 max-width: 100%!important;
 }

.u100entry a.title {font-size:18px!important;}
.u100entry a.h1 {font-size:18px!important;}
 
.condensed .inlineFrame{
 padding-left: 34px!important;
 padding-right: 34px!important;
 }

.entryBody img,
.entryBody iframe{
 margin: 33px 0!important;
 }

.inlineFrame .entryBody img{
 float: none!important;
 max-width: 100%!important;
 }

.u100Entry{
 width: 100%!important;
 min-height: 0!important;
 }

.u100Entry.selectedEntry {
 width: 100%!important;
 }

.u100frame{
 }

.entryList .u4Entry,
#mainArea .u10Entry{
 max-width: none!important;
 }

/*NavigationBar*/

#floatingBar{
 padding: 0!important;
 left: 0;
 top: 0;
 display: block!important;
 background-color: #333!important;
 width: 100%!important;
 height: 30px!important;
 }

#floatingBar h1{
 color: whitesmoke!important;
 padding-left: 33px!important;
 padding-bottom: 0!important;
 margin-top: 3px!important;
 }

#floatingBar .pageActionBar{
 margin-top: 0!important;
 margin-right: 23px!important;
 height: 30px!important;
 background-color: #aaa!important;
 }
 
#floatingBar .pageActionBar img{
 margin-top: 3px!important;
 }

#floatingTitleBar{
 margin-left: -7px!important;
 border-bottom: none!important;
 }
 
/*sidebar*/

#feedlyTabs{
 }
  
#feedlyTabs .list{
 padding-left: 0!important;
 transition-duration: 0s!important;  
 }
 
.tab .feedIndexTitleHolder{
 max-height: 25px!important;
 }
 
#feedlyTabs .label{
 text-transform: capitalize!important;
 }

.target:hover{
 background-color: rgba(0,0,0,0.1)!important;
 }
 
#feedlyTabsHolder #feedlyTabs .target.selected{
 }

#feedlyTabs .tab:last-child{
 margin-bottom: 10px!important;
 }
 
/*options*/

.favicon,
#feedlyTabsHolder .favicon,
#feedlyTabsHolder:hover .favicon{
 opacity: 1!important;
 }

}
