css:
@-moz-document domain("www.fanduel.com") {
div.buttonbar.creation { margin: 0px; border: 0px; border-radius: 0px; background: transparent; }
#scroller-cont { border-radius:0x;border:0px;padding: 0px;}
#scrollport { margin: 0px; padding:0px; }
#pager-cont { display:none; }


#content_center div#nav { 
  height: auto;
  width: 250px;
  position: fixed;
  top: 8px;
  z-index:991;
}
#content_center {
  margin-top: 0px;
    z-index:991;
  top: -18px !important;
  }
#content_center div {
    z-index:800;
  }
header { z-index: 990 }
#nav:first-child:before { 

  color: #ddd;
  display: block;
  content: "\2630";
  font-size:21pt;
  width: 1em;
  padding: 5px;
  height: 22px;
}
#nav .mainTabContainer .mymenu div.header { 
  float: none; width: auto;  
  margin: 0px;
}
#nav:hover .mainTabContainer a, 
#nav:hover .mainTabContainer .mymenu a, 
#nav:hover .mainTabContainer .mymenu .header .menu a
{
  display: block;
}
#nav .mainTabContainer .mymenu { float: none; }
#nav .mainTabContainer a, 
#nav .mainTabContainer .mymenu a, 
#nav .mainTabContainer .mymenu .header .menu a 
  { 
  float:none;
  margin-top: 0px; height: 18px;padding-top:2px; 
  background: #222;
  color: #ddd;
  border-bottom: 1px solid #ddd;
  padding: 8px;
  border-radius: 0px;
  display: none;
  position: static;
}
#nav .mainTabContainer .mymenu .header .menu {
  position: absolute;
  left: 250px;
  margin-top: -35px;
}
header h1 { 
  position: relative;
  left: 50px; 
}
.fdpbalance a { display:none }
.fdpbalance div { display:none }
.fdpbalance {
  position: relative;
  right: 82px;
   top: -11px
  }
.balance {
  position: relative;
   top: -2px
  }
li.balance a { position:relative; top: 6px; }
li.balance a:last-child { display:none; }
header { height: 40px; }
 header h1 { margin-top: 5px; }
#content_center { top: -10px }
}
