css:
@-moz-document domain("facebook.com") {
* {background-color: transparent !important}
BODY, .timelineLayout, #contentArea, #rightCol, .fbPhotoSnowlift .rhc, .fbChatSidebar .fbChatSidebarMessage, .fbTimelineSection {
  background-color: #000 !important
}

/* fb plugin */
BODY.plugin {
  background-color: #333 !important
}
BODY.plugin .uiHeader {
  background-color: #444 !important
}
BODY.plugin .postText {
  color: #bbb !important
}

/* login */
BODY.fbIndex #content > DIV > DIV > DIV {
  background-color: transparent !important
}

/*inputs & send message form */
TEXTAREA, INPUT[type="text"], INPUT[type="password"], SELECT, div.fbChatSidebarDropdown .uiCloseButton, .uiSearchInput, .uiTypeahead .wrap, #headNav > DIV[role="search"] {
  background-color: #333 !important; 
  color: #ccc !important
}

.uiSearchInput button {
  background-color: #222 !important
}
#headNav > DIV[role="search"] {
  box-shadow: none !important
}

.uiSearchInput {
  border-color: #888 !important
}
.uiSearchInput SPAN {
   border: none !important
}

.textInput SPAN {
  color: #ccc !important
}

.UFIPhotoAttachLinkWrapper {
  right: 1px !important
}


/* comments box */
.UFIRow, .timelineUnitContainer UL, .timelineUnitContainer LI {
  background-color: #CCCDD1 !important
}

/* messages dropdown */
.uiHeader, .notifNegativeBase .fbJewelFlyout .jewelFooter A {
    background-color: #ccc !important
}

/* sidebar */
.fbFeedTickerStory, .fbChatSidebar {
  background-color: #bbb !important
}
.fbFeedTicker .tickerStoryClickable:hover {
  background-color: #ddd !important
}

/* left column & stories */
#leftCol, ._4-u2, DIV[role="article"] {
  background-color: #ccc !important
}
._4-u2 .fcg, ._4-u2 ._5ptz {
  color: #777 !important
}

/* timeline stuff */
.uiScrollableAreaContent, .fbTimelineFeedbackHeader *, .timelineReportContainer, .timelineUnitContainer, .timelineReportContainer DIV[role="article"] A, .fbTimelineScrubber, .fbTimelineUFI, .uiBoxWhite, .aboveUnitContent {
    background-color: #d4d4d4 !important
}
.timelineReportContainer DIV[role="article"] A {
  color: #3B5998 !important
}
.timelineReportContainer DIV[role="article"] LI {
  color: inherit !important
}


.fbTimelineStickyHeader .back {
  background-color: #afafaf !important
}
.shareUnit > A > DIV {
  background-color: #ddd !important
}


/* wall */
.userContentWrapper .userContent+DIV>DIV {
  background-color: #d4d4d4 !important
}


/* popup chat area */
.fbNubFlyoutHeader, .fbNubFlyoutBody, .fbNubFlyoutFooter, .fbNubFlyoutAttachments {
  background-color: #ddd !important
}


/* messages */
.uiScrollableAreaContent .webMessengerMessageGroup A {
  background: transparent !important
}

/* messages timestamps */
.webMessengerMessageGroup A ABBR, .seen {
  color: #777 !important
}

/* send message area */
#pagelet_web_messenger > DIV > DIV > DIV:nth-child(2) > DIV > DIV:last-child > DIV:last-child > DIV > DIV:first-child > DIV:last-child {
  background-color: #999 !important
}
#pagelet_web_messenger > DIV > DIV > DIV:nth-child(2) > DIV > DIV:last-child > DIV:last-child > DIV > DIV:first-child > DIV:last-child > DIV:first-child,
#pagelet_web_messenger > DIV > DIV > DIV:nth-child(2) > DIV > DIV:last-child > DIV:last-child > DIV > DIV:first-child > DIV:last-child > DIV:first-child TEXTAREA {
  background-color: #333 !important; color: #ccc !important
}
#pagelet_web_messenger > DIV > DIV > DIV:nth-child(2) > DIV > DIV:last-child > DIV:last-child > DIV > DIV:first-child > DIV:last-child > DIV:last-child {
  background-color: #555 !important; color: #ccc !important
}
#pagelet_web_messenger ._1rw {
  background-color: #bbb !important;
}
#pagelet_web_messenger ._1rw * {
  color: #555 !important;
}


/* pic container */
.fbxPhoto .fbPhotoImageStage .stageContainer {
  background-color: #ccc !important
}


/* tag layer */
.uiTypeaheadView UL {
  background-color: #ddd !important
}

/* popup layer */
DIV[role="dialog"] > DIV[role="dialog"] {
  background-color: #ddd !important
}
DIV[role="dialog"] ._3ixn {
  background-color: #000 !important;
  opacity: 0.8;
}

.uiBoxGray {
  background-color: #ccc !important
}

.fbPhotoSnowliftPopup {
  border: 1px solid #222
}

/* text color in posts */
.uiStream .uiAttachmentDesc, .uiStream .uiStreamPassive, .uiStream .uiStreamRobotextBeforeMessage, .uiStream .uiAdminAttribution, .attachmentUnit, .shareUnit {
  color: rgb(63, 63, 63) !important
}
.uiStreamFooter, .uiStreamSource, .uiStreamSource A, .uiStreamFooter .uiStreamSource A, .uiStreamAdditionalLogging, .uiStreamAdditionalLogging A, .uiStreamPrivacyContainer, .uiLinkSubtle {
  color: rgb(110, 110, 110) !important
}

/* buttons */
.buttonItem {
  background-color: #ddd !important;
}

/* context menus */
.uiContextualLayer ._53ij, .uiContextualLayer ._54ng, .uiToggleFlyout {
  background-color: #ccc !important;
}

.uiContextualLayer ._54ng A {
  border: none !important;
}

/* tooltips */
.uiTooltipX .tooltipContent {
  background-color: #000 !important;
}

/* misc */
.wmMasterView .uiList, ._ksg > DIV > .clearfix > .clearfix {
  background-color: #ccc !important
}

._ksg, .uiScrollableAreaContent > DIV, #contentCol {
  background-color: transparent !important
}

.shareRedesignText, .shareRedesignContainer, .shareRedesign .shareMediaVideo {
 background-color: #ccc !important
}

#pageFooter {
  background-color: transparent !important
}

.fbxPhoto {
  background-color: #000 !important;
}


}
