css:
/*@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);



@namespace url(http://www.w3.org/1999/xhtml);*/



@-moz-document url("about:Home"), url("about:home") {



#sync, #brandLogo, #snippetContainer, #searchEngineLogo, #restorePreviousSessionSeparator



{ display: none !important; }



/* bottom icons */



.launchButton::before


{ display: none !important; }


html
{ background: #0F0D00 url("http://i42.tinypic.com/2nj8z2g.jpg") no-repeat center fixed !important; 



background-size: cover !important; }


body



{ background: transparent !important; }



#contentContainer


{ background: none !important;


margin: 0 auto !important; }



#searchContainer



{ -moz-appearance: none !important; 



margin: 0 auto !important;



width: 500px !important;



padding: 90px 100px 120px 100px !important;



border-radius: 4px !important;



border: 1px solid !important;



border-top-color: rgba(162,146,116,.5) !important;



border-right-color: rgba(98,88,79,.7) !important;



border-bottom-color: rgba(40,34,26,.8) !important;



border-left-color: rgba(47,43,34, .6) !important;



background: -moz-radial-gradient(center top, rgba(255,255,255, .2), transparent), rgba(73,64,52, .3) !important;



box-shadow: -2px 4px 14px 2px rgba(0,0,0, .6) !important; }




#searchText, #searchSubmit


{ -moz-appearance: none !important; 


font-size: 15px !important;



color: black !important;



text-shadow: 1px 2px 8px #97947F !important;



font-weight: bold !important;



font-family: Segoe UI, sans-serif !important;



padding: 0px !important;



outline: 0 none !important;



border-radius: 4px !important;



border: 1px solid !important;



border-left-color: #1C1916 !important;



border-top-color: #1C1916 !important; 



border-bottom-color: #6B6559 !important;



border-right-color: #594F46 !important;



box-shadow: 0px 4px 8px rgba(121,117,109, .4) !important; }




#searchText, #searchSubmit



{ background: -moz-radial-gradient(center top,#9C917E, rgba(73,83,87, .4), transparent) !important; }





#searchText:hover, #searchText:focus



{ background: -moz-radial-gradient(center top,#B7AA93, rgba(73,83,87, .2), rgba(110,125,102, .2)) !important; }




#searchSubmit



{ margin-left: 2px !important;



padding: 0px 5px !important;



font-weight: normal !important;



color:DodgerBlue !important;



text-shadow: 1px 0px 0px #000, 2px 2px 2px rgba(86,97,102, .5) !important;



font-family: Papyrus /*Enviro *//*Goudy Old Style*/ /*Viner Hand ITC*/ !important; }







#searchSubmit:hover



{ text-shadow: 1px 0px 0px #000, 1px 2px 4px #B7AA93 !important;



background: -moz-radial-gradient(center top,#B7AA93, rgba(162,163,146, .3), transparent) !important; }



/* ========== links bar */


#launcher button


{ border: 1px solid black !important;


box-shadow: inset rgba(0,0,0, 0.4) -1px 6px 12px 2px, 2px 4px 8px #000 !important; }


#launcher, #restorePreviousSession


{ background: transparent !important;



border: none !important;



box-shadow: none !important; } 




#launcher


{ position: fixed !important;


bottom: 40% !important;



margin-left: 6px !important;



display: block !important; 



text-align: center !important; 



white-space: nowrap !important; }







.launchButton



{ background: transparent !important;



border: none !important;



font-size: 16px !important;



color: #088CE5 !important;



box-shadow: inset rgba(190,190,190, .3) -1px 6px 12px 2px, 2px 4px 8px rgba(0,0,0, .6) !important; } }
