css:
html {
overflow-y: auto;
}

body {
background-color: #000;
}

#brandingBar {
background-image:url(http://i.imgur.com/erZFE.jpg)!important;background-attachment:fixed!important;background-size:100% 100%;
border-bottom: 1px solid #aaa;
}

#brandingBar .middlecolumn {
color: #FFF;
}

#brandingBar .rightcolumn {
color: #FFF;
}

#brandingBar .rightcolumn a {
color: #FFF;
}

#brandingBar .rightcolumn .user_activator:hover {
background: rgba(255,255,255,0.1);
}

.stationChangeSelectorNoMenu p {
width: auto;
max-width: none;
}

#playerBar {
background: -webkit-linear-gradient(top, #0284B8 20%, #000000);
}

#playerBar .searchBox {
margin-left: 18px;
background-image: none;
padding: 0;
}

#playerBar .searchBox:before {
content: "New Station";
color: #eee;
font-weight: bold;
background: -webkit-linear-gradient(top, #000000 , #0284B8);
position: relative;
top: 6px;
left: 5px;
padding: 2px 5px 3px 6px;
border-top-left-radius: 3px;
border-bottom-left-radius: 3px;
}

#playerBar .searchInput {
width: 100%;
background-color: #FFF;
margin-left: 2px;
padding: 3px 5px 3px 5px;
border-bottom-right-radius: 2px;
border-top-right-radius: 2px;
}

#searchPopupNavPosition {
left: 80px;
}

#mainContainer {
background-image:url(http://i.imgur.com/erZFE.jpg)!important;background-attachment:fixed!important;background-size:100% 100%;
}

.adSupported-layout #adLayout {
min-width: 666px;
width: 70% !important;
}

#mainContentContainer {
border: 1px solid #b00808;
width: 100%;
/*height: 767px; */
background: #2b2b2b;
}

.contentContainer .top {
background-color: #000;opacity:0;
border-top: 1px solid #2b2b2b;
}

.contentnav {
background-color: #2b2b2b;opacity:0.5;
border: 0;
}


.stationContent {
background-color: #2b2b2b;
border-right: 1px solid #b00808;
border-top: 1px solid #b00808;
}

.stationListItem .stationName {
color: #00dfff;
text-shadow: 1px 1px 0 #333;
}

.stationListItem .stationName_hover {
background-color: #2b2b2b;
}

.stationListItem.selected {
background: -webkit-linear-gradient(top, #888 , #333);
border-top: 1px solid transparent;
border-bottom: 1px solid transparent;
}

.stationListItem.selected .options {
background: #CCC;
}

.btn_bg_sort {
color: #00dfff;
text-shadow: 1px 1px 0 #333;
background: -webkit-linear-gradient(top, #7b7b7b,#5b5b5b);
}

.btn_bg_sort:hover {
background: -webkit-linear-gradient(top, #9b9b9b,#7b7b7b);
}

.btn_bg_sort.selected {
background: -webkit-linear-gradient(top, #5b5b5b,#3b3b3b);
}

#stationSortDate {
border-top: 1px solid #999;
}

#stationSortAlpha {
border-top: 1px solid #999;
border-left: 1px solid #999;
}

.slidesForeground .slides {
width: 200%;
left: -100%;
padding: 0;
}

.slidesForeground .slides .stationSlides {
left: 220px !important;
width: 100% !important;
}

.slidesBackground {
border-top: 1px solid #b00808;
width: 100%;
background: #1b1b1b;
}

.slidesBackground img {
opacity: 0;
}

.slidesBackground div img {
opacity: 0;
}

#currentSlideArrow {
opacity: 0;
width: 0;
height: 0;
}

#trackInfo {
background: #2b2b2b;
border-top: 1px solid #b00808;
border-bottom: 1px solid #b00808;
margin-top: -4px;
}

#trackInfo .info {
color: #eee;
text-shadow: 1px 1px 0 #3b3b3b;
}

#trackInfo .info a {
color: #00dfff;
}

.fxCol-cont-5 {
background: none;
}

.fxCol-cont-1 {
background: none;
}

#trackDetail a {
color: #DDD;
}

.detail {
height: 190px;
border-left: 1px solid #b00808;
background: #2b2b2b;
}

.detail .heading {
color: #00dfff;
text-shadow: 1px 1px 0 #9b3b3b;
}

.detail .itemContent {
color: #FFF;
}

.detail .divider {
border-bottom: 1px solid #b00808;
}

#ad_container {
opacity: 0;
width: 0 !important;
height: 0 !important;
}

#footer {
width: 100%;
margin: auto auto 10px auto;
}

#registeredNavFooter a {
color: #DDD;
}

#footer .legal {
color: #AAA;
}
