css:
@namespace url(http://www.w3.org/1999/xhtml);

/* ------------------------------------	*
 * Style:   DSGen2-Grey			*
 * Author:  TrihornX			*
 * Special Mention:  Stafos		*
 * ------------------------------------	*/

@-moz-document domain("dragon-soldier.net"){

/* General background settings */
body {
background: #333333 !important;
padding-top: 30px !important;
}

/* removed content (bg,logo,ads) */
html>body>div[role="banner"] {display: none !important;}
html>body>center>table[align="center"] {display: none !important;}
html>body>div[role="complimentary"] {background-image: none !important;
background: black !important;
border: 1px dashed !important;
padding: 5px 0 !important;
position: fixed !important;
top: 0pt !important;
width: 100% !important;
text-align: center !important;}
div[class="head center-text white bold"] {background-image: none !important;
border: 1px dashed !important;
background: black !important;}
html>body>center>table[class="td"]>tbody>tr>td[align="left"] {background-image: none !important;
top: 0px !important;
border: 1px dashed !important;
position: fixed !important;
background: black !important;
}
table[class="center-block"][width="800"]{
margin-top: 20px !important;
}

/** border **/
html>body>center>table[class="td"]>tbody>tr>td>table>tbody>tr>td>table>tbody>tr
{border: none !important;}

/** status page **/
td[background="http://static.ds22.dragon-soldier.net/images/700bar.jpg"] {
background-image: none !important;
background-color: black !important;
padding-top: 1px !important;
border: 1px solid grey !important;}


/** Battle **/
tr[style="background:url('http://static.ds22.dragon-soldier.net/images/700bar.jpg')"] {
background-image: none !important;
background-color: black !important;
padding-top: 1px !important;
border: 1px solid grey !important;}
div[class="centerbox ui-helper-clearfix"][role="main"]{
height: 600px !important;
overflow: auto !important;
}


/* Clan Page */
td[background="http://static.ds22.dragon-soldier.net/images/menu.jpg"]{background-image: none !important;
border: 1px solid !important;
background-color: black !important;}
td[bgcolor="#61544E"][height="19"]{background-image: none !important;
border: 1px solid !important;
background-color: black !important;}
table[id="memberList"]{
background-color: black !important;
}

/* Updates Page */
table[class="td"]{
background-image: none !important;
background-color: black !important;
padding: 5px 0 !important;
}

/* Login Screen */
html>body>table>tbody>tr>td>div>img
{display: none !important;}
td[style=" height: 18px; width: 700px; background-image: url(http://static.ds22.dragon-soldier.net/images/700bar.jpg);"]{
background-image: none !important;
background-color: black !important;
padding: 5px 0 !important;}


/* defaults for all links */
a:link {color: #CCCCCC !important;}
a:visited {color: #CCCCCC !important;}
a:hover {color: #CCCCCC !important;}
a:active {color: #CCCCCC !important;}

/* Chat, Mail, Forum text area color settings */
input, textarea {background-color:#333333 !important; 
color: #999999 !important; }
#content, #chatmsg, #chatuserlist {background-color:#666666 !important;}
#chatwindow {background-color:#333333 !important;
border-color: #666666 !important;}

/* General tag color settings */
size, span, center, color, div, li, tr, u, i { color: #999999 !important;}
b, font { color: #999999 !important;}
blockquote { color: #999999 !important;}
th, td { color: #CCCCCC!important;
background-color: transparent !important;
border-color: #666666 !important;}
table {color: #666666 !important;
border-color: #666666 !important;}
}
