css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("juick.com") {
html, body { width:100%; height:100%; }
#header { width:1285px !important; }
#topwrapper { width:1300px !important; overflow-x:hidden !important; }
#content { margin:0 0 0 13em !important; width:1100px !important; }
div#column + div#rcol { margin-top:490px !important; }
#column { width:200px !important; }
#column p { font-size:100% !important; }
#ctoolbar { text-align:center !important; margin:3px 0 !important; background:transparent !important; }
#ctoolbar li, #ctoolbar div { margin:0 !important; }
#ctoolbar a { padding:0 10px !important; }
#ctoolbar ~ ul, #ctoolbar ~ ul li { margin-top:0 !important; }
div#rcol { margin:0 !important; padding-top:5px !important; }
.msg-cont { width:auto !important; }
.msg-header { max-width:100% !important; }
.msg-comment textarea { width:900px !important; }
#content .msgthread, #mtoolbar, .title2, #replies { margin-left:-13em !important; }
#content.pagetext { margin-left:0 !important; }
#mtoolbar { width:1300px !important; }
.title2 { margin-top:0 !important; }
.msgthread textarea { width:1050px !important; }
.newmessage { width:100% !important; }
#footer { width:100% !important; margin:0 !important; text-align:center !important; }
#footer-left, #footer-right { float:none !important; display:inline-block !important; margin-right:1em !important; }
.ads { display:none; }
#postmsg, #postmsg ~ div, #postmsg ~ p { margin-left:15em!important; }
.msg-header, .msg-ts { display:inline-block!important; vertical-align:bottom!important; }
.msg-ts { margin:0 0 0 .5em!important; }
#column .inp { width:190px!important; }
.msg-txt { margin:.5em 0!important; }
.msg-comments { margin:0!important; text-indent:0!important; }
}
