css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("0chan.hk") {
::-moz-selection, ::-webkit-selection, ::selection {
    text-shadow: none !important;
    background: #333 !important;
    color: #fff !important;
}

html {
    background: url(http://img832.imageshack.us/img832/296/despairoverlay.png) no-repeat top right fixed, url(http://img35.imageshack.us/img35/3969/despairmain.png) !important;
}

body, .content-background {
    background-color: transparent !important;
    text-shadow: 0 1px 0 rgba(255, 255, 255, 0.5) !important;
}

#overlay_menu, .reflinkpreview {
    background: url(http://img35.imageshack.us/img35/3969/despairmain.png !important);
}

.reply, .replymode {
    background: url(http://img826.imageshack.us/img826/7130/despairreply.png) !important;
    box-shadow: 0 1px 0 rgba(255, 255, 255, 0.8) !important;
}

a, a:visited, .logo, .postername {
    color: rgb(88, 88, 88) !important;
}

a:hover {
    color: black !important;
    text-shadow: 0 0 3px rgba(0, 0, 0, 0.4) !important;
}

.reflink:hover {
    color: white !important;
}

.spoiler {
    text-shadow: none !important;
}

img {
    -webkit-filter: grayscale(100%) !important;
     -webkit-transition: 0.2s !important;
}

img:hover {
    -webkit-filter: grayscale(0) !important;
}

.unkfunc, .postertrip {
    color: rgb(145, 145, 145) !important;
}

.shareSite {
    display: none !important;
}

.filetitle, .replymode {
    color: rgb(22, 22, 22) !important;
}

.catalogmode {
    background: rgb(63, 63, 63) !important;
    text-shadow: none !important;
    box-shadow: none !important;
    border: none !important;
}

.ui-state-hover {
    border: 1px solid white !important;
    background: white !important;
    box-shadow: 0 0 8px rgba(0, 0, 0, 0.38) !important;
    text-shadow: none !important;
}
}
