css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("windowsmania.pl") {

.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
background : none 0 0 repeat scroll orangered !important ;
border : orangered solid 1px !important ;
}
body a {
color : orangered !important ;
}
#middle_header {
background-color : orangered !important ;
}
.column-right {
background-color : orangered !important ;
}
.forumbit_nopost .forumhead h2, .forumbit_post .forumhead h2 {
background-color : orangered !important ;
border-left : 1px solid orangered !important ;
border-right : 1px solid orangered !important ;
}
.navtabs li.selected li a {
color : #FFFFFF !important ;
}
.navtabs li a.navtab {
color : #FFFFFF !important ;
}
.forumbit_post .foruminfo .forumdata .forumtitle a, .forumbit_nopost .forumbit_nopost .forumrow .forumtitle a {
color : #2E3539 !important ;
}
.forumbit_nopost .forumhead .forumtitle, .forumbit_nopost .forumhead span, .forumbit_nopost .forumhead .collapse, .forumbit_post .forumhead h2 span {
color : #FFFFFF !important ;
}
.forumbit_nopost .forumhead .forumtitle a {
color : #FFFFFF !important ;
}
.navtabs li.selected .popupbody li a {
color : #000000 !important ;
}
.newcontent_textcontrol {
color : #FFFFFF !important ;
}
.postbitlegacy .postfoot .textcontrols a, .postbit .postfoot .textcontrols a, .eventbit .eventfoot .eventcontrols a, .postbitlegacy .postfoot .textcontrols span.mobile, .postbit .postfoot .textcontrols span.mobile {
color : #4E4E4E !important ;
}
* html .navpopupmenu.popupmenu.nohovermenu a.textcontrol, .navpopupmenu a.textcontrol, .navpopupmenu a.popupctrl, .navpopupmenu.popupmenu.nohovermenu a.textcontrol, .navpopupmenu.popupmenu.nohovermenu a.popupctrl {
color : #424242 !important ;
}
.newcontent_textcontrol > span, .pagetitleinfo.textcontrol > span {
background-color : orangered !important ;
}
.toolsmenu .popupgroup a.popupctrl, .toolsmenu .popupgroup .popupmenu a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active {
color : #4e4e4e !important ;
}
.postbit .posthead .nodecontrols a, .postbitlegacy .posthead .nodecontrols a, .eventbit .eventhead .nodecontrols a {
color : #FFFFFF !important ;
}
.threadlisthead a, .threadlisthead a:hover {
color : #FFFFFF !important ;
}
.threadlisthead {
background : none 0 0 repeat scroll orangered !important ;
border-left : 1px solid orangered !important ;
border-right : 1px solid orangered !important ;
}
.loginbutton {
background-color : orangered !important ;
}
#login-text {
color : orangered !important ;
}
.navbar_username_outer.active, .navbar_password_outer.active {
border-left : 6px solid orangered !important ;
}
h2.searchlisthead a {
color : #FFFFFF !important ;
}
h2.searchlisthead {
background : none 0 0 repeat scroll orangered !important ;
border : orangered solid 1px !important ;
}
.navbody a:link, .navbody a:visited, .navbody a:active {
color : #000000 !important ;
}
.forumhead {
background : none 0 0 repeat scroll orangered !important ;
}
.forumbits .forumhead {
border-left : 1px solid orangered !important ;
border-right : 1px solid orangered !important ;
}
dl.tabslight dd a {
color : #ffffff !important ;
}
html > body dl.tabslight dd.selected a, html > body dl.tabslight dd.selected a:hover {
background : none 0 0 repeat scroll #2E3539 !important ;
border : #2E3539 solid 1px !important ;
}
#profile_tabs dl.tabslight dd.userprof_moduleinactive {
background-color : orangered !important ;
border-color : orangered !important ;
}
#profile_tabs dl.tabslight dd.userprof_moduleinactive:hover {
background-color : #2e3539 !important ;
border-color : #2e3539 !important ;
}
dl.as-tabs dd a {
color : white !important ;
}
dl.as-tabs dd.selected a {
color : orangered !important ;
}
dl.as-tabs dd {
background-color : orangered !important ;
border-color : orangered !important ;
}
.activitystream_block dd.selected, dd.userprof_module {
background-color : #2e3539 !important ;
border-color : #2e3539 !important ;
}
dl.as-tabs dd:hover {
background-color : #2e3539 !important ;
border-color : #2e3539 !important ;
}
.announcements {
color : white !important ;
}
}
