css:
@-moz-document domain(dobrochan.com), domain(2chru.net), domain(0chan.hk), domain(iichan.hk) {
    body {
        background-color:#000000!important;
        color:#999999!important;
    }
    input {
        background-color:#000000!important;
        color:#999999!important;
        -moz-appearance:none!important;
        border-color:#999999!important;
        border-style:inset!important;
        border-width:3px!important;
    }
    textarea {
        background-color:#000000!important;
        color:#999999!important;
        -moz-appearance:none!important;
        border-color:#999999!important;
        border-style:inset!important;
        border-width:3px!important;
    }
    select {
        background-color:#000000!important;
        color:#999999!important;
        -moz-appearance:none!important;
        border-color:#999999!important;
        border-style:inset!important;
        border-width:3px!important;
    }
    
    a {
        color:#85658F!important;
    }
    a:hover {
        background-color:#85658F!important;
        color:#000000!important;
    }
    a img:hover {
        background-color:transparent!important;
    }
    .fileinfo {
        color:#999999!important;
    }
    .reply_ {
        background-image:url('data:image/png;base64,')!important;
        
    }   
    .reply_:hover {
        background-color:transparent!important;
    }
    .quote {
        background-image:url('data:image/png;base64,')!important;
    }
    .quote:hover {
        background-color:transparent!important;
    }
    
    .hide {
        background-image:url('data:image/png;base64,')!important;
    }
    .hide:hover {
        background-color:transparent!important;
    }
    .signed {
        background-image:url('data:image/png;base64,')!important;
    }
    .signed:hover {
        background-color:transparent!important;
    }
    .unsigned {
        background-image:url('data:image/png;base64,')!important;
    }
    .unsigned:hover {
        background-color:transparent!important;
    }
    .delete {
        background-image:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOCAYAAAAfSC3RAAAABGdBTUEAALGPC/xhBQAAABp0RVh0U29mdHdhcmUAUGFpbnQuTkVUIHYzLjUuMTAw9HKhAAAAVklEQVQ4T2P8//8/A1kApHHmzJn/QRjExoeR1YEVwgTwaUZXA7cBn2ZscihOw6YAl4EYfkJWiM8VWAMDXTO2AKOeRrKcSlbgkBUdZCcAspMcoTSKTR4ASAkWO/XaOY8AAAAASUVORK5CYII=')!important;
    }
    .checked {
        background-image:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOCAYAAAAfSC3RAAAABGdBTUEAALGPC/xhBQAAABp0RVh0U29mdHdhcmUAUGFpbnQuTkVUIHYzLjUuMTAw9HKhAAAAVklEQVQ4T2P8//8/A1kApPFMGsN/EAax8WFkdWCFMAF8mtHVwG3ApxmbHIrTsCnAZSCGn5AV4nMF1sBA14wtwKinkSynkhU4ZEUH2QmA7CRHKI1ikwcAxqXv7McycPcAAAAASUVORK5CYII=')!important;
    }
    .delete:hover {
        background-color:transparent!important;
    }
    .close {
        background-image:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOCAYAAAAfSC3RAAAABGdBTUEAALGPC/xhBQAAABp0RVh0U29mdHdhcmUAUGFpbnQuTkVUIHYzLjUuMTAw9HKhAAAAVklEQVQ4T2P8//8/A1kApHHmzJn/QRjExoeR1YEVwgTwaUZXA7cBn2ZscihOw6YAl4EYfkJWiM8VWAMDXTO2AKOeRrKcSlbgkBUdZCcAspMcoTSKTR4ASAkWO/XaOY8AAAAASUVORK5CYII=')!important;
    }
    .close:hover {
        background-color:transparent!important;
    }
    .logo {
        color:#CC6600!important;
    }
    .description {
        color:#CC6600!important;
    }
    .theader {
        background-color:#999966!important;
        color:#000000!important;
        border-radius:5px!important;
        -moz-border-radius:5px!important;
        border-color:#000000!important;
    }
    .theader a {
       color:#000000!important;
    }
    .postblock {
        background-color:#333333!important;
        color:#999999!important;
    }
    .postername {
        color:#999966!important;
    }
    .postertrip {
        color:#669966!important;
    }
    .replytitle {
        color:#CC6600!important;
    }
    .oppost {
        background-color:#000000!important;
        color:#999999!important;
    }
    .replypost {
        background-color:#000000!important;
        color:#999999!important;
        border-radius:5px!important;
        -moz-border-radius:5px!important;
    }
    .popup {
        background-color:#333333!important;
        color:#999999!important;
        border-color:#666666!important;
        border-radius:5px!important;
        -moz-border-radius:5px!important;
    }
    .oppost.popup {
        background-color:#000000!important;
        color:#999999!important;
        border-radius:5px!important;
        -moz-border-radius:5px!important;
    }
    .transparent.popup {
        background-color:transparent!important;
    }
    .reply {
        background-color:#333333!important;
        color:#999999!important;
        border-color:#666666!important;
        border-radius:5px!important;
        -moz-border-radius:5px!important;
    }
    .spoiler {
        background-color:#999999!important;
        color:#333333!important;
    }
    .spoiler:hover {
        background-color:#999999!important;
        color:#333333!important;
    }
    
    .spoiler a {
        background-color:#999999!important;
        color:#333333!important;
    }
    .spoiler a:hover {
        background-color:#999999!important;
        color:#333333!important;
    }
    blockquote .spoiler {
        background-color:#999966!important;
        color:#333333!important;
    }
    blockquote .spoiler:hover {
        background-color:#999966!important;
        color:#333333!important;
    }
    img.banner:hover {
        background-color:#000000!important;
    }
    div.file a:hover {
        background-color:transparent!important;
        color:#85658F!important;
    }
    .thumb:hover {
        background-color:transparent!important;
    }
    /*main page*/
    .newssize {
        background-color:#000000!important;
    }   
    .featuredimgbottom {
        border-radius:5px!important;
        background-image:none!important;
    /*  background-color:#000000!important;*/
    }
    .featuredimgtop {
        background-image:none!important; 
        background-position:top;
        background-repeat:no-repeat;
    }
    .header {
        color:#000000!important;
        background-color:#85658F!important;
    }
    .list div a {
        color:#85658F!important;
        background-color:#000000!important;
    }
    .list div a:hover {
        background-color:#85658F!important;
        color:#000000!important;
    }
    /* settings */
    td.logo { 
        background-color:#999999!important;
        color:#000000!important;
    }
    span.logo { 
        background-color:#999999!important;
        color:#000000!important;
    }
    /* bookmarks */
    table.threadlist td {
        background-color:#000000!important;
        color:#999999!important;
    }
    span.filetitle {
        color:#CC6600!important;
    }
    /* blockquote */
    blockquote[depth="0"] {
        background-color:inherit!important;
        color:#999966!important;
    }
    blockquote[depth="1"] {
        background-color:inherit!important;
        color:#777755!important;
    }
    blockquote[depth="2"] {
        background-color:inherit!important;
        color:#666644!important;
    }
    blockquote[depth="3"] {
        background-color:inherit!important;
        color:#666644!important;
    }
    .spoiler blockquote {
        background-color:inherit!important;
        color:#333333!important;
    }
}
/* 0chan.hk */
@-moz-document domain(0chan.hk) {
    
    td.content-background {
        background-color:#000000!important;
    }
    div.content-background {
        background-color:#000000!important;
    }
    span.ui-button-text {
        background-color:#000000!important;
    }
    span.ui-button-text:hover {
        background-color:#85658F!important;
        color:#000000!important;
    }
    div.replymode {
        background-color:#999966!important;
        color:#000000!important;
        border-radius:5px!important;
        -moz-border-radius:5px!important;
        border-color:#000000!important;
    }
    div.replymode a {
       color:#000000!important;
    }
    span.extrabtns a:hover {
       background-color:#000000!important;
    }
    img.shareSite:hover {
       background-color:#000000!important;
    }
    pre.text {
       color:#999999!important;
       background-color:#000000!important;
    }
}
/* iichan.hk, 2chru.net */
@-moz-document domain(iichan.hk), domain(2chru.net) {
    span.spoiler {
        background-color:#999999!important;
        color:#333333!important;
    }
    span.spoiler:hover {
        background-color:#999999!important;
        color:#333333!important;
    }
}
/* iichan.hk */
@-moz-document domain(iichan.hk) {
    div.news {
       background-color:#999966!important;
       color:#000000!important;
       border-radius:5px!important;
       -moz-border-radius:5px!important;
       border-color:#000000!important;
    }
    span.adminname {
       color:#CC6600!important;
    }
    td.prohibition {
       color:#999999!important;
    }
    span.thumbnailmsg {
       color:#999999!important;
    }
    span.commentpostername {
       color:#999966!important;
    }
    span.spoiler {
        background-color:#999999!important;
        color:#333333!important;
    }
    span.spoiler:hover {
        background-color:#999999!important;
        color:#333333!important;
    }
    blockquote.unkfunc {
        background-color:inherit!important;
        color:#999966!important;
    }
    blockquote.unkfunc span.spoiler{
        color:333333!important;
        background-color:#999966!important;
    }
}
/* 2chru.net */
@-moz-document domain(2chru.net) {
    div.replymode {
	background-color:#999966!important;
	color:#000000!important;
	border-radius:5px!important;
	-moz-border-radius:5px!important;
	border-color:#000000!important;
    }
    div.replymode a {
        color:#000000!important;
    }
    span.unkfunc {
        background-color:inherit!important;
        color:#999966!important;
    }
    span.unkfunc span.spoiler {
        color:333333!important;
        background-color:#999966!important;
    }
}
