@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.google),
               url-prefix(http://images.google),
               url-prefix(https://www.google) 

{
.gbqfqwc{
height:29px!important;

}

#gbz ol.gbtc li.gbt a span {
     color: white !important;
}

/*[[colour]]*/
      
  .pmoabs {
  display: none!important
}
}

style_setting_options.value:
/*Googlebar colour*/
#gbx3, #gbx4 {
  background-color: #E0BEBC !important;
  border-bottom-color:rgba(0,0,0,0)!important; 
  border-bottom-style:solid;
  border-bottom-width:0;
  font-size:24px;
}

#gbz ol.gbtc li.gbt a span {
     color: white !important;
}


  body#gsr 
  {
    background:url(http://s20.postimg.org/mjk0gm8r1/1dge.png) !important;
    background-size: cover !important;
  }
  #gbg6 #gbi4t, #gbg4 #gbgs4d, #prm-pt, #ftby, #ftby a:visited, #prm-pt a, .ftl, #footer a  {
    color: gainsboro !important;
  }
  #hplogo
  {
    display: none !important;
  }
  #lga
  {
    background-image: url("http://upload.wikimedia.org/wikipedia/commons/d/d5/Transparent_google_logo.png") !important;
    background-position: center 111px !important;
    background-repeat: no-repeat !important;
    background-size: 275px 95px !important;
    margin-top: 20px!important
  }
  #gbqf.gbem, #tsf, #gbqf{    margin-top: 25px!important}
  #mw, #cnt
  {
      background: white !important;
  }
  .pmoabs {
  display: none!important
}
#logo span, .lsb{
      background-color:#BF7D82 !important;
      border-color:#BF7D82 !important;
      padding:1px!important;
      margin-top:-1px!important;
      margin-left:-1px!important
      }

