@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tvtorrents.com") {

html a:hover 
{
color:#999 !important;
}
html
{
background: #222 !important;
}
div.container
{
margin:0 auto !important;
width:1005px !important;
}
div.left
{
margin-right:10px !important;
}
div.middle
{
position:static !important;
float:left !important;
left:auto !important;
}
div.right div[style="width: 100%;"]{
width:auto !important;
}

.menu {
background:black !important;
-webkit-border-radius: 10px;
border-radius: 0px;
}
.topbar {
background:black !important;
-webkit-border-radius: 10px;
border-radius: 10px;
margin:10px !important;
}
body 
{
background:#222 !important;
}
.body, .list_even td, .list_odd td
{
background:#333 !important;
}
.container > table > tbody > tr > td:first-child > a > img {
width: 0 !important;
padding-left: 496px !important; /* replace with width of new image */
height: 124px !important; /* replace with height of new image */
background: url("http://www.thermalzombie.com/stylish/tvts.png") center no-repeat !important;
}
.head2, a.head2:link, a.head2:visited, a.head2:active, a.head2, .tvtbox .body {
color:#FFF !important;
}
.boxhead h2, .head, .rub td
{
background:#000 !important;
}
.boxbody{
color:#FFF !important;
background:#444 !important;
}
.boxbody td{
color:#FFF !important;
background:#222 !important;
}
html a,
font.big, span.big, font, .medium
{
color:#FFF !important;
}
.schedule_fav{
color:#000 !important;
}
.subbox, .subbox a {
background:#000 !important;
}
 div.subbox img{
display:none !important;
}
.tvtbox {
border:2px black solid !important;
}
#dl_frame {
display:none !important;
}
.tinput {
color:#FFF !important;
background:#000 !important;
}

.tvtbox {
box-shadow:4px 7px 3px #111 !important;
}
}

