css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ubuntuforums.org") {

body, .forumbit_nopost .forumhead a, .threadpagenav .threadpagestats, .toolsmenu .popupgroup .popupmenu a.popupctrl,
.navlinks, .navlinks a, .pagetitle a, .below_body .time, .above_postlist .pagination_top .postpagestats,
.breadcrumb .navbit > a, .breadcrumb .lastnavbit > span, #pagetitle a {
    color: rgb(255, 255, 255) !important;
}

.above_body, .navtabs, .navbar, .navlinks, .logindetails, ul.nouser a {
    background: none repeat scroll 0% 0% rgb(74, 100, 108) !important;
}

a.navtab, .toolsmenu, .toolsmenu div, .moreactivity, .moreactivity a {
    color: rgb(255, 255, 255) !important;
    background: none repeat scroll 0% 0% rgb(74, 100, 108) !important;
}

.forumhead a, .titleline a, .formcontrols .showsignature, li.activitybit a, .youcandoblock, .bbcodeblock, .summaryinfo,
.blockrow, .yui-skin-sam .yui-layout .yui-layout-unit div.yui-layout-bd, .searchtitle a, .threadtitle a, .threadbit a,
.pagination span a, .requestlist > li, .postbit .posttitle a, .postbitdetails_noavatar a, .forumlastpost.td a,
div.posttext, div.date, div.username, div#usercp_nav a, #pollinfo .pollsubhead, #pollinfo .blockfoot, div[style*=lightyellow] {
    color: rgb(74, 100, 108) !important;
}

.announcements .announcerow {
    background: none repeat scroll 0% 0% rgb(112, 128, 144) !important;
    box-shadow: none !important;
}

.announceinfo, .announcerow .announcement, #searchtypeswitcher li.selected a, .blockhead, .pagination span.selected a,
.forumhead, #forum_icon_legend h5 {
    background: none repeat scroll 0% 0% rgb(112, 128, 144) !important;
    color: rgb(255, 255, 255) !important;
}

.shade, .below_body .time {
    color: rgb(150, 150, 150) !important;
}

.footer, .blockfoot {
    background: none repeat scroll 0% 0% rgb(74, 100, 108) !important;
}

.newcontent_textcontrol, .group input {
    background: none repeat scroll 0% 0% rgb(112, 128, 144) !important;
    box-shadow: none !important;
}

.body_wrapper, html, .forumbit_nopost .forumhead {
    background: none repeat scroll 0% 0% rgb(74, 100, 108) !important;
    box-shadow: none !important;
}

#pollinfo .blockfoot input {
    color: rgb(225, 225, 225) !important;
}

.threadbit .bloginfo a.threadstatus {
   	background-position: 0px 0px !important;
}

.navbithome a img, img.gotonewpost, .threadbit .threadlastpost img, .forumlastpost.td a img {
    visibility: hidden !important;
}

.navbithome a {
    background:url(data:image/png;base64,) !important;
}

a[id^=thread_gotonew] {
        background:url(data:image/png;base64,) !important;
}

a.lastpostdate.understate, .threadbit .icon0 .threadlastpost > dd > a:last-child, .lastposttitle a:last-child {
    background:url(data:image/png;base64,) !important;
}

li[id^=search_announcement] > div.nonsticky > dl.threadlastpost.td > dd > a {
    background: none !important;
}

.breadcrumb .navbit, .navbit.lastnavbit span, .formcontrols, .wysiwyg_block, .toolsmenu  {
    border: 0px solid transparent !important;
}

.navbar_search input, .moreactivity {
    border: 1px solid transparent !important;
}

.navtabs li a.navtab {
    text-align: left !important;
    padding-left: 2px !important;
}

.navtabs .popupbody li {
    border-top: 1px solid rgb(74, 100, 108) !important;
}

.globalsearch .textboxcontainer span {
    width: 120px !important;
    padding-left: 17px !important;
}

.globalsearch .buttoncontainer {
    float: left !important;
    width: 30px !important;
}
 
.forumbit_nopost .forumhead h2 {
    display: none !important;
}

.threadbit, .threadlisthead, .forumrow, .wgo_block, .footer, .forum_info .blockhead, .forum_info .blockbody, .postbitlegacy,
.forumbits .forumhead, .thread_info .blockbody, .thread_info h4, h2.searchlisthead, .threadbit, #pollinfo, #notices li {
    box-shadow: none !important;
}

.postbit, li.activitybit {
    box-shadow: 0px 26px 0px 0px rgb(74, 100, 108) !important;
}

.threadlisthead  {
    border: 1px solid transparent !important;
}

form > ul.popupbody > li > div {
    background: rgb(225, 225, 225) !important;
}

#pollinfo .blockhead, #pollinfo .blockfoot {
    background: rgb(243, 243, 243) !important;
}

.popupmenu a.popupctrl {
    background-image: url("http://ubuntuforums.org/images/ubuntu-VB4/misc/arrow.png") !important;
}

.toplinks ul.isuser li > a#yui-gen1, .toplinks ul.isuser li, .toplinks li.welcomelink > a:hover,
.toplinks ul.isuser > li > a:hover {
    background-color: rgb(74, 100, 108) !important;
}

.options_input_wrapper .options_input_block .group .button {
    background: url("http://ubuntuforums.org/images/gradients/generic_button.png") repeat-x scroll left top rgb(233, 233, 233) !important;
}

.formcontrols .blockrow input.textbox, .formcontrols .section, .formcontrols, a[id^=cke] {
    background-color: rgb(255, 255, 255) !important;
    color: rgb(0, 0, 0) !important;
}

#vbmenu_qlinks li:last-child, #menu_mja2_174 li:last-child, #menu_mja2_686 li:last-child, #menu_mja2_593 li:last-child, #menu_mtaz_185 li:last-child {
    border-bottom: 1px solid rgb(74, 100, 108) !important;
}

#vbmenu_qlinks li, #menu_mja2_174 li, #menu_mja2_686 li, #menu_mja2_593 li, #menu_mtaz_185 li {
    border-left: 1px solid rgb(74, 100, 108) !important;
    border-right: 1px solid rgb(74, 100, 108) !important;
    border-top: 1px solid rgb(74, 100, 108) !important;
}

}
