css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.serlo.org") {
    #header {
        min-width:0px !important;
        width:100% !important;
        margin:0 !important;
        padding:0 !important;
        height:80px !important;
    }
    #header .inner{
        margin:0 !important;
        width:100% !important;
        height:25px !important;
    }
    #header .right {
        width:100% !important;
        position: static !important;
        height: 0 !important;
    }
    #header .profile {
        height:100% !important;
        text-align:left !important;
        float:right !important;
        margin:0 !important;
    }
    #sub_navigation {
        height:49px !important;
    }
    #navigation {
        padding:0 !important;
    }
    #qs_container {
        float:none !important;
        margin: 0 !important;
    }
    #qs_container input {
        height: 2rem !important;
        font-size: 1.8rem !important;
    }
    #quicksearch {
        width:93% !important;
        padding: 0 !important;
    }
    #quicksearch_button {
        height: 2rem !important;
        width:7% !important;
    }
    #qs_autocomplete {
        width:100% !important;
        border:0 !important;
        border-bottom: 1px solid #C5C5C5 !important;
        top: 28px !important;
        
    }
    #qs_results .category {
        margin-top:0 !important;
        width:30% !important;
    }
    #qs_results .entries {
        margin-left:0 !important;
        width:70% !important;
        float:right !important;
    }
    #qs_results .divider {
        clear:both !important;
    }
    .autocomplete {
        border:0 !important;
        padding:0 !important;
        margin: 0 !important;
    }
    #top_navigation {
        width:100% !important;
        background:none !important;
        height:0 !important;
        margin: 0 !important;
    }
    .right {
        float:none !important;
    }
    #logo {
        display:none !important;
    }
    #container {
        min-height:0px !important;
        width: 100% !important;
    }
    #addummy {
        display:none !important;
    }
    #application {
        width:100% !important;
        border-right:0px !important;
        padding-right:0px !important;
    }
    .title {
        width:100% !important;
    }
    #application table {
        max-width: 100% !important;
    }
    .clear {
        clear:none !important;
    }
    .box {
        margin-left:-100px !important;
    }
    #overlay {
        background:black !important;
        opacity: 0.7 !important;
    }
    #frontbox {
        width:100% !important;
        top:0 !important;
        left:0 !important;
        
    }
    
    
    .cell {
        float:none !important;
        width:100% !important;
        padding:4px 0 !important;
    }
    .cell:nth-child(even) {
        background:#e8e8e8
    }
    .wiki .cell .video {
        background-repeat:no-repeat !important;
        padding:0 !important;
    }
    
    body {
        overflow-x:-moz-hidden-unscrollable !important;
    }
    
    h1, .h1 {
        font-size:2rem !important;
    }
}
