css:
@-moz-document url-prefix("http://cloud.feedly.com/") {
div#feedlyFrame { width : 100% !important;}
div#feedlyPart { width : 102% !important;}
div#feedlyPage, div#mainArea { width: 100% !important;}
div#sideArea, div#floatingBar, img.favicon, h2, #feedlyTitleBar, #section0
   { display: none !important; }
div#feedlyPart0 {margin-left: -30px !important; margin-right: -5px !important; }
div#feedlyPart {margin-left: -25px !important;}
div#feedlyPart #feedlyPage {margin-top: -20px !important;}
div.pageActionBar {margin-top: -30px !important;}
div.sourceinfo {width: 8% !important;}
span.nbrRecommendations {color: grey !important; line-height: 25px !important;}

.u0Entry { height: 25px !important; font-size: 12px !important; padding-left:10px !important;}
.u0Entry .title { font-size: 13px !important; font-weight: 0 !important;}

#feedlyTabsHolder{ margin-left: -15px !Important; margin-top:0px !important; height:100% !important; width:350px !important;float:left !important;}

#feedlyTabs { height:100% !important; width:60% !important;}

.categoryUnreadCount.simpleUnreadCount,.feedUnreadCount.simpleUnreadCount{
	float:left !important;
  	padding-right:5px !important;
}

.feedIndex.target{
	margin-left:9px !important;
}

.topWikiWidget,.u100Entry.itemscope > .entryHeader,.u0Entry.title{
	margin: 5px !important;
}

#systemBar,#aboutArea{
	display:none !important;
}

.u100Entry.itemscope,.inlineFrame,.inlineFrame table{
	width:97% !important;
	padding: 2px !important;
  	margin: 5px !important;
 	float: left !important;
}

.condensed .entryholder .u100entry{
	margin-left:0 !important;
}
}
