css:
@-moz-document domain("2chru.net"), domain("2-chru.net") {
html, body {
		background: #212121 !important;
	color: #799CD0 !important;
	font-family: "Trebuchet MS",Trebuchet,tahoma,serif !important;
	font-size: 1em !important;
}
a, #adminbar ul.menu ul li a, .rmenu a {
	color: #C9BE89 !important;
}
a:hover, #adminbar ul.menu ul li a:hover, .rmenu a:hover {
	color: #EEFEBB !important;
}
.adminbar {
        text-align: right !important;
        background: inherit !important;
        clear: both !important;
        float: right !important;
        line-height: 17px !important;
	font-size: 0.8em !important;
}
.adminbar a {
	font-weight: normal !important;
}
#adminbar ul.menu ul li a {
	font-weight: bold !important;
}
#adminbar ul.menu ul:not(:first-child) li:before {
	content: "[" !important;
}
#adminbar ul.menu ul:not(:first-child) li:after {
	content: "]" !important;
	padding-right: .3em !important;
}
.logo {
	clear: both !important;
	text-align: left !important;
	font-size: 1em !important;
	font-weight: bold !important;
	color: #C9BE89 !important;
	text-shadow: rgba(255,210,0,.7) 0px 0px 10px, #000000 2px 2px 0px !important;
	
}
.logo2 {
	text-align: center !important;
}
.theader, .passvalid, .replymode {
	background: #2C2C2C !important;
	text-align: center !important;
	padding: 2px !important;
	color: #006AB9 !important;
	clear: both !important;
	font-weight: bold !important;
	margin-bottom: .5em !important;
	border: solid 1px #575757 !important;
	-webkit-border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	border-radius: 5px !important;
	text-shadow: #212121 0px -1px 0px !important;
}
.footer {
	text-align: center !important;
	font-size: 12px !important;
	font-family: serif !important;
	margin: 2em 0 0 0 !important;
}
.dellist {
	font-weight: bold !important;
	text-align: center !important;
}
.delbuttons {
	text-align: center !important;
	padding-bottom: 4px !important;
}
hr {
	height: 3px !important;
	border: 0 !important;
	background:url('http://2ch.hk/icons/line.png') center no-repeat !important;
}
table {
	border-style: none !important;
}
table td {
	border-style: none !important;
}
div[style*="0.5em"] {
	float: left !important;
}


	form {
	font-size: .9em !important;
}
.postarea {
	font-size: .9em !important;
}
.rules {
	font-size: .8em !important;
}
.rules ul li strong {
	color: #993333 !important;
}
.postblock, .form-tbl p {
	background: transparent !important;
	color: #757575 !important;
	font-weight: bold !important;
	text-shadow: #000000 0px 0px 8px, #111111 0px .1em 0px !important;
}
.form-tbl i {
	padding-left: 1em !important;
}
input[type="password"], input[type="file"], input[name="snum"], input[type="submit"], input[type="search"], div form select {
	-webkit-box-sizing: content-box !important;
	-moz-box-sizing: content-box !important;
	box-sizing: content-box !important;
	-webkit-appearance: none !important;
	-moz-appearance: none !important;
	appearance: none !important;
	color: #BBBBBB !important;
	border: 2px solid #545454 !important;
	background-color: #111111 !important;
}
input[type="file"], input[name="snum"],  div form select {
	-webkit-border-radius: 5px;
	-moz-border-radius: 5px;
	border-radius: 5px;
}
input[type="text"] {
	-webkit-box-sizing: content-box !important;
	-moz-box-sizing: content-box !important;
	box-sizing: content-box !important;
	-webkit-appearance: none !important;
	-moz-appearance: none !important;
	appearance: none !important;
	background-color: #111111 !important;
	color: #FFFFFF !important;
	border: 2px solid #545454 !important;
	-moz-border-radius: 5px !important;
	border-radius: 5px !important;
}
input:hover[type="text"], input:focus[type="text"] {
	background-color: #151515 !important;
	color: #AAAAFF !important;
}
textarea, #path {
	-webkit-box-sizing: content-box !important;
	-moz-box-sizing: content-box !important;
	box-sizing: content-box !important;
	-webkit-appearance: none !important;
	-moz-appearance: none !important;
	appearance: none !important;
	background-color: #111111 !important;
	color: #CCCCCC !important;
	border: 2px solid #545454 !important;
	-webkit-border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	border-radius: 5px !important;
}
textarea:hover, textarea:focus, #path:hover, #path:focus {
	background-color: #151515 !important;
	color: #EEEEEE !important;
}
	.form-tbl td .but {
	background: url("http://2ch.hk/icons/send.png") no-repeat scroll left top transparent !important;
}


.unkfunc {
	background: inherit !important;
	color:#789922 !important;
}
.reflink {
	font-size: 85% !important;
	font-weight: bold !important;
	color: #BBBBBB !important;
	padding: 7px !important;
}
.reflink a {
	color: #C9BE89 !important;
	text-shadow: #FFD200 0px 0px 12px !important;
}
.reflink a:first-of-type {
	padding-right: .3em !important;
}
label {
	color: #BBBBBB !important;
	font-size: 90% !important;
}
.filesize {
	background-color: #212121 !important;
	border: solid 1px #575757 !important;
	padding: 3px 8px !important;
	-webkit-border-radius: 8px !important;
	-moz-border-radius: 8px !important;
	border-radius: 8px !important;
	text-decoration: none !important;
	color: #999999 !important;
	font-size: .8em !important;
}
.filesize a, .filesize a:hover {
	color: #789922 !important;
}
div[id*="t"] > .filesize {
	background-color: #2C2C2C !important;
}
.filetitle, .replytitle {
	background: inherit !important;
	font-size: 1.2em !important;
	color: #48B0FD !important;
	font-weight: bold !important;
}
.oldpost, .notabene {
	color: #999999 !important;
	font-weight: bold !important;
}
.omittedposts {
	color: #999999 !important;
}
.reply {
	background: url('http://2ch.hk/icons/lightning.png') right bottom no-repeat !important;
	background-color: #2C2C2C !important;
	
	border: solid 1px #575757 !important;
	margin: 0 !important;
	padding: 0 !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
}
blockquote {
	margin: .5em .5em .5em 1em !important;
	line-height: 1.3em !important;
}

.reply blockquote {
	margin: .5em !important;
}
.doubledash {
	display: none !important;
 	vertical-align: top !important;
	clear: both !important;
	float: left !important;
}
.thumbnailmsg {
	font-size: .6em !important;
	color:#999999 !important;
	padding: 0 4px !important;
}
.nothumb {
	background-color: #555555 !important;
	border-style: dotted !important;
	margin: .3em .5em !important;
}
.abbrev {
	color:#999999 !important;
}
.highlight {
	color:#999999 !important;
	background: #111111 !important;
	border: 2px dashed #789922 !important;
}
a.highslide img {
	visibility: hidden !important;
	width: 1px !important;
}
a.highslide:before {
	content: url('http://2ch.hk/icons/inc_neut.png') !important;
	visibility: visible !important;
	position: relative !important;
	top: 2px !important;
}
.extrafunctions {
	color: #008000 !important;
}
	div.reply[id*="prev"] {
	border: 1px solid #000000 !important;
	background: rgba(25,25,25,.9) !important;
	-moz-box-shadow: #006699 -1px -1px .4em, #006699 1px 1px .6em !important;
	box-shadow: #006699 -1px -1px .4em, #006699 1px 1px .6em important;
}
img.img {
	padding: 4px 0 0 0 !important;
}
	


.postername, .commentpostername {
	color: #CCCCCC !important;
	font-weight: bold !important;
}
.postertrip {
	color: #228854 !important;
	font-weight: bold !important;
}
span.mod {
	color: #FFAB3F !important;
}
span.adm {
	color: #EE0000 !important;
}
	
	
body.replypage {
	counter-reset: postcount !important;
	counter-increment: postcount !important;
}
body.replypage td.reply .reflink:before {
	counter-increment: postcount !important;
	content: "#" counter(postcount) " " !important;
	color: #789922 !important;
	font-weight: normal !important;
	margin-right: 1em;
}
	a[href="mailto:sage"],  a[href="mailto:Sage"], a[href="mailto:SAGE"] {
	color: #993333 !important;
	text-shadow: #000 0px 0px 5px !important;
	text-decoration: none !important;
}
a[href="mailto:sage"]:before,  a[href="mailto:Sage"]:before, a[href="mailto:SAGE"]:before {
	content: " [sage] " !important;
	font-size: 14px;
}
a:hover[href="mailto:sage"],  a:hover[href="mailto:Sage"], a:hover[href="mailto:SAGE"] {
	color: #CC1212 !important;
	text-shadow: black 0px 0px 5px !important;
}
table.locked {
	width: 50% !important;
	margin: 20px !important;
	text-align: center !important;
	background-color: #151515 !important;
	border:1px solid #242424 !important;
	-moz-box-shadow: #006699 -1px -1px 1em, #006699 1px 1px 2em !important;
	box-shadow: #006699 -1px -1px 1em, #006699 1px 1px 2em !important;
}
.msgheader {
	background-color: #242424 !important;
	color: #FFFFFF !important;
}
.msg {
	background-color: #333333 !important;
	color: #48B0FD !important;
	font-size: .9em !important;
}


p.spoiler > span.warning {
	color: #FFB600 !important;
	font-weight: bolder !important;
}
	span.spoiler {
	background: #575757 !important;
	color: #575757 !important;
}
span.spoiler:hover {
	color: #48B0FD !important;
}
.spoiler a {
	color: #575757 !important;
}
.spoiler:hover a {
	color: #FFB600 !important;
}
 .u { text-decoration: underline !important; } 
 .s {text-decoration: line-through !important;} 
 .o { text-decoration: overline !important; }
pre, code {
	background: #2C2C2C !important;
	color: #CCCCCC !important;
	white-space: pre-wrap !important;
	white-space: -moz-pre-wrap !important;
	white-space: -pre-wrap !important;
	white-space: -o-pre-wrap !important;
	word-wrap: break-word !important;
	overflow: auto !important;
	max-width: 80em !important;
	display: block !important;
}
pre a, code a {
	color: #FFFFFF !important;
}
.code_part {
	border: none !important;
}
code .spoiler, code .spoiler:hover { 
	background: none !important;
	color: #CCCCCC !important;
}
code .spoiler:before {
	content: "%%" !important;
}
code .spoiler:after {
	content: "%%" !important;
}


.managehead {
	background: #2C2C2C !important;
	color:#6393CD !important;
	padding: 0px !important;
}
.postlists {
	background: #FFFFFF !important;
	width: 100% !important;
	padding: 0px !important;
	color: #800000 !important;
}
.row1 {
	background: #2C2C2C !important;
	color: #799CD0 !important;
}
.row2 {
	background: #575757 !important;
	color: #6393CD !important;
}
a[href*="admin="] {
	color: #B6FF00 !important;
	text-shadow: none !important;
}
a[href*="admin="]:hover {
	color: #F6BB00 !important;
	text-shadow: none !important;
}


dl.menu dt {
	background:#111111 !important;
	border: solid 1px #575757 !important;
	-webkit-border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	border-radius: 5px  !important;
	margin-top: 1em !important;
	padding-left: .5em !important;
	cursor: pointer !important;
	color: #698CC0 !important;
	font-weight: normal !important;
}
dl.menu dd {
	margin-left: .5em !important;
	padding-left: .5em !important;
	border-left: solid 1px #575757 !important;
	background:#212121 !important;
}
dl.menu dd:hover {
	background: #575757 !important;
}
dl.menu dd.hidden {
	display: none !important;
}


blockquote span[style*="color:"] {
	background-color: #141414 !important;
	color: #698CC0 !important;
}
::-moz-selection {
	background: #DBDBDB !important;
	color: #111111 !important;
}
::selection {
	background: #DBDBDB !important;
	color: #111111 !important;
}
font[color*="C12267"] em {
	color: #EE0000 !important;
	text-shadow: #000000 0px 0px 2px !important;
}
body > center > span {
	color: #EEEEEE !important;
}
	


img[src*="captcha"] {
	background: #CCCCCC !important;
}
.captcha {
	color: #757575;
}


div.box1  {
	background-color: #2C2C2C !important;
	
	border: solid 1px #575757 !important;
	-moz-border-radius: 5px !important;
	border-radius: 5px !important;
	margin: 4px 0 0 0 !important;
}
div.txt-box11 {
	background-color: #2C2C2C !important;
	border: solid 1px #575757 !important;
	-moz-border-radius: 5px !important;
	border-radius: 5px !important;
}
div.nl404 h5 {
	background: #111111 !important;
	text-align: center !important;
	padding: 2px !important;
	color: #999999 !important;
	clear: both !important;
	font-weight: bold !important;
	margin-bottom: .5em !important;
	border: solid 1px #575757 !important;
	border-radius: 5px !important;
	-moz-border-radius: 5px !important;
}
div.so, img#mas, .yo18 {
	display: none !important;
}
 .head .logo {
	background-image: none !important;
	text-decoration: none !important;
}
.nav h3, .main h4 {
	background: #000000 !important;
	border: solid 2px #575757 !important;
	color: #999999 !important;
}
.main h4 {
	margin-top: 1em !important;
}
.left .pop {
	display: none !important;
}
input, textarea  {
	outline: medium none !important;
}

}
