css:
@-moz-document url-prefix("http://www.ksml.fi/"), url-prefix("https://www.ksml.fi/") {
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 400;
  src: local('Lato Regular'), local('Lato-Regular'), url(http://themes.googleusercontent.com/static/fonts/lato/v6/9k-RPmcnxYEPm8CNFsH2gg.woff) format('woff');
}
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 700;
  src: local('Lato Bold'), local('Lato-Bold'), url(http://themes.googleusercontent.com/static/fonts/lato/v6/wkfQbvfT_02e2IWO3yYueQ.woff) format('woff');
}
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 900;
  src: local('Lato Black'), local('Lato-Black'), url(http://themes.googleusercontent.com/static/fonts/lato/v6/BVtM30trf7q_jfqYeHfjtA.woff) format('woff');
}

* {
	font-family: 'Lato', sans-serif;
  	text-shadow: none;
  	font-size: 1.1em;
}

img, .caption-wrapper { 
	border: 0px solid #fff!important;
}

h1, h2, h3 {
	font-family: 'Lato', sans-serif;
    line-height: 1.3;
}

h1, h2 { 
	font-weight: 900;
}

.teaserBodyText {
	font-size: 1em;  
}

body, #container {
	background: none;
}


#nav-secondary, #main #site-search {
	background: rgb(234, 234, 240);
  	margin-bottom: 2em;
  	border: none;
}

#nav-secondary a { 
	color: rgb(17, 24, 109);
}

#nav-secondary ul li{ 
	padding: 0 12px;
}

#nav-primary {
	background: rgb(138, 145, 160);
  	-moz-box-shadow: 0 0 0px rgba(50,50,50,0.8);
	-webkit-box-shadow: 0 0 0px rgba(50,50,50,0.8);
	box-shadow: 0 0 0px rgba(50,50,50,0.8);
  	text-shadow: none;
  	border: none;
    width: 840px;
}

#nav-primary ul li {
  	background: none;
  	border-left: 1px solid rgb(114, 124, 139);
 	padding-right: 0px;
	
}

#nav-primary a { 

}

#nav-primary a:hover { 
	background: #1a5dac;
  	text-shadow: none;
  	text-decoration: none;
}

#nav-primary li.selected a { 
	background: #1a5dac;
  	text-shadow: none;
}
    
.customLogo img{ 
	display:none;   
}
        
.column-2 #polling div div.answer form fieldset span:nth-child(4) { 
	background: #009036;
	border: none;
}

.column-2 #polling div div.answer form fieldset span:nth-child(6) { 
	background: #bf111c;
	border: none;
}

div.x520x320 div.column-1 { 
	padding: 0px;

}

.story { 
    border-top: 1px solid #e1e1e1;
    padding-top: 20px;
}

.tabs, .e2e-widget1 .e2e-tab-links {
	background-image: none;  
}

.ad .asset {
	display: none;    
}

.highlighted {
  	padding: 2em 0em;
	border: none;
    background: none;
}

.likeFacebook, .fb_iframe_widget {
	display: none;  
}

#sitemap section, .sitemap-section { 
	background: rgb(247, 247, 247)
}

.teaserBodyText {
  	font-family: georgia, serif;
  	font-size: 15px;
}

.customFilmstrip {
	display:none;
}

.caption-wrapper img {
	cursor: pointer;
}

.customLogo {
	width: 750px;
	background: url('data:image/png;base64,') no-repeat;
}
}
