css:
@-moz-document domain("2chru.net") {
/* С того самого сосача  */

html, body {
	background: scroll #211F1A url('http://web.archive.org/web/20120310193729/http://2ch.so/icons/muon_bg.jpg') repeat !important;
	color: #9B8165 !important;
	font-family: Verdana, sans-serif !important;
	font-size: 1em !important;
}

form {
	font-size: 0.9em !important;
}

a, #adminbar ul li a {
	color: #FFD97A !important;
	text-decoration: none !important;
	padding: 0px !important;
	-o-transition: all 0.3s ease !important;
	-moz-transition: all 0.3s ease !important;
	-webkit-transition: all 0.5s ease !important;
	transition: all 0.3s ease !important;
}

a:hover, #adminbar  ul li a:hover {
	color: #FCE236  !important;
	text-shadow: #FCE236 0px 0px 10px !important;

}

#adminbar ul li a {
	text-decoration: underline !important;
	padding: 0 2px !important;
}

.adminbar {
	text-align: right !important;
	background: inherit !important;
	clear: both !important;
	float: right !important;
	line-height: 17px !important;
	font-size: 0.8em !important;
}

.adminbar a {
	font-weight: normal !important;
	color:#B5935F  !important;
}

.logo {
	clear: both !important;
	text-align: left !important;
	font-size: 1em !important;
	font-weight: bold !important;
	color: #ECCA74 !important;
	/*width:100% !important;*/
}

.logo2 {
	text-align: center !important;
}

.theader, .passvalid, .replymode {
	background: url('http://web.archive.org/web/20120310193729/http://2ch.so/icons/muon_inputs.jpg') #161616 !important;
	text-align: center !important;
	padding: 2px !important;
	color: #757575 !important;
	clear: both !important;
	font-weight: bold !important;
	margin: .5em 0 !important;
	border: solid 1px #9B8165 !important;
	box-shadow: 0 0 4px rgba(155,129,101,0.7) !important;
	border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
}

.postarea {
	font-size: 0.9em !important;
}

.rules {
	font-size: 0.8em !important;
}

.postblock, .form-tbl p {
	background: transparent !important;
	color: #5E5E5E  !important;
	font-weight: bold !important;
}

#dynamicNamed {
	margin-right: 0.5em !important;
	padding: 0.1em 0.2em !important;
}

.footer {
	text-align: center !important;
	font-size: 12px !important;
	font-family: serif !important;
	margin: 2em 0 0 0 !important;
}

.footer a {
	color: #9B8165  !important;
}

.dellist {
	font-weight: bold !important;
	text-align: center !important;
}

.delbuttons {
	text-align: center !important;
	padding-bottom: 4px !important;
}

.managehead {
	background: #9B8165 !important;
	color: #000000 !important;
	font-family: sans-serif !important;
	font-size: 14px !important;
	padding: 0px !important;
}

.postlists {
	background: #FFFFFF !important;
	width: 100% !important;
	padding: 0px !important;
	color: #000000 !important;
}

.row1 {
	background: #B5935F !important;
	font-family: sans-serif !important;
	font-size: 12px !important;
	color: #000000 !important;
}

.row2 {
	background: #ECCA74 !important;
	font-family: sans-serif !important;
	font-size: 12px !important;
	color: #000000 !important;
}

.row1 a, .row2 a {
	color: #115011  !important;
}

.row1 a:hover, .row2 a:hover {
	text-shadow: none !important;
} 

.unkfunc {
	background: inherit !important;
	color: #C1B387 !important;
}

.reflink {
	font-size: 85% !important;
	font-weight: bold !important;
}

.filesize {
	text-decoration: none !important;
	color: #5E5E5E !important;
	font-size: .8em !important;
}

.filesize a {
	margin: 0 .5em  !important;
}

.filetitle {
	background: inherit !important;
	font-size: 1.2em !important;
	color: #FFC17B !important;
	font-weight: bold !important;
}

hr {
	height: 3px !important;
	border: 0  !important;
	background: url('http://web.archive.org/20120310193729/http://2ch.so/icons/muon_line.png') center no-repeat !important;
	background-size: 100% 100% !important;
	-moz-background-size: 100% 100% !important;
}

a.highslide img {
	visibility: hidden !important;
	width: 1px  !important;
}

a.highslide:before {
	content: url('http://web.archive.org/web/20120310193729/http://2ch.so/icons/inc_neut.png')  !important;
	visibility: visible  !important;
	position: relative  !important;
	top: 2px  !important;
}

.highslide-loading {
	background-color: #212121  !important;
	border: 1px dotted black  !important;
}

.postername, .commentpostername {
	color: #9B8165 !important;
	font-weight: bold !important;
}

.postertrip {
	color: #896541 !important;
	font-weight: bold !important;
}

.oldpost, .notabene {
	font-family: serif !important;
	color: #0F0C5D !important;
	font-weight:bold !important;
}

.omittedposts {
	color: #896640 !important;
}

.reply {
	color: #C6985C !important;
	background: url('http://web.archive.org/web/20120310193729/http://2ch.so/icons/muon_posts.jpg') #292825 !important;
	border: solid 1px #34352D !important;
	margin: 5px 5px !important;
	padding: 1px 2px 6px 2px !important;
	border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	-moz-box-shadow: 0px 0px 12px rgba(0, 0, 0, 0.3) !important;
	-webkit-box-shadow: 0px 0px 12px rgba(0, 0, 0, 0.3) !important;
	box-shadow: 0px 0px 12px #161616 !important;
}

blockquote {
	margin: .5em .5em .5em 1em !important;
	line-height: 1.3em !important;
}

.reply blockquote {
	margin: .5em !important;
}

.doubledash {
	display: none !important;
 	vertical-align:top !important;
	clear:both !important;
	float:left !important;
}

.replytitle {
	font-size: 1.2em !important;
	color: #FFC17B !important;
	font-weight:bold !important;
}

.thumbnailmsg {
	font-size: .6em !important;
	color:#666666 !important;
}


table {
	border-style: none !important;
}

table td {
	border-style: none !important;
}

.nothumb {
	background: url('http://web.archive.org/web/20120310193729/http://2ch.so/icons/muon_inputs.jpg') #3E3C38 !important;
	border: 1px #666666 dashed !important;
	margin: .3em .5em !important;
}

.abbrev {
	color: #666666 !important;
}

.highlight {
	color: #C6985C !important;
	background: #292825 url('http://web.archive.org/web/20120310193729/http://2ch.so/icons/muon_posts.jpg') !important;
	border: 2px dashed #725B48 !important;
	border-radius: 3px !important;
	-moz-border-radius: 3px !important;
	-webkit-border-radius: 3px !important;
	margin: 5px 5px !important;
	padding:1px 2px 3px 2px !important;
	-moz-box-shadow: 0px 0px 12px rgba(0, 0, 0, 0.3) !important;
	-webkit-box-shadow: 0px 0px 12px rgba(0, 0, 0, 0.3) !important;
	box-shadow: 0px 0px 12px #161616 !important;
}

.extrafunctions {
	color: #800000 !important;
}

dl.menu dt {
	background: url('http://web.archive.org/web/20120310193729/http://2ch.so/icons/muon_inputs.jpg') #3E3C38 !important;
	color: #757575 !important;
	border: solid 1px #34352D  !important;
	border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	margin-top: 1em !important;
	padding-left: .5em !important;
	cursor: pointer !important;
}

dl.menu dd {
	margin-left: .5em !important;
	padding-left: .5em !important;
	border-left: solid 1px #34352D !important;
}

dl.menu dd.hidden {
	display: none !important;
}

p.spoiler > span.warning {
	color: #FF6600 !important;
	font-weight: bolder !important;
}
/*[[spoiler_1]]*/

span.spoiler:hover {
	color: #AEAEAE !important;
	opacity: 1 !important;
}
.spoiler:hover a {
	color: #FCE236  !important;
}


.u { text-decoration: underline !important; } 
.s {text-decoration: line-through !important;} 
.o { text-decoration: overline !important; }
 
span.mod {
	color: #FFAB3F !important;
}

span.adm {
	color: #EE0000 !important;
}

.locked {
	width: 50%  !important;
	margin: 20px  !important;
	text-align: center  !important;
	background-color: #151515  !important;
	border:1px solid #242424  !important;
	-moz-box-shadow: rgba(155,129,101,0.7) -1px -1px 1em, rgba(155,129,101,0.7) 1px 1px 2em  !important;
	-webkit-box-shadow: rgba(155,129,101,0.7) -1px -1px 1em, rgba(155,129,101,0.7) 1px 1px 2em  !important;
	box-shadow: rgba(155,129,101,0.7) -1px -1px 1em, rgba(155,129,101,0.7) 1px 1px 2em  !important;
}
.msgheader {
	background-color: #242424  !important;
	color: #757575  !important;
}
.msg {
	background-color: #333333  !important;
	color: #FEC77D  !important;
	font-size: .9em  !important;
}

center>span[style*="rgb"] {
	color: #857070  !important;
}

div>form>select, textarea, input[type="text"], input[type="file"], input[type="password"], input[name="snum"], input[type="search"]  {
	color: #FEC77D  !important;
	background: #3E3C38 url('http://web.archive.org/web/20120310193729/http://2ch.so/icons/muon_inputs.jpg') !important;
	border: 1px solid #44453D  !important;
	border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	-o-transition: all 0.3s ease !important;
	-webkit-transition: all 0.3s ease !important;
	-moz-transition: all 0.3s ease !important;
	transition: all 0.3s ease !important;
	margin: 0px !important;
}

div>form>select:hover, textarea:hover, input[type="text"]:hover, input[type="file"]:hover, input[type="password"]:hover, input[name="snum"]:hover, input[type="submit"]:hover, input[type="search"]:hover {
	border: 1px solid #5B5347  !important;
}

div>form>select:focus, textarea:focus, input[type="text"]:focus, input[type="file"]:focus, input[type="password"]:focus, input[name="snum"]:focus, input[type="submit"]:focus,, input[type="search"]:focus  {
	box-shadow: 0 0 4px rgba(155,129,101,0.7) !important;
}

input, textarea { 
	outline: medium none   !important;
}


div[id*="preview_"] {
	border: solid 1px #34352D  !important;
}

div[style="float: right !important; margin: 0 0 0.5em 0 !important;"] {
	float: left  !important;
}

body.replypage {
	counter-reset: postcount !important;
	counter-increment: postcount !important;
}
body.replypage td.reply label:after {
	counter-increment: postcount !important;
	content: " " "#" counter(postcount) "  " !important;
	color: #789922  !important;
}
a[href="mailto:sage"],  a[href="mailto:Sage"], a[href="mailto:SAGE"] {
	color: #AA0000  !important;
}
a[href="mailto:sage"]:hover,  a[href="mailto:Sage"]:hover, a[href="mailto:SAGE"]:hover {
	text-shadow: none !important;
}
a[href="mailto:sage"]:before,  a[href="mailto:Sage"]:before, a[href="mailto:SAGE"]:before {
	content: url('http://web.archive.org/web/20120310193729/http://2ch.so/icons/sage.png')  !important;
}

::-moz-selection {
	background: #FEC77D  !important;
	color: #111111  !important;
}
::selection {
	background: #FEC77D  !important;
	color: #111111  !important;
}

pre, code {
	white-space: pre-wrap !important;
	white-space: -moz-pre-wrap !important;
	white-space: -pre-wrap !important;
	white-space: -o-pre-wrap !important;
	word-wrap: break-word !important;
	overflow: auto  !important;
	max-width: 80em  !important;
	display: block  !important;
}


input[type="submit"], input[type="reset"], .buttons input[type="button"] {
	-o-appearance: none  !important;
	-webkit-appearance: none  !important;
	-moz-appearance: none  !important;
	appearance: none  !important;
	background-color: #333333  !important;
	background-image: -moz-linear-gradient(center top, #4A4A4A, #292929, #4A4A4A)  !important;
	background-image: -o-linear-gradient(rgb(74,74,74),rgb(41,41,41),rgb(74,74,74))  !important;
	background-image: -webkit-gradient(linear, center top, center bottom, from(#4A4A4A), color-stop(25%, #313131), color-stop(50%, #292929), color-stop(75%, #313131), to(#4A4A4A))  !important;
	-webkit-border-radius: 5px  !important;
	-moz-border-radius: 5px  !important;
	border-radius: 5px  !important;
	border-bottom: 1px solid #151515  !important;
	border-top: 1px solid #151515  !important;
	border-left: 1px solid #000000  !important;
	border-right: 1px solid #000000  !important;
	color: #AAAAAA  !important;
	font-weight: bold  !important;
}
input[type="submit"]:hover, input[type="reset"]:hover, .buttons input[type="button"]:hover {
	background-color: #212121  !important;
	background-image: -moz-linear-gradient(center top, #151515, #505050, #151515)  !important;
	background-image: -o-linear-gradient(rgb(21,21,21),rgb(80,80,80),rgb(21,21,21))  !important;
	background-image: -webkit-gradient(linear, center top, center bottom, from(#151515), color-stop(25%, #222222), color-stop(50%, #505050), color-stop(75%, #222222), to(#151515))  !important;
	border: 1px solid #9B8165  !important;
	color: #FFFFCC  !important;
	box-shadow: inset 0 0 4px 1px  #9B8165 !important;
}

font[color*="2267"] em {
	color: #EE0000  !important;
	font-weight: bold !important;
}

#oekaki-form select[name="oek_painter"], #oekaki-form input[type="text"] {
	margin-right: .7em !important;
}



}

style_setting_options.value:
span.spoiler {
	background: #454545 !important;
	color: #AEAEAE  !important;
	opacity: 1 !important;
}
.spoiler a {
	color: #FCE326  !important;
}

span.spoiler {
	background: #454545 !important;
	color: #454545 !important;
	opacity: .5 !important;
}
.spoiler a {
	color: #454545  !important;
}
