css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(portableapps.com) {

#header-container {
  background-image: url("data:image/png;base64,") !important;
}

#header > a {
  position: absolute !important;
  top: 10px !important;
  background: url("data:image/png;base64,") no-repeat !important;
}

img#mainlogo {
  visibility: hidden !important;
}

#footer-container {
  background: url("data:image/png;base64,") top center !important;
}

table thead th {
background: url("data:image/png;base64,") !important;
}

h1, h2, h3, h4, h5, h6,
h1 a, h2 a, h3 a, h4 a, h5 a, h6 a,
#maincontent a, .primary-links a:hover, .new {
  color: #3366FF !important;
}

}
