css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("developer.apple.com") {

body{
    background-color:#aaa !important;
    }
    
article{
    max-width:850px !important;
    background-color:#FFF !important;
    padding:20px !important; 
    }
    
/*NAV: ONLY !IMPORTANT ARE OVERRIDES*/

/*NAV SIDEBAR*/
nav#tocContainer.isShowingTOC {
    width:360px !important;
    border-right: 1px solid #ACACAC;
    display: block;
    overflow-x: hidden;
    overflow-y: auto;
    padding: 0;
}

/*CONTENT WHEN NAV SIDEBAR IS SHOWING, MATCH 'LEFT' TO NAV WIDTH*/
#contents.isShowingTOC {
    left: 360px !important;
    min-width: 320px;
}

/*TOC LIST IN THE NAV SIDEBAR*/
#tocContainer > ul#toc {
    -moz-user-select: none;
    font-size: 11px;
    margin: 0;
    padding: 12px 0 18px;
    width: 349px !important;
}

/*LESS INDENT*/
#tocContainer > ul#toc > li > ul, ul#toc > li > ul > li {
    margin-left: 0;
    padding-left: 5px !important; 
}

/*SUB ITEMS UNBOLD*/
#tocContainer > ul#toc > li > ul, ul#toc > li > ul > li span a {
    font-weight: normal !important;   
}

/*EVERYTHING ELSE*/
h1,h2,h3,h4,h5,h6 {
    font-family: Tahoma !important;
    font-weight: bold !important;    
}


h2 {
    color: #234 !important;
    font-size: 24px !important;
    border-bottom:1px solid #DDD !important; 
    margin-top: 1.75em;
    padding-bottom: 0px !important; 
}



code, tt, samp, pre {
    color: #111!important;
    font-family: Consolas,monospace !important;
    font-size: 15px !important;
}

p code{
    color: #C37!important;
    font-family: Consolas,monospace !important;
    font-size: 15px !important;
    background-color:#F0F0F0 !important; 
    border:1px solid #DDD !important; 
}


a, a:link {
  color:#032352 !important;
}


a:visited, a:visited:hover {
  color:#436 !important;
}
  

p, p em {
    font-family: Georgia,sans-serif!important;
    color: #444!important; 
    font-size: 15px !important;
    line-height: 26px !important;
    margin-bottom: 0.833em;
    margin-top: 0;
}  

span.content_text{
    font-family: Georgia,sans-serif!important;
    color: #444!important; 
    font-size: 15px !important;
    vertical-align:text-bottom !important;
}

div.declaration {
    color: #111 !important;
    font-family: Consolas, monospace !important;
    font-size: 15px !important;  
    background-color:#F0F0F0 !important; 
    border:1px solid #DDD !important; 
    padding:11px !important; 
    border-radius:5px !important; 
    margin-bottom: 0.909em;
    margin-top: 1.182em;
}
  
  
div.declaration div.declaration{
    background-color:transparent !important; 
    border:none !important; 
    padding:0px !important; 
    margin:0px !important; 
}
  
div.api.seeAlso,div.api.availability,
div.api.declaredIn,div.api.relatedSampleCode{
    margin:5px !important;
    display:inline-block !important;
    vertical-align:top!important; 
    border-left:1px solid #DDD !important; 
    padding:10px !important; 
}

div.api.seeAlso,div.api.availability,
div.api.declaredIn,div.api.relatedSampleCode,
div.api.seeAlso code, div.api.declaredIn code {
    font-size: 12px !important;  
}
  
div.api.relatedSampleCode .content_text{
    font-size: 12px !important;
    font-family: 'Lucida Grande','Lucida Sans Unicode',Helvetica,Arial,Verdana,sans-serif !important;
}  
  
  
  
}
