@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {




#yt-masthead-container {
    width: 960px !important; 
    margin-left: auto !important;
    margin-right: auto !important;
    -position: center !important;
}

#yt-masthead {
    width: 960px !important; 
    margin-left: auto !important;
    margin-right: auto !important;
    -position: center !important;
}



page:not(.channel) {
    margin-left: auto !important;
    margin-right: auto !important;
    -position: relative !important;
    width: 960px !important; 
}









#body-container {
    -margin-left: auto !important;
    -margin-right: auto !important;
    -position: relative !important;
    -width: 960px !important; 
}

.master-sprite {
    background: url("http://web.archive.org/web/20090305161138/http://s.ytimg.com/yt/img/master-vfl77641.png") repeat-x scroll 0 0 transparent;
    border: 0 none;
    cursor: pointer;
    padding: 0;
}


#bar {
    background: url("http://web.archive.org/web/20090305161138/http://s.ytimg.com/yt/img/master-vfl77641.png") repeat-x scroll 0 -487px transparent !important;
    border: 1px solid #999999 !important;
    clear: both !important;
    cursor: default !important;
    height: 35px !important;
}


#masthead-bar-contents {
    padding-top: 4px !important;
    text-align: right !important;
}

.nav-item {
    background: url("http://web.archive.org/web/20090305161138/http://s.ytimg.com/yt/img/master-vfl77641.png") repeat-x scroll 0 -487px transparent !important;
    border-left: 1px solid #FFFFFF !important;
    border-right: 1px solid #999999 !important;
    display: block !important;
    float: left !important;
    font-size: 14px !important;
    font-weight: bold !important;
    height: 16px !important;
    padding: 9px 8px 10px !important;
    text-align: center !important;
    text-decoration: none !important;
    white-space: nowrap !important;
}

a.nav-item:hover {
    background: url("http://web.archive.org/web/20090305161138/http://s.ytimg.com/yt/img/master-vfl77641.png") repeat-x scroll 0 -522px transparent !important;
    border-left: 0 none !important;
    padding-left: 9px !important;
}
a.nav-item:active {
    background: url("http://web.archive.org/web/20090305161138/http://s.ytimg.com/yt/img/master-vfl77641.png") repeat-x scroll 0 -557px transparent !important;
    border-left: 0 none !important;
    padding-left: 9px !important;
}


#masthead-bar-contents {
    padding-top: 4px !important;
    text-align: right !important;
}

#masthead-bar-content, #masthead-search-term {
    display: inline !important;
}

a {
    color: #0033CC !important;
    text-decoration: none !important;
}










#yt-masthead-content {
    wid-th: 80px !important;
    hei-ght: 20px !important;
    fl-oat: left !important;
    float: right !important;
    padding-top: 4px !important;
    text-align: right !important;
    margin-right: 5px !important;
    margin--left:830px !important;
    padding-top: 0px !important;
    posi-tion: relative !important;
}

