    wi-dth: 80px !important;
    hei-ght: 20px !important;
    floa-t: right !important;
    margin-top: 1px !important;
    padd-ing-top: 4px !important;
    text--align: right !important;
    marg-in-left:0px !important;
}




#upload-button-menu.yt-uix-button-menu.yt-uix-button-menu-default.yt-uix-button-menu-external {
    background-color: #FFEB81 !important;
    margin: 0px !important;
    padding: 0px !important;
    border-color: #ECC101 !important;
    border-radius: 0px 0px 0px 0px;
    -z-i-ndex: 99999999 !important;
    margin-left: 20px !important;
}
#upload-button-menu.yt-uix-button-menu.yt-uix-button-menu-default.yt-uix-button-menu-external li a.yt-uix-button-menu-item.upload-menu-link {
    background: none repeat scroll 0 0 #FFFFCC !important;
    color: #994800 !important;
}

#upload-button-menu.yt-uix-button-menu.yt-uix-button-menu-default.yt-uix-button-menu-external {
    bor-der: 1px solid #999999 !important;
    border: 1px solid #ECC101 !important;
    le-ft: 0.25em !important;
    posi-tion: absolute !important;
    to-p: 1.5em !important;
    visib-ility: hidden;
    white-space: normal !important;
    wi-dth: 110% !important;
    z-index: 99;
}

#upload-button-menu li a.yt-uix-button-menu-item.upload-menu-link {
    background: none repeat scroll 0 0 #EEEEEE;
    color: #000000;
    display: block !important;
    padding: 0.4166em 0.6666em !important;
}


* html .yt-dropdown-menu {
    display: none;
    top: 1.8333em;
    visibility: visible;
    width: 10em;
}


.yt-uix-button-menu-item.upload-menu-link img {
    width: 0px !important;
    height: 0px !important;'
    margin: 0px !important;
    padding: 0px !important;
}


#upload-button-menu.yt-uix-button-menu.yt-uix-button-menu-default.yt-uix-button-menu-external li a.yt-uix-button-menu-item.upload-menu-link:hover, .yt-dropdown-primary .yt-dropdown-menu li a:hover {
    background: none repeat scroll 0 0 #FFFFFF !important;
}

#upload-button-menu.yt-uix-button-menu.yt-uix-button-menu-default.yt-uix-button-menu-external li:first-child {
    margin-top: 0 !important;
}
#upload-button-menu.yt-uix-button-menu.yt-uix-button-menu-default.yt-uix-button-menu-external li {
