css:
@namespace url(http://www.w3.org/1999/xhtml); @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')  

/* made by behindmylove.tumblr.com 
please don't delete the credits */

{

body{color: #666;}

*{text-shadow:none !important; color:#626262!important;}
    
div#right_column,

div#right_column:after {background:transparent!important;}

body,#left_column, #right_column {background-color:transparent!important;}

div.l-content.l-content.l-content {background:transparent!important;}

.post_info A,
.post_controls A,
.footer_links.with_tags SPAN A {color:#333333 !important;}

#content_bottom {border-radius:0px !important;}

.notification a {border-bottom: 0px solid #3366FF !important;}
.notification blockquote {border-left: 3px solid #3366FF !important;}

/* FENETRE QUI S'OUVRE POUR UN NEW POST */
.identity-refresh, .plexi.show.color, .post-tab-switching{
    background: none repeat scroll 0% 0% rgba(222,222,222, 0.9);}
/* FENETRE QUI S'OUVRE POUR UN NEW POST */
    
/* TOP TITLE TUMBLR */
.l-header-container--refresh {
box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.13);
transform: translateZ(0px);
z-index: 90;
background-color:#d9d9d9!important;
position: fixed;
width: 100%;}
	
.identity-refresh .plexi.show.color {background: none repeat scroll 0% 0% rgba(222,222,222, 0.9);}

.identity-refresh .controls_section li:not(.section_header):hover{background-color:#d9d9d9}
	
.compose-button {
    content: "";
	background-color:#eee!important;
	border:0px;}
	
.chrome.blue {border-color:#d9d9d9!important;
background:#ededed!important;
border-radius:0px !important;}

.logged-out-header .signup-button[disabled],.logged-out-header .signup-button.disable,.logged-out-header .signup-button[disabled]:active,.logged-out-header .signup-button.disabled.active{border:0px;background:#CCC}

/* TRACKED BLOGS/TAGS */
.xkit-people-notifier-person .hide_overflow {
    width: 125px;
	padding-left: 40px;}
	
	.xtag .result_title {
    width: 80% !important;
    display: block;
	padding-left: 30px;
}
/* TRACKED BLOGS/TAGS */

.option.selected, .option{background:transparent!important;}

/* IMG 500px */
.post_full.is_full_width.is_photo .post_media_photo {height: auto!important; max-width: 500px!important;}
.post_full.is_photoset .photoset .photoset_row .photoset_photo img {width: auto!important;}
.photoset_row {width: 500px!important; height: auto!Important; margin-left: 20px!important;}
.photoset_row_2 img {max-width: 245px!important;}
.photoset_row_3 img {max-width: 160px!important;}
.post_full.is_full_width.is_photoset .photoset .photoset_row .photoset_photo {margin-left: 10px;}
.post_full.is_full_width.is_photoset .photoset .photoset_row, .post_full.is_full_width.is_photo .photoset .photoset_row {margin-top: 10px;}
.photoset_row.photoset_row_1>a>img {width: 500px!important; min-width: 500px!important;}
/* IMG 500px */

/* MENU COLUMN */
.identity-refresh .controls_section li.selected:not(.no_push):not(.section_header), .identity-refresh .controls_section li.selected:hover:not(.no_push):not(.section_header) {
    background-color: #ccc;}
	
.identity-refresh .controls_section li {
    border-top: 1px solid #d5d9dd;
	background-color: #ededed;
    background-clip: content-box;
    position: relative;
    padding: 0px;}
/* MENU COLUMN */

/* Date et heure */
.xtimestamp {
    position: absolute;
    top: 35px;
    color: #A8B1B8;
    text-align: left;
    font-weight: normal !important;
    font-size: 11px;
    margin-bottom: 0px;
    margin-left: 19px;}
/* Date et heure */

/* ACIVITY */
#range_controls {
    position: relative;}

#range_controls h2.heading {
    position: relative;
    float: left;
    margin: 0px 0px 0px 15px;
    z-index: 10;
    font-size: 13px;
    border: 0px none;
    color: #444;
    padding: 0px 0px 20px;}

#range_controls h2.heading.arrow {
    padding-right: 20px;
	font-size: 12px;}
/* ACIVITY */

/* CONTENU */
#left_column, #right_column {background-color:transparent!important;}
div.l-content.l-content.l-content {background:transparent!important;}
#left_column, #posts, .post, .permalink{background-color:transparent!important;}
.post_container {
margin: 0px 0px 25px;}

.post {
border:1px solid #D5D9DD!important;
border-radius:0px !important;}

.left_column.notifications_page{
border: 1px solid #D5D9DD !important;
border-radius:0px !important;}

.post_source {position: relative!important;
overflow: hidden!important;
white-space: nowrap!important;
top: 2px!important;
font-size: 9.5px!important;
text-transform:uppercase!important;}

.post_footer{background: transparent!important;
border-radius:0px !important;
margin-bottom:-4px!important;}
/* FIN CONTENU */

/* ACTIVITY BIGGEST FANS AND TOP POST */
.notification .notification_right .preview_frame.conversation,.ui_post_badge.conversation,#toaster .icon.conversation{background-color:#ccc;}
.new_post_label i.icon_post_chat{color:#ccc;}
.post_full .post_tags .post_tag.featured{background:#ccc;}
.controls {background:#ccc;}

.avatar_image{
border-radius: 0px!important;}

#post_controls_avatar, .post_avatar_link {border: 1px solid #D5D9DD !important;
border-radius:0px !important;
box-shadow:0px 0px 0px 0px #ccc!important;}

.tumblelog_popover {
background: none repeat scroll 0% 0% rgb(255, 255, 255);
border-top:0px solid #999999!important;
clear: both;
min-height: 38px;
padding: 15px 15px 15px 64px;
position: relative;
border-radius: 0px!important;
box-shadow:0px 0px 0px 0px #ccc!important;}

.blog_icon {border: 1px solid #999!important;
border-radius:0px !important;
box-shadow:0px 0px 0px 0px #ccc!important;}

.token {border: 0px solid #444 !important;background-color:#DDDDDD!important;}

img[src*="http://assets.tumblr.com/images/content_bottom_edit_form.png"],
img[src*="http://assets.tumblr.com/images/content_top_edit_form.png"]{
height: 0px !important;
background-color:transparent!important;
box-shadow: none !important;
border: 0px solid #D5D9DD !important;
border-radius:0px !important;
padding:0px!important; margin:0px!important;}
/* ACTIVITY BIGGEST FANS AND TOP POST */

/* MENU QUI DEROULE AVEC LES BLOGS */

.popover_blogs, .popover_menu{box-shadow:0px 0px 0px 0px #ccc!important;
border: 0px solid #868686 !important;
background-color:#F7F7F7!important;
border-radius:0px !important;}

.popover_inner{box-shadow:0px 0px 0px 0px #ccc!important;
border: 1px solid #E9E9E9 !important;
padding:2px!important;
background-color:#ededed!important;
border-radius:0px !important;}

#popover_search_promo{box-shadow:0px 0px 0px 0px #ccc!important;
border: 0px solid #E9E9E9 !important;
background-color:#ededed!important;
border-radius:0px !important;}

#popover_search_promo {box-shadow:0px 0px 0px 0px #ccc!important;
border: 0px solid #E9E9E9 !important;
background-color:#ededed!important;
border-radius:0px !important;}

.popover_menu_item{box-shadow:0px 0px 0px 0px #ccc!important;
border-bottom: 0px solid #E9E9E9 !important;
background-color:#ededed!important;
border-radius:0px !important;}

#popover_search{box-shadow:0px 0px 0px 0px #ccc!important;
border: 0px solid #E9E9E9 !important;
background-color:#ededed!important;
border-radius:0px !important;}

.blog_url { font-size:9px!important;
text-transform:uppercase!important;}

.currently_selected_blog.hide_overflow.blog_title{ font-size:12px!important;
color:#000;
text-transform:uppercase!important;}

/* FIN MENU QUI DEROULE AVEC LES BLOGS */

/* QUESTION BLOCKQUOTE SUR LE DASH */
.post_question {background:#F4F4F4!important;
box-shadow: none !important;
border-radius:0px !important;}

.post_question_asker
{background-color:transparent!important;
text-transform: uppercase!important;
color:#000!important;
font-family:consolas!important;
letter-spacing:0px!important;
font-size:11px!important;}
 /* FIN  QUESTION BLOCKQUOTE SUR LE DASH */
 
 /* SEARCH */
#search_query {display: block;
position: absolute;
top: 0px;
right: 0px;
bottom: 0px;
left: 0px;
height: 100%;
border-width: 0px;
background-color: transparent!important;
color: inherit!important;
font-weight: bold;
-moz-user-select: text;
-moz-box-sizing: border-box;
border-radius:0px !important;}

.search_form_row {position: relative;
background-color: #ccc!important;
margin: 5px 0px 0px;
height: 28px;
width: 215px;
border-radius: 28px 28px 28px 28px;}

.search_popover .result {display: table;
height: 45px;
width: 100%;}

.popover_menu_item:hover,.popover_menu_item.keyboard_focus{background:#d9d9d9}

.xkit_tag_margin_fix {margin-left: 0px;}
/* FIN SEARCH */

/* LOADING MORE POSTS */
#previous_page_link, #next_page_link, .add_and_remove,
.mass_editor, .MissingE_marklist_title, .customize,
.recessed, .answer_avatar, .flat, .dashboard_options_form,
#auto_pagination_loader{background:#ededed !important;} 
/* FIN LOADING MORE POSTS */

/* Bouton pour remonter en haut */
.elevator{color:rgba(0,0,0,.8);height:32px;width:33px;}
/* Bouton pour remonter en haut */

/* NOUVEAU POST EN ROUGE */
.tab_notice {top: 0px !important;
background-image:none!important;
margin-left: 4px !important;
height:auto!important;
width:auto !important;
border: 0px solid #EDEDED!important;
text-align: center !important;
border-radius: 0px !important;
vertical-align:middle !important;
background-color:#de0000 !important;
font-weight: bold !important;}

tab_notice_nipple{border: 0px solid #C9C9C9 !important;
border-radius:0px !important;
background-color:none!important;}

*.tab_notice_value{text-shadow:none !important; color:#FFF!important; font-size:10px!important; }
/* FIN NOUVEAU POST EN ROUGE */

/* MENU POUR POSTER */
ol#posts li.post.new_post, #post_buttons, #new_post {background: transparent !important;
box-shadow: none !important;
border: 0px solid #D5D9DD !important;
border-radius:0px !important;
padding:0px!important; margin:0px!important;
text-transform:uppercase!important;}

#posts.posts {background:transparent !important;
border-radius:0px !important;
box-shadow:0px 0px 0px 0px #ccc!important;}
/* MENU POUR POSTER */

/* SIDEBAR */
 .controls_section, .popover_button_blogs + li, .controls_section .popover_blogs + li, .controls_section li.section_header + li {
    border-top: 0px none;
	background-color:transparent!important;
	background-image:none!important;}
/* FIN SIDEBAR */

/* IMAGES ICONS */
.notification_right {border: 0px solid #C9C9C9 !important;
border-radius:0px !important;
background-color:transparent !important;
box-shadow: 0px 10px 10px -12px #333333!important;}

.inner_frame, .avatar_frame {
background-color:transparent!important;
border: 1px solid #999!important;
box-shadow:0px 0px 0px 0px #ccc!important;
border-radius: 0px!important;}

.ui_jumbo_avatar .avatar_image{
background-color:transparent!important;
border: 0px solid #000!important;
box-shadow:0px 0px 0px 0px #ccc!important;}

/* IMAGES ICONS */
}
