css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("diasp.eu"),
domain("diaspora-fr.org"),
domain("framasphere.org"),
domain("diasp.org"),
domain("joindiaspora.com"),
domain("pod.geraspora.de"),
domain("diasp.de"),
domain("despora.de"),
domain("calispora.org"),
domain("poddery.com"),
domain("londondiaspora.com"),
domain("wk3.org"),
domain("www.cryptospora.net"),
domain("ser.endipito.us"),
domain("ilikefreedom.org"),
domain("yaspora.com"),
domain("diasp.eu.com"),
domain("nesc.io"),
domain("pod.nocentre.net"),
domain("failure.net"),
domain("diaspora.re"),
domain("dipod.es"){
body{
	background: #333 !important; /*28616A */

}


@font-face {
  font-family: 'Lobster';
  font-style: normal;
  font-weight: 400;
  src: local('Lobster'), url(https://themes.googleusercontent.com/static/fonts/lobster/v5/MWVf-Rwh4GLQVBEwbyI61Q.woff) format('woff');
}




/*****************************************************************************************
*
*					Header
*
*******************************************************************************************/

header{
	background-image: none !important;
	background-color: #404039 !important; /* #e9e4ca */
    font-family: Lobster !important;
}

.diaspora_header_logo{
	display: none !important;
	background: url('http://diasp.eu/assets/favicon-a15200a371573542232c9546210c4e69.png');
}
/*
.header-nav:before{
    display: inline-block !important;
    width: 5px !important;
    content: url('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSacLnZb0F3rxcnvttB6CV_pnCNGu9vGsrGqnPZObKJ7zYB6M5xwg');
    
}*/

#user_menu .avatar{
    border-radius: 11px !important;
}

#global_search form input{
	background-color: #C5C5C5 !important;
	box-shadow: 0 0 0px #CCCCCC !important;
	border-radius: 3px 0px 0px 3px !important;
}

/*****************************************************************************************
*
*					Body
*
*******************************************************************************************/

.span-24{
	width: 100% !important;
}

.container{
	width: 100% !important;
}


/*****************************************************************************************
*
*					Navigation (left)
*
*******************************************************************************************/

.span-5{
	width: 16% !important;

}

#home_user_badge img{
	height: 50px !important;
	width: 50px !important;
	position: relative !important;
	background: #fff !important;
	padding: 4px !important;
	border-radius: 25px !important;
	
}

#home_user_badge h4{
	font-size: 30px !important;
	font-family: Lobster !important;
}


#home_user_badge h4{
	color: #eee !important;
}

ul.left_nav a.aspect_selector:hover:before, 
ul.left_nav a.home_selector:hover:before, 
ul.left_nav a.tag_selector:hover:before, 
ul.left_nav a.element_selector:hover:before{
	content: url('http://diasp.eu/assets/favicon-a15200a371573542232c9546210c4e69.png') !important;
	position: relative !important;
	
    
	margin-left: 5px !important;
	margin-right: 5px !important;
	
}

.section .left_nav ul.sub_nav a.aspect_selector, .section .left_nav ul.sub_nav a.tag_selector{
    color: #fff !important;
}

ul.left_nav a.aspect_selector:hover:before, ul.left_nav a.home_selector:hover:before, ul.left_nav a.tag_selector:hover:before, ul.left_nav a.element_selector:hover:before{
    color: #333 !important;
}

/*****************************************************************************************
*
*					Main content (middle)
*
*******************************************************************************************/

.span-13{
	width: 60% !important;
}


.question_mark img,
.block_user img,
.hide_post img{
    background: #fff !important;
    border-radius: 4px !important;
    padding: 1px !important;
    border: 1px solid #000 !important;
}

#author_info h2{
    color: #fff !important;
    font-family: Lobster !important;
}

.media .img img{
    border-radius: 50px !important;
}

.stream_element .collapsible .expander{

	background-color: #404040 !important;
	background-image: none !important;
	font-size: 20px !important;
	text-shadow: none !important;
	line-height: 34px !important;
	font-family: Lobster !important;
	color: #eee !important;

}

.stream_container{
	border-left: 1px solid #505050 !important;
}

.comment.media{
	background-color: #fff !important;
	border-bottom: none !important;
	box-shadow: 1px 1px 1px #000 !important;
	color: #333 !important;
}

.comment{
	border-bottom: none !important;
}

.stream_element .collapsible p{
	color: #ccc !important;
}

.collapsible.comment-content > p{
	color: #000 !important;
}

.likes img{
	background: none !important;
	padding: 0 !important;
}

.avatar.small{
	padding: 4px !important;
	background: #fff !important;
	box-shadow: 1px 1px 1px #000 !important;
}

.stream_container h3,
.post-content h3,
.post-content h2,
.post-content h1{
	color: #eee !important;

	font-family: Lobster !important;
}

p{
	color: #fff !important; /* d4cb94 */

}

.diaspora_handle{
	color: #ccc !important;
}

#profile ul#profile_information > li{
    color: #fff !important;
}

/*****************************************************************************************
*
*					Right column
*
*******************************************************************************************/
#selected_aspect_contacts .avatar{
    border-radius: 20px !important;
}

.rightBar .title h5{
    color: #fff !important;
    margin-left: 3px !important;
}

.title img{
    background: #fff !important;
    border-radius: 4px !important;
    padding: 1px !important;
    border: 1px solid #000 !important;

}

#back-to-top{
    font-size: 35px !important;
    border-radius: 50px !important;
    background-color: #fff !important;
    color: #333 !important;
}

/*****************************************************************************************
*
*					Single post
*
*******************************************************************************************/

article.newspaper header{
    color: #fff !important;
}


}
