css:
@-moz-document domain(feedly.com) {

body {
    background: white;
}

#feedlyFrameHeader {
    background: #2d2d2d;
    border-bottom: 1px solid #e5e5e5;
}

#feedlyFrameHeader:after {
  content: "";
  position: fixed;
  top: 0px;
  left: 0;
  width: 100%;
  display: block;
  height: 40px;
  z-index: 10;
  
  margin: 0px;
  padding: 5px 0px 5px 0px;
  border-top: 1px solid #DFDFDF;
  border-bottom: 1px solid #DADADA;
  -webkit-box-shadow: 0px 1px 4px rgba(0, 0, 0, .08);
  -moz-box-shadow: 0px 1px 4px rgba(0,0,0,.08);
  box-shadow: 0px 1px 4px rgba(0, 0, 0, .08);
  
  background: #EEEEEE;
  background-image: linear-gradient(bottom, #EEEEEE, #F3F3F3);
  background-image: -o-linear-gradient(bottom, #EEEEEE, #F3F3F3);
  background-image: -moz-linear-gradient(bottom, #EEEEEE, #F3F3F3);
  background-image: -webkit-linear-gradient(bottom, #EEEEEE, #F3F3F3);
  background-image: -ms-linear-gradient(bottom, #EEEEEE, #F3F3F3);
}

div.area {
    margin-top: -23px;
    border: 0;
    background: transparent;
    box-shadow: none;
    padding-top: 30px !important;
}

#greetings2, body.narrow #feedlyTopNav, body.narrow .ttab.selected {
    color: #eee;
    text-shadow: none;
    line-height: 29px;
    padding-top: 0;
    font-weight: normal;
}

body.narrow #feedlyTopNav, body.narrow .ttab.selected {
    line-height: 1;
}

body.narrow #feedlyTopNav div {
    border-top: 2px solid #2d2d2d;
    padding-top: 6px;
}

body.narrow #feedlyTopNav .ttab.selected {
    border-top: 2px solid #D14836;
    padding-top: 8px;
}

body.narrow #feedlyTopNav img#contents, body.narrow #feedlyTopNav img#otherstab_pulldown {
 margin-top : -6px;  
}

#feedlyTopNav #coverttab, #feedlyTopNav #latestttab, #feedlyTopNav #savedttab {
    padding-top: 0;
}

#greetings2 a, #greetings2 span.action {
    padding-top: 5px;
}

#greetings2 a:hover, #greetings2 span.action:hover, .narrow #feedlyTopNav div:hover {
    border-top: 2px solid #D14836;
    color: white;
}

#mobileModule_part, #fusionModule_part, #listPageNumber {
    display: none;
}

#feedlyTabs {
    margin-top: 0px;
}

#feedlyTabs .tab .label 
{
  
}

#feedlyTabs .tab .label {
    color: #333;
    font-size: 12px;
    text-shadow: none;
    font-weight: bold;
    text-transform: capitalize;
    margin: 5px 0;
  	font-family: arial ,sans-serif;
}

#feedlyTabs .tab .label.selected, h2 {
    color: #DD4B39;
    text-transform: capitalize;
}

#feedlyTabsHolder{
  z-index:10;
  margin-top:-5px !important;
}

#feedlyTabsHolder #feedlyTabs .target.selected {
  border-left: 3px solid #DD4B39;
}

#feedlyTitleBar{
color: #DD4B39;
}

#floatingBar{
  background-color:transparent !important;
}

#systemBar{
z-index: 9999 !important;
position: relative;
}

#who{
border-width: 0px !important;	
}

#navSelectorHolder
{
margin-top: 11px;
}
}
