css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.rememberthemilk.com/home/YOURUSERNAMEHERE/#section.tasks") {

		div#appheader, div#statusbox, div#break, div#listtabs, div.appfooter, .snake-line, .snake-left-link, .snake-right-link { display: none; }

		div.xtoolbox_actions, div#sorting { display: none; }
		div#content, div#listbox, div#list { width: auto !important; clear: both; }

		/* Do something with the details box */
		div#detailsbox, div#details, div#detailswrap, div#detailscontent { clear: both !important; width: auto !important; }
		div#detailsbox { font-size: 1.5em; }
		
		/* Add more padding around the items */
		.xtd { padding: 15px !important; }
		
		/* Make the font a lot bigger */
		div#list { font-size: 3em; }
		
		
		/* Maybe remove this */
		div#tools_spacer, div#tasksToolbox { display: none; }
		/* Maybe add this */
		/* div#listwrap { border-top: 1px solid #CACAAC; } */
		
		div#midcontent, div#detailsbox { margin: .5em !important; }
}
