css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blogspot.com") {

  body {
     background-image: none !important;
  /*   background-color: white !important; */
  }

  #hide, #navbar, #header-wrapper, #footer-wrapper, #sidebar-wrapper, #crosscol-wrapper, #comments, #blog-pager, .post-feeds
    { display: none !important; }

  #upper_level_containers, #outer-wrapper, #wrap2, #content-wrapper, #main-wrapper, #main, #Blog1
  {
     width: 100% !important;
     max-width: none !important;
     margin: 0 !important;
     padding: 0 !important;
     float: none !important;
  }

  #primaryContent, #Blog1>div:first-child
  {
     padding: 2% !important;
     width: 96% !important;
     text-align: justify !important;
     font-size: 12pt !important;
     font-family: arial, sans-serif !important;
     max-width: none !important;
  /*
     position: absolute !important;
     left: 0 !important;
  */
  }

}
