.lyt-hdg-02, .lyt-box-02, #headNavigations, .lyt-link-pagetop
{ display: none !important; }

body {
  background-color: #f5f5f5;
}

body.broadWin div#str-container {
  width: 750px;
  margin: 20px auto;
}

#str-header {
  width: auto !important;
  padding: 40px 0 !important;
}

.header-l {
  float: none !important;
  margin: 0 auto !important;
}

#str-contentsleft {
  float: none !important;
  margin: 0 auto !important;
}

.lyt-box-01 {
  border: 0;
}

