css:
@-moz-document domain("www.gunplablog.com") {

body,
div{
 font-family: Arial!important;
 }

body{
 background-color: #e9e9e9!important;
 }
div{
 color: #000!important;
 }

#all{
 width: 1000px!important;
 }

#hd-top{
 display: none;
 }

#all,
#hd-middle,
#hd-middle-left,
#hd-middle-center,
#hd-middle-right, 
#bd-right-cont,
#bd-right,
#bd,
#ft{
 background-color: #fff!important;
 }
 
#bd-left{
 display: none;
 }

#bd-right-cont{
 width: 100%!important;
 }
 
#bd-center{
 width: 750px!important;
 float: right!important;
 }
#bd-right{
 margin: 0!important;
 width: 250px!important;
 float: left!important;
 }

/*header--------------------*/
#hd-middle-left a:before{
 content: "GUNPLA BLOG";
 margin: 0 20px!important;
 font-family: Arial!important;
 font-size: 14px;
 font-weight: bold!important;
 }
#hd-middle-left,
#hd-middle-center{
 width: 100%!important;
 float: none!important;
 }
#hd-middle-left{
 padding: 10px 0!important;
 }
#hd-middle-left a{
 color: #333!important;
 text-decoration: none!important;
 }
#hd-middle-left img{
 display: none;
 }
#hd-middle-center{
 padding: 0!important;
 height: auto!important;
 }
#hd-middle-center h2{
 margin: 0 20px 15px!important;
 color: #999!important;
 font-size: 13px!important;
 font-weight: normal!important;
 }
#hd-middle-right{
 display: none;
 }

/*navigation bar-----------*/

#hd-bottom{
 padding: 0!important;
 width: 100%!important;
 border-top: 1px solid #e9e9e9!important;
 border-bottom: 1px solid #e9e9e9!important;
 clear: none!important;
 }

ul.topmenu{
 background-color: #fff!important;
 height: 30px!important;
 }
ul.topmenu li{
 font-size: 13px!important;
 font-weight: normal!important;
 border-right: 1px solid #e9e9e9!important;
 width: 130px!important;
 text-align: center!important;
 }
ul.topmenu li a{
 padding: 0!important;
 line-height: 30px!important;
 display: block;
 }
ul.topmenu li a:hover{
 color: #000!important;
 background-color: #f0f0f0!important;
 }
 
/*entry--------------------*/
.entry{
 margin: 20px 0!important;
 border: none!important;
 }

.entry_title,
.entry_state{
 padding-left: 10px!important;
 margin: 0 20px 0 10px!important;
 text-align: left!important;
 }
 
.entry_title{
 border-left: 5px solid #333!important;
 font-size: 16px!important;
 }
.entry_title a{
 color: #000!important;
 text-decoration: none!important;
 }
.entry_title a:hover{
 text-decoration: underline!important;
 }

.entry_state{
 padding-top: 7px!important;
 padding-bottom: 7px!important;
 font-size: 12px!important;
 border-left: 5px solid #d0d0d0!important;
 border-top: 1px dotted #d0d0d0!important;
 border-bottom: 1px dotted #d0d0d0!important;
 background-color: #f0f0f0!important;
 }
 
.entry_state a{
 color: #666!important;
 text-decoration: none!important;
 }
 
.entry_body{
 padding-top: 10px!important;
 margin: 5px 20px!important;
 font-size: 13px!important;
 }

.entry_body a{
 color: #666!important;
 }
 
.pict_w,
.pict_h{
 margin: 0!important;
 border: none!important;
 }
 
.entry .entry_state:last-child{
 border-top-style: solid!important;
 border-left: none!important;
 border-bottom: none!important;
 background-color: #fff!important;
 }

.entry .entry_state:last-child a{
 text-decoration: underline!important;
 }
 
/*comments-------------------*/
.com{
 border: 1px solid #ddd!important;
 background-color: #fff!important;
 }
.com_body{
 font-size: 13px!important;
 }
.com_state{
 font-size: 13px!important;
 border-top: 1px solid #ddd!important;
 }
.com_state a{
 color: #666!important;
 }
 
/*sidebar--------------------*/
#navi{
 margin: 20px 0 0 0!important;
 }

#navi .linktext:nth-child(1),
#navi .linktext:nth-child(2){
 display: none;
 } 

.slidetitle{
 font-size: 14px!important;
 }
.slidetitle:hover{
 color: #333!important;
 background-color: #fff!important;
 }
.linktitle{
 margin: 0 20px!important;
 border-bottom: 3px solid #ddd!important;
 color: #333!important;
 font-size: 14px!important;
 width: auto!important;
 }

.linktext{
 margin: 10px 20px 30px!important;
 font-size: 13px!important;
 }

.linktext a{
 color: #666!important;
 text-decoration: none!important;
 }
.linktext a:hover{
 color: #333!important;
 text-decoration: none!important;
 }
 
.linktext ul li{
 margin-left: 20px!important;
 list-style: disc outside none!important;
 }
 
/*table----------------------*/
.gunpla_tbl table{
 width: 100%!important;
 border: none!important;
 }

.gunpla_tbl table th{
 color: #333!important;
 border: 1px solid #ddd!important;
 background-color: #f0f0f0!important;
 }

.gunpla_tbl table td{
 padding: 0 5px!important;
 border: 1px solid #ddd!important;
 }
 
.gunpla_tbl table td a{
 color: #666!important;
 }

/*ranking--------------------*/
#info{
 padding: 0!important;
 margin: 15px 20px!important;
 }
 
.ranksum{
 margin: 0 0 10px 20px!important;
 width: 305px!important;
 background-color: #fff!important;
 }

.rankbody{
 margin: 0 auto!important;
 }

.ranktitle{
 margin: 0 0 10px!important;
 }

#s_items{
 width: 100%!important;
 border-color: #aaa!important;
 }
#s_items .title{
 margin: 3px 7px!important;
 font-size: 12px!important;
 }
#s_items .title a{
 color: #666!important;
 text-decoration: none!important;
 }
#s_items .title a:hover{
 text-decoration: underline!important;
 }
#s_items .company{
 font-size: 11px!important;
 }
#s_items li,
li .seq{
 border-color: #aaa!important;
 }
#s_items li{
 height: 60px!important;
 }
li .seq{
 background-color: #adff2f!important;
 }
  
 }
