css:
@-moz-document url-prefix("http://www.gethifi.com/tools/regex"), url-prefix("https://think2011.net/regex-tester/") {
#regexPaneInputs {
    color: rgb(255, 240, 240);
}
dd {
    color: rgb(255, 255, 255);
}
#cheatSheet {
    background: rgba(26, 19, 0, 0.88);
  box-shadow: -18px 19px 12px 0px rgba(0, 0, 0, 0.81);
  opacity:.1;
  -webkit-transition: all 0.5s ease-in-out;
-moz-transition: all 0.5s ease-in-out;
-ms-transition: all 0.5s ease-in-out;
-o-transition: all 0.5s ease-in-out;
transition: all 0.5s ease-in-out;
}
dt {
    color: #FFF;
}
#haystackPaneText {
    font-family: century gothic, Courrier, Georgia, Palatino, serif;
    font-size: 11px;
}
#haystackPane .match {
    color: rgb(129, 84, 1);
    box-shadow: #FFF 0 0 12px;
}
.group-1, .group-2, .group-3, .group-4, .group-5, .group-6 {
    color: rgba(0, 0, 0, 0.72) !important;
}
.group-2 {
    color: rgba(0, 0, 0, 0.87);
}
.group-3 {
    color: rgba(0, 0, 0, 0.88);
}
table {}
#matchesPane table {
background: rgba(51, 51, 51, 0.8);
font-size: 14px;
width:100%;
}
#regexPane {
    position: fixed;
    top: -62px;
    left: 0;
    width: 100%;
    box-shadow: 0 0 12px 4px #000;
  -webkit-transition: all 0.5s ease-in-out;
-moz-transition: all 0.5s ease-in-out;
-ms-transition: all 0.5s ease-in-out;
-o-transition: all 0.5s ease-in-out;
transition: all 0.5s ease-in-out;
}
#matchesPane 
{
position: fixed;
bottom: 0;
height: 27px;
max-height: 27px;

min-width:20%;
width: 60%;
max-width:90%;

word-break: break-all;
border: #FFF thin solid;
background: rgba(0, 0, 0, 0.84);
left: 0;
z-index: 2;
box-shadow: 0 0 12px 4px #000;
  -webkit-transition: all 0.5s ease-in-out;
-moz-transition: all 0.5s ease-in-out;
-ms-transition: all 0.5s ease-in-out;
-o-transition: all 0.5s ease-in-out;
transition: all 0.5s ease-in-out;
resize: auto;
}
#regex {
    width: 89%;
}
#codePane {
    position: fixed;
    bottom: -148px;
    right: 0;
    z-index: 1;
-webkit-transition: all 0.5s ease-in-out;
-moz-transition: all 0.5s ease-in-out;
-ms-transition: all 0.5s ease-in-out;
-o-transition: all 0.5s ease-in-out;
transition: all 0.5s ease-in-out;
}
#codePane:hover {
    bottom: 0;
    z-index: 2;
}
#matchesPane:hover {
z-index: 10;
bottom: 0px;
height: 60%;
max-height: 90%;
overflow-y: auto;
min-width:20%;
width: 60%;
max-width:90%;
}
#cheatSheet:hover {
    right: 0;
    opacity:1;
}
#regexPane:hover {
    top: 0;
}
#regexPane {
top: 0;
}
#haystackPaneText {
    word-break: break-all;
    width: 100%;
    margin: 0 auto;
}
#haystackPane {
    width: 80%;
    margin: 22px auto;
}
#tool {
    width: 100%;
}
#container {
    width: 100%;
	margin: 86px auto;
}
#header {
	top: 103px;
    right: 0;
    width: auto;
}
#sidebar {
    position: fixed;
    right: -280px;
    -webkit-transition: all 0.5s ease-in-out;
-moz-transition: all 0.5s ease-in-out;
-ms-transition: all 0.5s ease-in-out;
-o-transition: all 0.5s ease-in-out;
transition: all 0.5s ease-in-out;
}
#sidebar:hover {
    right: 0;
}
html, body{color: #FFF;background: #000;font-family: Courrier, Georgia, Palatino, serif;color: #FFF;background: rgb(27, 27, 27);font-family: Courrier, Georgia, Palatino, serif;}
#container #tool {
    width: 100%;
}
#haystackPaneInputs textarea {
    font-size: 10px;
    font-family: Century Gotic,Courrier, arial;
    background: rgba(37, 37, 37, 0.85);
    color: rgb(0, 255, 0);
    height: 14%;
    position: fixed;
    top: 10%;
    left: 14px;
    width: 32%;
    margin: 0 auto;
    z-index: 10;
}
#regex {
    background: #111;
    color: rgb(255, 255, 255);
}
#codePane {
}
#code {
    box-shadow: 0 0 12px 4px #000;
}
#cheatSheet dl dt {
    color: rgb(255, 245, 0);
}
#footer a, #footer a:visited, #footer a:hover {
color: rgb(255, 255, 255);
}
#cheatSheet h3.selected {
background: rgb(36, 30, 0);
color: rgb(255, 217, 97);
font-weight: bold;
border: 2px solid #FCC119;
text-shadow: 0px 1px 4px rgb(0, 0, 0),0px 1px 4px rgb(0, 0, 0);
}
#cheatSheet h3,  #cheatSheet h2 {
    text-shadow: #000 0 0 3px,rgb(77, 1, 1) 0 1px 7px;
}

#regexPane.invalid {
background: rgb(90, 1, 1);
border: 3px solid #FF0000;
  box-shadow: 0 0 12px 4px #FF0000;
}
#regexPane.match {
border: rgb(0, 255, 20) 2px solid;
background: rgba(7, 53, 22,.9);
color: rgb(221, 255, 44);
text-shadow: 0px 1px 4px #000000;
-webkit-box-shadow: 0px 4px 12px 0px rgb(73, 255, 2);
box-shadow: 0px 0px 6px 0px rgba(82, 255, 0, 0.8) inset,0px 0px 28px 0px rgba(0, 255, 0, 0.8);
}
#regexPane.valid {
background: rgb(78, 78, 1);
border: 3px solid #FFFF33;
}
#about {
background: rgb(39, 33, 0);

}
}
