css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tarvalon.net") {

    a {
        text-decoration: none !important;
    }
    
    
    /* Dark red BG */
    .navbar,
    .forumhead,
    .wgo_block .blockhead,
    .newcontent_textcontrol,
    .posthead,
    #footer,
    .pagination span.selected a,
    .vbform .fullwidth,
    .blockhead,
    .blockfoot,
    .blockfoot.actionbuttons,
    .threadlisthead,
    .blockhead
    {
        background-color: #91140E !important;
    }
    
    /* Dark biege BG */
    .navtabs,
    .navtabs li a.navtab
    {
        background-color: #d28768 !important;
    }
    
    /* Medium biege BG */
    html,
    .navlinks,
    .wgo_block .blockbody,
    .toolsmenu,
    .userinfo,
    .postdetails,
    .postfoot .textcontrols,
    .postfoot a,
    .pagination span a,
    .wysiwyg_block .blockbody,
    .wysiwyg_block .blockbody .blockrow,
    .bbcode_quote,
    .threadbit .sticky,
    .threadbit .sticky .alt,
    .forum_info .blockbody,
    .forumbit_nopost .subforumdescription,
    #usercp_nav .blocksubhead
    {
        background-color: #FBD9B2 !important;
    }
    
    /* Light biege BG */
    .body_wrapper,
    .forumbit_nopost .forumbit_nopost .forumrow,
    .forumbit_post .forumrow,
    .postbody,
    .threadbit .nonsticky,
    .threadbit .nonsticky .alt,
    .threadbit .discussionrow,
    #usercp_nav .blockrow
    {
        background-color: #FFEDD8 !important;
    }
    
    /* Dark red font */
    .forumrow a,
    .navlinks a,
    .wgo_block a,
    .breadcrumb a,
    .pagetitle a,
    .toolsmenu a,
    .postdetails a,
    .postbody a,
    .pagination span a,
    .threadbit a,
    .forum_info a
    {
        color: #91140E !important;
    }
    
    /* pagination select link */
    .pagination span.selected a {
        color: #fff !important;
    }
    
    /* editor */
    html.CSS1Compat {
        background-color: #fff !important;
    }
    
    .blockhead {
        border-color: #91140E !important;
    }

}
