css:
@-moz-document domain("rizzoma.com") {
/*
MINIMAL
a custom style for Rizzoma by http://www.wavewatchers.org

GET IT:
http://userstyles.org/styles/85193/rizzoma-minimal-ui
https://github.com/WaveWatchers/wave-custom-styles/blob/master/Rizzoma/minimal.css
*/

.right-tools-panel {
  z-index: 3;
}
.right-tools-panel .unvisible {
  display: none;
}

.wave-blips>.container-blip {
  margin: 0; padding: 0;
}

.root-thread .blip-container {
  border-radius: 0;
  border: 0;
  border-top: 1px solid #faa;
}

.root-thread .editor-container {
  padding: 0 0 0 3rem;
}

.blip-container .active {
  background-color: #ffd;
}

.blip-container .active:not(.root-blip) {
  background-color: #ffa;
  border-top: 2px solid #8292A1;
}

.blip-container .non-editable {
  background-color: #fafafa;
}

.blip-info {
  float: left;
  margin: 0 0 0 .2rem;
}

.blip-info .edit-date {
  left: 0;
}

.fold-button-container {
  margin-left: .2rem;
}

.root-blip {
  padding: 0;
  border-top: 0 !important;
}

.root-blip>.editor-container {
  padding: 2.5rem 1rem 2.5rem 1.5rem;
}

.root-blip>.blip-info {
  float: right;
  margin: 2.2rem 1.5rem 0 0;
  color: #f55;
}

.blip-menu button.is-folded-by-default.active {
  color: #000;
}

.bottom-blip-menu {
  bottom: -30px;
  border: 1px solid #f0f0f0;
  border-top-color: #ddd;
  border-bottom-color: #ddd;
  border-radius: 0;
}

.blip-container .active:last-of-type {
  margin-bottom: 30px;
}

.topic-tip, .wave-panel {
  z-index: 1;
}
}
