css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.inside.com.tw") {

/** General ************************************/

body { font-family: '微軟正黑體', sans-serif !important; }

h1, h2, h3, h4, h5, h6 {
  font-family: '微軟正黑體', sans-serif !important;
}

/** Header *************************************/

div.topcont ul#nav-top li a{
  font-size: 13px !important;
  line-height: 16px !important;
  padding: 10px 16px !important;
}

ul#social-icons li {
  padding:  6px 4px 4px 4px !important;
}

div.topcont ul#social-icons li a {
  background-size: 268px 52px !important;
  width: 26px !important;
  height: 26px !important;
}

  
div.topcont ul#social-icons li#facebook a {
  background-position: -32px 0 !important;
}
  
div.topcont ul#social-icons li#twitter a {
  background-position: -64px 0 !important;
}

div.topcont #searchform #s {
  font-size: 13px !important;
  line-height: 20px !important;
  padding: 8px 16px !important;
}
  
div.topcont #searchform #searchsubmit {
  background-color: #DDD !important;
  background-position: 8px 8px !important;
  height: 36px !important;
  padding: 5px !important;
}

header h1 a {
  background-size: 204px 54px !important;
  width: 204px !important;
  height: 54px !important;
  margin: 8px 0 !important;
}

header #top-ad {
  display: none !important;
}

div.menucont ul li a {
  padding: 10px 16px !important;
}

/** Content Block ******************************/

/* Annoying */
#content #featured { display: none !important; }

article.post {
  padding-bottom: 20px !important;
  margin-bottom: 20px !important;
}

.thumb-container {
  width: 22% !important;
  margin-right: 12px !important;
}
  
.thumb-container .share-post {
  padding: 3px 50px 3px 25px !important;
  background-position: 4px -292px !important;
  font-size: 14px !important;
}

.thumb-permalink {
  background-position: 2px -320px !important;
  width: 24px !important;
  height: 24px !important;
}

.post-container {
  width: 74% !important;
}

#articles h2.entry-title {
  padding: 10px 0 !important;
  font-size: 20px !important;
  line-height: 26px !important;
}
  
#articles h2.entry-title a {
  text-decoration: none !important;
}

.content, .excerpt {
  padding: 12px 12px 0 0 !important;
  font-size: 15px !important;
}

/** Content Block (Small) **********************/

#articles-small {
  margin-top: 20px !important;
  padding-top: 12px !important;
}

#articles-small h3.title {
  padding: 6px 0 !important;
  margin: 0 0 12px !important;
}

#articles-small h2.entry-title {
  padding: 0 !important;
  margin-bottom: 10px !important;
  line-height: 15px !important;
}

#articles-small h2.entry-title a {
  padding: 0 !important;
  margin: 0 !important;
  text-decoration: none !important;
}

/** Reading Page *******************************/

.post-container.single {
  width: 100% !important;
}

.post-container.single .rating-stars {
  display: none !important;
}

.post-container.single .fb-social-plugin {
  width: auto !important;
  height: 30px !important;
}

.post-container.single .fb-social-plugin +*:before {
  display: block;
  width: 0;
  height: 0;
  content: '.';
  clear: both;
  overflow: hidden;
}

.comment-body p, .content p {
  font-size: 16px !important;
  line-height: 1.75em !important;
  padding: 6px 0 12px 0 !important;
}

.comment-body blockquote p, .content blockquote p {
  padding: 6px 20px 6px 30px !important;
}

/** Sidebar Counters ***************************/

#sidebar .widget {
  margin-bottom: 20px !important;
  overflow: hidden;
}

ul#counters {
  float: right !important;
}
  
ul#counters li {
  background-size: 60px 80px !important;
  margin-bottom: 0 !important;
  opacity: 0.4 !important;
}
ul#counters li:hover { opacity: 1 !important; }
  
ul#counters li a{
  width: 60px !important;
  padding-top: 60px !important;
}

#magnovus_feedburner_subscribe-2 { display: none !important; }
.widget.ads { display: none !important; }

/** Recent Articles ****************************/

.widget.popular li h4 a { color: #000 !important; }
.widget.popular li a { color: #666 !important; }

.widget.popular .author-link {
  border-color: #444444 !important;
  background-position: 0 -228px !important;
}
.widget.popular .published-time {
  background-position: 0 -259px !important;
}

.widget.popular li.even {
  background-color: #F2F2F2 !important;
}
.widget.popular li.even .comments-link {
  background-color: #EAEAEA !important;
}

.widget.popular li.odd {
  background-color: #EAEAEA !important;
}
.widget.popular li.odd .comments-link {
  background-color: #F2F2F2 !important;
}

/** Sidebar Others *****************************/

#rss-3 { display: none !important; }

/** Footer *************************************/

.row.widfoot { padding-bottom: 12px !important; }

#footer-left-column { width: 40% !important; margin-right: 0 !important; }
#footer-center-column { width: 40% !important; margin-right: 0 !important }
#footer-right-column { width: 20% !important; margin-right: 0 !important }

.row.widfoot .column {
   float: left;
   width: 50% !important;
}
#footer-right-column .column { width: 100% !important; }

.footer h3 {
  margin: 12px 0 8px !important;
  font-size: 16px !important;
}

.footer ul li {
  padding: 4px 4px 4px 14px !important;
  font-size: 13px !important;
  width: 40% !important;
}

.footer .textwidget div {
  text-align: left !important;
  font-size: 13px !important;
}

#wpstats { display: none !important; }

}
