/* AGENT_SHEET */
@namespace html url(http://www.w3.org/1999/xhtml);
@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url-prefix(http://), url-prefix(https://), url-prefix(ftp://), url-prefix(view-source:), url-prefix(about), url-prefix(file:///), url-prefix(unmht:///), url-prefix(javascript) 
{ 
  html {
    background-color: #000 !important; 
    color: #0f0 !important; 
    background-position: center !important;
    width: 100% !important;
  }

/*----- DEFAULT TEXT, BORDER & BACKGROUND COLORS -----*/
* {
  color: #0f0 !important; 
  text-shadow: none !important; 
  background-color: #000 !important; 
  -moz-border-top-colors: #991 !important; 
  -moz-border-bottom-colors: #991 !important; 
  -moz-border-left-colors: #991 !important; 
  -moz-border-right-colors: #991 !important; 
  font-weight: bold !important; 
}

table {
  background-color: #000 !important; 
  -moz-border-radius: 6px !important;
} /*** background ***/

div[style] > form {
  background-color: #000 !important; 
  -moz-border-radius: 6px !important;
}

a, a * {
  color: #f80 !important; 
  text-decoration: none !important; 
  font-weight: bold !important; 
}
a:hover, a:visited:hover, a:hover *,span[onclick]:hover, div[onclick]:hover, [role="link"]:hover, [role="link"]:hover *, [role="button"]:hover, [role="button"]:hover *, [role="menuitem"]:hover, [role="menuitem"]:hover *, .gac_b:hover *, .google-ac-b *, .yt-suggest-selected *, .yt-uix-button-menu-item:hover, .acl div.sug .sel *, .acl div.sug .sel, .ac_active, .ac_active *, .yui-ac-bd li:hover {
  color: #FF0 !important; 
  text-shadow: none !important;
} /*** text color ***/
a:visited {
  color: #f0f !important;
}   /*** visited color ***/

.selected, .selected *, .gtab-selected, .gtabi-mc, [href="#"] {
  color: #fff !important; 
  background-color: #651 !important; 
  font-weight: bold !important;
}

h1, h2, h3, h4, h1 *, h2 * {
  color: #fff !important;
}
[class*="error"], [class*="alert"], code, span[onclick], div[onclick] {
  color: #fff !important;
}

::-moz-selection {
  background-color: #080 !important; 
  color: #fff !important;
}
:focus {
  outline: none !important;
}

/*----- MENU & CO BACKGROUND-COLORS -----*/
iframe, embed, label [onclick], div[style*="position:"][style*="left:"][style*="visible"], div[style*="z-index:"][style*="left:"][style*="visible"], div[style*="-moz-user-select"], div[role="menu"], div[role="dialog"], span[class*="script"] div, [class*="dropdown"], [class="title"], ul[style*="display:"], #vspb, #search_controls, .menu, .tooltip, .olp-mn, .gh-iovr, #categoriesdropdown, #user_link_menucontent, .overlay-stream, .dd, .ac_res, .yui-ac-bd, #overhead-collect #lub, .bluestip, .oh-menu, .popup2, .bt-mid, .gac_m, .google-ac-m, .gbm, .yt-suggest-table, .yt-uix-button-menu, .acl div.sug, .yt-uix-tooltip, .yt-uix-tooltip-tip > div > div, #currentStore {
  background-color: #000 !important;
  -moz-border-radius: 5px;
}

/*----- OTHER COLOR CHANGES -----*/
.modbox *:empty, div[style*="border: 1px"], div[style="display: block;"] {
  background-color: #000 !important;
}
i.gr2, i.gr3, .gr-top {
  background-color: #000 !important;
}
.goog-imageless-button-top-shadow {
  border-color: #555 !important;
}
.cc-in, .gr-body, .smbutton > span, #watch-info, #watch-next-list, #watch-channel-discoverbox {
  background: #000 !important;
}

/*----- DEFAULT BUTTONS, SEARCHBOXES & CO -----*/
input, select, button:not(.ws):not(.wsa):not(.vspib), a[href="javascript:;"]:not([t]):not([onclick]) {
  -moz-appearance: none !important;
  background: #000 !important;
  border: 2px solid !important;
  -moz-border-radius: 4px !important;
}
input:hover, input[type="button"]:hover, select:hover, button:hover {
  -moz-border-top-colors: #555 !important;
  -moz-border-bottom-colors: #555 !important;
  -moz-border-left-colors: #555 !important;
  -moz-border-right-colors: #555 !important;
}
input:focus, select:focus {
  -moz-box-shadow: none !important;
}
input[type="checkbox"], input[type="checkbox"]:focus {
  border: 2px solid #722 !important;
  background-color: #000 !important;
  color: #fff !important;
  -moz-border-radius: 4px !important;
}
input[type="radio"], input[type="radio"]:focus {
  border: 2px solid #622 !important;
  background-color: #000 !important;
  color: #fff !important;
}
select input[type="button"] {
  border: none !important;
}

button:active, input[type="submit"]:active { 
  background: #000 !important;
}

input *, input, select {
  color: #FFF  !important;
}
input *:hover * {
  color: #FFF !important; text-shadow: none !important;
}

textarea {
  -moz-appearance: none !important; 
  background: #000 !important; 
  -moz-border-radius: 3px !important; 
  border: 1px solid #000 !important; 
  -moz-box-shadow: none !important;
}
textarea:focus {
  background: #000 !important;
}
textarea, textarea * {
  color: #fff !important; 
  text-shadow: none !important;
}
textarea:focus, textarea:focus > * {
  text-shadow: none !important; 
  -moz-box-shadow: none !important;
}

option { -moz-appearance: none !important; background: none !important; color: #ff0 !important}
option:not([disabled]):hover, option:focus, option:checked {
  background: #000 !important; 
  color: #f0f !important;
}

/*----- IMAGE CHANGES -----*/
*:not(:empty):not(html):not(span):not(option):not([style="display: block;"]):not([onclick*="open"]):not([onclick*="s_objectID"]):not(#globalsearch):not(IMG):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="SPRITE"]):not([id*="SPRITE"]):not([class*="item"]):not([id*="item"]):not([class*="thumb"]):not([id*="lbImage"]):not([class*="cc-in"]):not([class*="gr-body"]):not([id*="watch"]):not(.sp),
span[style*="width"], .r3_hm, .gmbutton2 b, .gtab-i, .ph, .bstab-iLft, .csb, #pagination div, [style*="sprite2.png"] {
  background-image: none !important;
}
.olp-mn * {
  background: none !important; 
  border: none !important;
}

img {
  opacity: 1 !important;
}
img:hover, a:hover img {
  opacity: 1 !important;
}

.a9 {
  background: #000 !important;
} 

img[alt^="YouTube"] {
  width: 0 !important;
  padding-right: 100px !important;
  opacity: 1 !important;
  background: #000 !important;
} 
}


/*----- ABOUT PAGES BACKGROUND -----*/
@-moz-document url-prefix(about) {
* {
  background-color: #000;
}
body > * {
  background-color: #000 !important;
  width: 100% !important;
} 
}

/*----- SHOW INSTALLS ON USERSTYLES.ORG -----*/
@-moz-document domain(userstyles.org) {
li[total-install-count]:after {
  font-size: .70em !important;
  content: " (Installs: " attr(total-install-count) ", " attr(weekly-install-count) " wk)";
} 
}

/*----- RIGHT-CLICK MENUS & TOOLTIP STYLE -----*/
#contentAreaContextMenu menuitem[disabled="true"], #contentAreaContextMenu menuitem[disabled="true"] menuseparator {
  display: none !important;
}

popup {
  -moz-appearance: none !important;
  background: #000 !important;
  border: 1px solid !important;
  -moz-border-left-colors: #155 !important;
  -moz-border-right-colors: #515 !important;
  -moz-border-top-colors: #551 !important;
  -moz-border-bottom-colors: #511 !important;
  -moz-border-radius: 8px !important;
  -moz-box-shadow: none !important;
  padding: 2px 0 !important;
}

popup * { /*** right click menu popup ***/
  color: #0f0 !important;
  background: #010 !important;
}

popup menuitem:hover * {
  color: #fff !important;
  background: #000 !important;
  text-shadow: none !important;
}

popup menuitem:last-child:hover * {
  color: #FFF !important;
  background: #000 !important;
  text-shadow: none !important;
}

popup menuseparator {
  -moz-appearance: none !important;
  margin: 1px 0 1px -3px !important;
  padding: 0 !important;
  border-top: none !important;
  border-color: #616 !important;
  -moz-box-shadow: none !important;
}

popup menuitem {
  -moz-appearance: none !important;
  font-size: 13px !important;
  font-weight: bold !important;
}

tooltip {
  -moz-appearance: none !important;
  color: #fff !important;
  background-color: #000 !important;
  border: 1px solid #555 !important;
  -moz-border-radius: 3px !important;
}
