@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.freerepublic.com") {

body.index-page h3{
    font-size : medium !important;
    margin-bottom : 0 !important;
    margin-top : 1em !important; }
    
body.index-page ul.listing .meta .attrib {
    margin-top : 0 !important; }

body.index-page ul.listing div.text {
    margin-top : 0 !important; }
    
body.index-page ul.listing div.content {
    margin-left : 0 !important; }

body { background: url('http://i104.photobucket.com/albums/m165/t17o76m/beer_854_480_zps10a6bee6.gif')
  no-repeat fixed center black !important;
  background-size: 100% !important;  }

/*  opacity:0.53 !important; */

* { background-color: rgba(0,0,0,0.0) !important;
  border-color: rgba(0,0,0,0.0) !important;
  color: rgba( 0,0,0,0.45)!important;}

a, body.index-page ul.listing .meta .attrib a, a * { color : rgba(51, 102, 153, 0.75) !important;  text-decoration: none !important; outline: 0 !important; } 

a:visited, body.index-page ul.listing .meta .attrib a, a:visited * { color : rgba(51, 102, 153, 0.75) !important; } 

a:hover, a:hover * { color : rgba(224, 0, 64, 0.66) !important; }

img { opacity:0.4 !important; } 

img:hover { opacity:0.85 !important; }

div#header a { color : rgba(51, 102, 153, 0.75) !important;  text-decoration: none !important; outline: 0 !important; } 

div#header a:hover { color : rgba(224, 0, 64, 0.66) !important; }

div#header ul strong { color: rgba(34, 139, 34, 0.75) !important;
 text-decoration:blink !important; 
 text-transform:capitalize !important; } 
 
div#header ul strong:hover { color : rgba(224, 0, 64, 0.66) !important; }
        input[type=text], textarea {
  -webkit-transition: all 0.30s ease-in-out !important; 
  -moz-transition: all 0.30s ease-in-out !important; 
  -ms-transition: all 0.30s ease-in-out !important; 
  -o-transition: all 0.30s ease-in-out !important; 
  outline: none !important; 
  padding: 3px 0px 3px 3px !important; 
  margin: 5px 1px 3px 0px !important; 
  border:1px solid #000 !important; 
}
 
input[type=text]:focus, textarea:focus {
  box-shadow: 0 0 5px #DCDCDC !important; 
  padding: 3px 0px 3px 3px !important; 
  margin: 5px 1px 3px 0px !important; 
  border: 1px solid #000 !important; 
}
}
