@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("e-sim.org") {
    .plate{
    background: none repeat scroll 0 0 #E1E1E1 !important;
    }
    .ui-widget-header {
        background: -moz-linear-gradient(top,  rgba(69,72,77,1) 0%, rgba(0,0,0,1) 100%) !important;
    }
    .ui-widget-content{
        border: 1px solid #222 !important;
    }
    .progress .bar .ui-widget-header {
        border-color: #222 !important;
    }
    .smallArticleTab, .bigArticleTab{
        background: linear-gradient(to bottom, #343434 0%, #5B5B5B 45%, #474747 51%, #000000 100%) repeat scroll 0 0 transparent !important;
        border: 1px solid #222222 !important;
        box-shadow: 0 0 2px 2px rgba(10, 10, 10, 0.35) !important;
    }
    .rightTabBlue, .rightTabGrey, .rightTabLower{
        background: -moz-linear-gradient(top,  rgba(244,188,83,1) 0%, rgba(244,172,90,1) 100%) !important;
        border-color: #666 !important;
        box-shadow: 0 0 5px rgba(100, 100, 100, 0.5), 0 -12px 12px rgba(100, 100, 100, 0.2) inset !important;
        text-shadow: 1.2px 1.2px 0.5px #D0D0D0 !important;
    }
    .miniContent{
        border-color: #666 !important;
        box-shadow: 0 0 5px rgba(100, 100, 100, 0.5), 0 -12px 12px rgba(100, 100, 100, 0.2) inset !important;
        margin-left: 0 !important;
        width: 350px !important;
    }
    a:link, a:visited {
        color: #6A6A6A !important;
        transition: color 0.1s ease-in !important;
    }
    a:hover {
        color: #FF4444 !important;
    }
    .littleDashedLine {
    border-bottom: 1px dashed #AEAEAE !important;
    border-top: 1px dashed #AEAEAE !important;
    }
    .shout2{
        box-shadow: 0 0 4px rgba(0, 0, 0, 0.1) !important;
    }
    input, select, textarea{
        border: 1px solid #222222 !important;
    }
    #twitterPlate{
        border: 2px solid #787878 !important;
        box-shadow: 0 0 2px #404040 !important;
    }
    .testDivblue{
        background: none repeat scroll 0 0 #EFEFEF !important;
        border: 1px solid rgba(0, 0, 0, 0.7) !important;
        box-shadow: 0 0 5px rgba(0, 0, 0, 0.5), 0 -12px 12px rgba(0, 0, 0, 0.25) inset !important;
    }
    .smallStatsLabel, .tooltip, .blueLabel, #hitHelp, .equipmentBlueBox, #criticalHelp, #missHelp, #avoidHelp{
        background: none repeat scroll 0 0 #EFEFEF !important;
        border: 1px solid rgba(0, 0, 0, 0.5) !important;
        box-shadow: 0 0 5px rgba(0, 0, 0, 0.3), 0 -12px 12px rgba(0, 0, 0, 0.15) inset !important;
    }
    .equipmentRedCap{
        border: 1px solid rgba(0, 0, 0, 0.35)!important;
        box-shadow: 0 0 5px rgba(255, 187, 0, 0.4), 0 -12px 12px rgba(188, 153, 122, 0.4) inset !important;
    }
    .showShoutDetails:hover{
        background: none !important;
    }
    .citizenAction{
        background: none repeat scroll 0 0 #FEFEFE !important;
        border: 1px solid rgba(0, 0, 0, 0.7) !important;
        box-shadow: 0 0 1px rgba(0, 0, 0, 0.8), 0 -12px 12px rgba(0, 0, 0, 0.2) inset !important;
    }
}
