css:
@-moz-document domain('feedly.com'){


.entryBody a 
{ 
color : rgb(73, 73, 73) !important;
text-decoration: underline !important;
} 

div#feedlyFrame { width : 98% !important;}
div#feedlyPart { width : 98% !important;}
div#feedlyPage, div#mainArea { width: 100% !important;}
div#sideArea { display: none !important; }

.entryHolder {
width:100% !important;
max-width: 100% !important;
}

.area { margin-left:100px !important } 


div#feedlyTabs { width:400px !important; }
div#feedlyTabsHolder { width:400px !important; }
div#mainArea { width:100% !important; }
BODY.home { background: #FFFFFF; !important; }


.u100Entry               { width: 100% !important; }
.u100Entry.selectedEntry { width: 100% !important; } 

/* .u100Frame.selectedEntry {  border: 1px solid #CCCCCC !important; } */
.u100Frame               { padding: 10px !important; cursor: auto !important;  }

.entryTitle  { font-size: 20px !important; }
.title      { font-size: 20px !important; width : 50% !important; }
.title.read { color: rgb(120, 120, 120) !important; }



.topWikiWidget {display: none !important; float:right !important;  } /* Why is topWikiWidget not displayed within entryHeader div? */

div#fullyLoaded.entriesLoadingMessage { padding-bottom: 1000px !important;  }
img.highlightableIcon { display: none !important; }
img.wikiWidgetSave { display: inline !important; }
.gplus iframe { display: none !important; } /* Why is Google plus one still visible? */

.bottomWikiWidget  { display: inline !important; }


div#plusone { display: none !important; }


.entryBody { margin-top: 5px !important; } /* broken */


}
