css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.hardocp.com") {

html * {
  font-family: Helvetica, "Lucida Sans Unicode", Arial;
  font-size: 14px;
  line-height: 20px;
  letter-spacing: 0.3px;
  font-weight: 300;
}
html {
  margin: 0 !important;
  padding: 0 !important;
  background: black;
  position: static !important;
  overflow: visible;
}
html body {
  margin: 0px !important;
  padding: 20px !important;
  width: 960px !important;
  margin-left: auto !important;
  margin-right: auto !important;
  overflow: visible !important;
  border-left: 2px solid #e5e5e5;
  border-right: 2px solid #e5e5e5;
  position: static !important;
  background: #181818 !important;
}
html body a {
  color: #888 !important;
  text-decoration: underline !important;
}
html body a:hover {
  text-decoration: none !important;
  color: #e5e5e5 !important;
}
html body h3 {
  color: #e5e5e5 !important;
  font-weight: 700 !important;
  font-size: 20px !important;
  line-height: 35px !important;
  padding: 0 !important;
  margin: 0 0 15px !important;
}
html body blockquote {
  padding: 0 !important;
  margin: 0 -20px !important;
  width: 100%;
}
html body blockquote p,
html body blockquote p i {
  margin: 0 !important;
  background: #888 !important;
  color: #181818 !important;
  padding: 0 20px !important;
  width: 100%;
}
html body blockquote p i {
  font-weight: 300;
  font-style: italic;
  padding: 0 !important;
}
html body blockquote a,
html body blockquote a:active,
html body blockquote a:visited {
  background: #888 !important;
  color: #181818 !important;
  text-decoration: underline;
}
html body blockquote a:hover {
  text-decoration: none;
}

.byline {
  margin-left: -20px !important;
  margin-right: -20px !important;
  padding-left: 20px !important;
  padding-right: 20px !important;
  border-bottom: 1px solid #e5e5e5 !important;
}

#wrapper {
  display: block !important;
  width: 960px !important;
  overflow: visible !important;
  position: relative;
}

#content-wrapper {
  display: block !important;
  width: 960px !important;
  overflow: visible !important;
  position: relative;
}

#header .headermask { display: none !important; }

#header {
  display: block !important;
  width: 100% !important;
  height: 80px !important;
  border: none !important;
  margin: 0 !important;
  padding: 0 !important;
  color: #e5e5e5;
  position: static;
  background: #181818 !important;
}
#header * {
  background: #181818 !important;
}

#header .social_media {
  display: none !important;
}

#header .logo {
  border: 0 !important;
  padding: 0 !important;
  margin: 0 !important;
  display: block !important;
  width: 50% !important;
  float: left !important;
}
#header .logo a,
#header .logo a * {
  font-size: 60px;
  text-decoration: none !important;
  border: 0 !important;
  margin: 0 !important;
  padding: 0 !important;
}
#header .logo a:before {
  content: attr(title);
}
#header .logo img {
  display: none !important;
}
#header .slogan {
  display: block !important;
  width: 50% !important;
  float: right !important;
  margin: 0 !important;
  border: none !important;
}

#header .menu {
  display: block !important;
  float: left !important;
  width: 100% !important;
  margin: 20px -20px !important;
  padding: 10px 20px !important;
  border: 0 !important;
  border-top: 1px solid #e5e5e5 !important;
  border-bottom: 1px solid #e5e5e5 !important;
}
#header .menu ul#nav {
  margin: 0 !important;
  padding: 0 !important;
}
#header .menu ul#nav li {
  display: inline !important;
  margin: 0 !important;
  margin-right: 10px !important;
  padding: 0 !important;
}
#header .menu ul#nav li:first-child {
  margin-left: 60px !important;
}
#header .menu ul#nav li:last-child {
  margin-right: 60px !important;
}
#header .menu ul#nav li a {
  display: inline !important;
  padding: 0 !important;
  margin: 0 !important;
  font-size: 12px !important;
  text-decoration: underline;
  border: 0 !important;
}
#header .menu ul#nav li a:hover {
  text-decoration: none !important;
  color: white !important;
  border: 0 !important;
}


#content-left {
  position: relative;
  top: 0;
  right: 0;
  display: block !important;
  background: #181818 !important;
}
#content-left * {
  background: #181818 !important;
}
#forum_post {
  background: none !important;
  border: 1px solid #e5e5e5 !important;
  padding: 10px !important;
  margin: 0px 0 20px !important;
  width: 320px !important;
}
#forum_post img {
  display: none;
}
#forum_post ul {
  padding: 0 !important;
  margin: 0 !important;
}
#forum_post ul li {
  list-style-type: none !important;
  color: #e5e5e5 !important;
  padding: 0;
  margin: 0;
  width: 320px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
#forum_post:nth-of-type(1) ul li:before {
  content: '[F]: ';
}
#forum_post:nth-of-type(2) ul li:before {
  content: '[T]: ';
}

#mainarticle p,
#mainarticle i,
#mainarticle h3,
#mainarticle hr,
#mainarticle div {
  display: block;
  width: 100%;
  float: left;
  margin: 5px 0;
  padding: 0;
}
#mainarticle hr {
  border: 0 !important;
  border-bottom: 1px solid #e5e5e5 !important;
}

#mainarticle .articleinfo {
  position: absolute;
  top: 170px;
  left: 0;
  display: block;
  float: right;
  width: 580px;
  border: 1px solid #e5e5e5 !important;
  margin: 0px;
  padding: 10px;
}
#mainarticle .articleinfo .addthis_toolbox {
  width: 80%;
  margin: 0;
  padding: 5px 0 0;
}


#mainarticle .articleintro {
  display: block;
  float: left;
  clear: both;
  width: 100%;
  border-bottom: 1px solid white !important;
  margin: 20px -20px !important;
  padding: 20px !important;
}
#mainarticle .articleintro img {
  margin: 10px !important;
  padding: 0 !important;
  float: left !important;
  display: block !important;
}
#mainarticle .articleintro h3 {
  width: auto !important;
  display: inline !important;
  white-space: normal !important;
}
#mainarticle .articleintro p {
  width: auto !important;
  display: inline !important;
  white-space: normal !important;
  padding: 0 !important;
  margin: 0 !important;
}

#mainarticle .topcontrols {
  position: absolute;
  top: 0px !important;
  right: 15px;
  display: block !important;
  width: 600px;
  margin: 0px !important;
  padding: 0px !important;
}
#mainarticle .topcontrols select {
  width: 100%;
}
#mainarticle .topcontrols select option {
  background: #e5e5e5 !important;
  color: #181818 !important;
  font-family: Helvetica, "Lucida Sans Unicode", Arial;
}
#mainarticle .topcontrols select option:hover {
  background: #888 !important;
}

#content-wrapper > center:nth-of-type(1),
.bottomcontrols {
  display: block;
  float: left;
  clear: both;
  width: 100% !important;
  border-top: 1px solid #e5e5e5 !important;
  margin: 60px -20px 0 !important;
  padding: 20px !important;
  border-bottom: 1px solid #e5e5e5 !important;
}
#content-wrapper > center:nth-of-type(1) {
  margin-bottom: 20px !important;
}
.bottomcontrols form {
  display: block;
  width: 100%;
  overflow: visible;
  padding: 0;
  margin: 0;
}
.bottomcontrols ul {
  display: block;
  width: 100%;
  overflow: visible;
  padding: 0;
  margin: 0;
}
.bottomcontrols ul li {
  display: block;
  margin: 0;
  padding: 0;
  text-align: center;
  vertical-align: middle;
}
.bottomcontrols ul li.prevpg {
  float: left;
}
.bottomcontrols ul li.nextpg {
  float: right;
}
.bottomcontrols select option {
  background: #e5e5e5 !important;
  color: #181818 !important;
}
.bottomcontrols select option:hover {
  background: #888 !important;
}



#footer {
  margin: 0 !important;
  padding: 0 !important;
  width: 100% !important;
}

#footer .footer_header {
  width: 100% !important;
  height: 0 !important;
  padding: 0 !important;
  margin: 0 !important;
}

#footer .footer_content {
  width: 100% !important;
  display: block !important;
  clear: both !important;
  overflow: hidden !important;
  padding: 0 !important;
  margin: 0 !important;
  background: #181818 !important;
}

#footer .footer_column {
  background: transparent;
  display: block !important;
  float: left;
  margin: 0;
  padding: 5px;
  font-size: 12px !important;
  width: 230px !important;
}
#footer .footer_column * {
  background: transparent !important;
  font-size: 12px;
  margin: 0 !important;
  padding: 0 !important;
}
#footer .footer_column ul li {
  list-style: disc !important;
}

#footer .footer_copyright {
  border-top: 1px solid #e5e5e5 !important;
  width: 100% !important;
  padding: 20px 20px 0 !important;
  margin: 20px -20px !important;
}



#hardnews,
#announcements {
  width: 100% !important;
  clear: both !important;
  background: #181818 !important;
  overflow: hidden !important;
}
#archivelist .archivecontrols h4,
#announcements h2,
#hardnews h2 {
  background: #181818 !important;
  display: block !important;
  float: left !important;
  width: 100% !important;
  margin: 0 !important;
  padding: 0 !important;
  clear: both !important;
  overflow: hidden !important;
  height: 60px !important;
  border-bottom: 1px solid white;
  padding-bottom: 12px !important;
  margin-bottom: 10px !important;
}
#archivelist .archivecontrols h4,
#hardnews h2:after,
#announcements h2:after {
  content: ':';
  font-size: 33px !important;
  line-height: 60px !important;
  font-weight: 700 !important;
}
#archivelist .archivecontrols h4 span,
#hardnews h2 span,
#announcements h2 span {
  margin: 0 !important;
  padding: 0 !important;
  font-size: 33px !important;
  line-height: 60px !important;
  font-weight: 700 !important;
  display: inline !important;
  visibility: visible !important;
}
#announcements .story {
  display: block !important;
  float: left !important;
  width: 100% !important;
  margin: 0 -20px !important;
  padding: 10px 20px !important;
  border: 0 !important;
  border-bottom: 1px solid #e5e5e5 !important;
}

#hardnews,
#hardnews .day,
#hardnews .day .story {
  width: 100% !important;
  clear: both;
  background: #181818 !important;
  padding: 0 !important;
  margin: 0 !important;
}
#hardnews .day h4 {
  background: #181818 !important;
}
#hardnews .story blockquote {
  width: 100% !important;
  display: block !important;
  padding: 0 20px !important;
  margin: 0 -20px !important;
  background: #888 !important;
}
#hardnews .story blockquote p {
  display: inline-block !important;
  background: #888 !important;
  margin: 0 !important;
  padding: 10px !important;
  width: 98%;
}




#content-wrapper > img:nth-of-type(1) {
  width: 100%;
  height: 1px;
  visibility: hidden;
}
ul.featured_articles {
  width: 100% !important;
  margin: 0 !important;
  padding: 0 !important;
}
ul.featured_articles * {
  margin: 0 !important;
  padding: 0 !important;
  background: transparent !important;
}
ul.featured_articles li {
  width: 100% !important;
  margin: 0px -20px !important;
  padding: 0 20px !important;
  border-bottom: 1px solid #e5e5e5 !important;
}
ul.featured_articles > li:nth-of-type(1) {
  border-top: 1px solid #e5e5e5 !important;
}
ul.featured_articles li:hover {
  background: #2a2a2a !important;
  cursor: pointer;
}
ul.featured_articles .image {
  margin: 10px !important;
}
ul.featured_articles a.fullbox {
  padding: 0 !important;
  margin: 0 !important;
  border: none !important;
  text-decoration: none !important;
}
ul.featured_articles a.fullbox:hover {
  border: none !important;
}
ul.featured_articles .title {
  color: #e5e5e5 !important;
  font-weight: 700 !important;
  font-size: 20px !important;
  line-height: 30px !important;
  margin-bottom: 10px !important;
}
ul.featured_articles img {
  border: none !important;
}

}
