css:
@-moz-document domain("deviantart.com") {


.mobile { display: none; }
body { background-color: #506256; }
.fez-menu { background-color: #506256; width: 200px; color: whitesmoke; position: absolute; left: -200px; top: 40px; transition: left 0.4s;  -moz-transition: left 0.4s; -webkit-transition: left 0.4s; box-shadow: inset -21px 0px 10px -15px #3C4441;
;  }
.fez-menu a, .fez-menu .mi { display: block; color: #9EB1A3 !important; }
.fez-menu .oh-hr { display: none; }
.fez-menu .child { padding-bottom: 10px; color: #9EB1A3; }
.fez-menu a.selected, .fez-menu .noncollapseHeader { color: white !important; }
.fez-menu .child .oh-smaller.oh-darker { display: none; }
.fez-menu .parent { font-size: 16px; padding: 10px 0px 10px 5px; background-color: #3C4441 }
.fez-menu .mi { height: 20px; font-size: 12px; padding: 5px 0px; padding-left: 10px; }
.fez-menu .mi i { display: none; }

.fez-menu.active { left: 0px; transition: left 0.4s;  -moz-transition: left 0.4s; -webkit-transition: left 0.4s; }

.fez-menu .browse-facet { width: 100%; }
.fez-menu .browse-facet ul a { font-size: 12px; padding: 10px 6px !important; }
.fez-menu .browse-facet-browse-links a { font-size: 16px !important; }

.fez-menu .grptagline, .fez-menu #groups_links { display: none; }
.fez-menu .grpname { display: block; text-align: center; }
.fez-menu .grpname .u { display: inline; position: relative; }
.fez-menu .grpicon { display: block; text-align: center; }
.fez-menu .gm-chaos.gmbuttons { margin-top: -80px; }

.fez-menu .child.aboutinsidebar  { margin-left: 10px; }
.fez-menu .child.aboutinsidebar strong { color: white; }
.fez-menu .child.aboutinsidebar small { color: whitesmoke; }
.fez-menu .child > b { color: white; display: block; margin-bottom: -23px; margin-top: 10px;}
.fez-menu .child.info { color: whitesmoke ; }
.fez-menu .child.info label { color: #9EB1A3; }
.fez-menu .child.info strong { margin-top: 10px; display: block; margin-bottom: -10px; }
.fez-menu .child.info .a { display: inline; }

.fez-menu .pdw_button_download { width: 150px !important; }
.fez-menu .pdw_title_normal { font-size: 14px !important; }
.fez-menu #pointsdownload_widget, .fez-menu .pdw_details { width: 145px !important; }
.fez-menu #pointsdownload_widget .pdw_button_download { min-width: 0px; }
.fez-menu { display: none; }
.fez-menu .smbutton.right.deviationmenu.mobile-only { display: none;}
.fez-menu .edit_buttons { width: 180px; }

.fez-menu .pager-holder, .fez-menu .pager2, .fez-menu .page2 { width: 100% !important; background-color: transparent !important;   }
.fez-menu .page2 a { background: none !important; }
.fez-menu .page2 .headder { font-size: 16px; padding: 10px 0px 10px 5px; background: #3C4441; }
.fez-menu .page2 .icon { background-image: url("data:image/gif;base64,"); }
    
    #messages { min-width: 0px; }
    
    
  

 
@media (min-width: 100px) and (max-width: 960px) {

   #overhead-collect { position: fixed; }

   .fez-menu { display: block; }

   #output, .bubbleview, #overhead-collect { transition: all 0.4s;  -moz-transition: all 0.4s; -webkit-transition: all 0.4s; }
   #output.active, .bubbleview.active, .active #overhead-collect { margin-left: 200px; overflow: hidden; transition: all 0.4s;  -moz-transition: all 0.4s; -webkit-transition: all 0.4s; margin-right: -200px;  }


   #output.active .bubbleview.active { margin-left: 0px; }
   body > #depths { position: relative; left: 200px; }

   .ghost-edit.active { left: 211px !important; }


    .mods-column-ll, .mods-column-rr { width: 100% !important; display: block; }
    body { min-width: 100% !important; width: 100%; overflow-x: hidden; }
    .notMobile { display: none; }
    .mobile { display: block; }
  
    #oh-menu-deviant, #oh-menu-split, #oh-menu-submit, #oh-menu-shop, #oh-menu-friends, #oh-menu-collect { display: none; }
    .right-buttons, .sitbackbutton, .submit-art-button, #gmi-ResourcePageMetaPane, .browse-left-bar { display: none !important; } 
    .gtab { font-size: 0px;  line-height: 13px; padding-right: 0px; }	
    #gmi-ResourcePageMLTPreviewClickTracker, .morelink { display: none; clear: both; }
    .gmpanel.gallery-links { display: block; } 
    #gmi-EditableResourceStream, .page-results.results-page-thumb { text-align: center; }
    .f.gruze-sidebar.left-sidebar.module-min.module-blocks, #footer-pane-extra { display: none; }

    .browse-results-page { padding-left: 0px !important; }
    .browse-top-bar { margin-left: 0px !important; }

    .resview7-about, .resview7-view { margin-right: 0px !important; }

    #gmi-ResViewContainer { max-width: 100% !important; }
#zoomed-in img { max-width: 100%;
height: auto !important; }

.f.messages-left { display: none; }

.messages-head { height: 60px !important; }

div.messages .gmbutton2admin .gmbutton2plus { top: -65px; }

#ile-container .ile-right .ile-right-panel { position: relative; }

.btd-browse div.stream.stream-fh, .browse-container div.stream.stream-fh, #gruze-columns div.stream.stream-fh.shop200h, .gruze-main div.stream.stream-fh { text-align: center; }

}
}
