css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com"),domain("www.youtube.com") {

@font-face {
  font-family: 'Boogaloo';
  font-style: normal;
  font-weight: 400;
  src: local('Boogaloo'), local('Boogaloo-Regular'), url(https://themes.googleusercontent.com/static/fonts/boogaloo/v3/1x_XysQB9ZwoSzu9a0nb7PesZW2xOQ-xsNqO47m55DA.woff) format('woff');
}

#body{background: #E9E4CA !important;}
#yt-masthead #logo-container span.content-region, #yt-masthead #logo-container .logo {background: none !important;}
#yt-masthead-container,.yt-grid-box{
    /* top menu */
    background: #28616A !important;
    top: 0 !important;left: 0px !important; right: 0px !important;
    box-shadow: 0px 2px 6px #333 !important;
}
#masthead-expanded{
    /* sub menu */
    margin-top: 5px !important;
    position: fixed !important;
    z-index:1000 !important;
    width: 100% !important;}
    
#appbar-guide-menu {margin-top: 60px !important;} /* Hidden menu */
#watch-response{background: none !important;margin-top: 152px !important;position: relative !important;}
#watch-response-content, #watch-response-header{background: none !important;margin-top: 172px !important;}
#yt-masthead a.yt-uix-button,
ul.yt-uix-button-menu, .yt-uix-button-menu{background: #E9E4CA !important;}
#yt-masthead #masthead-search-terms, #yt-masthead button.yt-uix-button{background: #C94C46 !important;border: 1px solid #C94C46 !important;}
body #masthead-expanded-container{margin-top:50px !important;background: #28616A !important;border-top: 1px solid #333 !important;box-shadow: 0px 2px 6px #333 !important;}
body #masthead-expanded-container a{color: #E9E4CA !important;}
#yt-masthead #logo-container span.content-region {display: none !important;}
#yt-masthead #logo-container:before{
    content: 'YouTube' !important;
    position: relative !important;
    height: 10px !important;
    display: block !important;
    margin-left: -28px !important;
    font-family: Boogaloo !important;
    font-size: 38px !important;
    font-style: italic !important;
    text-shadow: 1px 1px 2px #C94C46 !important;
    color: #E9E4CA !important;
    }
#yt-masthead #logo-container:hover{text-decoration: none !important;}
#masthead-search-term{background: #E9E4CA !important;font-family: Boogaloo !important;}
#yt-masthead #search-btn{background: #C94C46 !important;border: 1px solid #C94C46 !important;}
#yt-masthead-user{color: #E9E4CA !important;font-family: Boogaloo !important;}
#yt-masthead-user-displayname{font-size: 32px !important;color: #E9E4CA !important;}
#page-container{margin-top: 52px !important;}
.yt-lockup-list{position:absolute !important;width: 100% !important;display:block !important;left:50% !important;margin-top:60px !important;}
.feed-promo h3{font-family: Boogaloo !important;}
#watch7-sidebar{background: #28616A !important;padding-top: 10px !important;}
#watch7-sidebar .title{color: #000 !important;}
#watch7-sidebar .video-thumb{background: #fff !important;padding:0px !important;}

body #footer-container{background: #E9E4CA !important;box-shadow: 0px -2px 6px #333 !important;}
}
