css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {

/*color theme and background image*/ body {
  background:url("http://i.imgur.com/ZGEoJ0U.jpg") no-repeat bottom left fixed !important;

  background-color: #00010e !important;


  

}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color: rgba(0,0,0,.0) !important;
}
.l-container.l-container--two-column-dashboard .left_column{
background-color: rgba(0,0,0,0) !important;
}
    .l-container.l-container--two-column
.right_column, 
  
.l-container.l-container--two-column-dashboard .right_column{
background-color: rgba(0,0,0,0) !important;
}
.l-header-container--refresh {background: rgba(0,0,0,0)!important}
.xkit-control   {
  background-color:transparent !important;
  border-radius: 20px !important;
}

.compose-button {
display:none!important;
  
}
.post_avatar, .blog-avatar {
    
  background-color: transparent;}


/*header*/#header .tab.iconic a{

background:transparent !important;
  
border-radius: 15px !important;

margin-top: -8px !important;
margin-bottom: 20px !important;
padding-bottom: 0px !important;
box-shadow: 0px 10px 8px -9px #000 !important;
height: 80px !important;}



.search_form_row  {
background: rgba(0,0,0, 0.1) !important;
color: #000 !important;
  }




     


::selection {
background-color:#bbb;
}

/*sidebar*/#right_column:after { 
display: none !important; 
}

/*sidebar*/#right_column a {
color:#494949!important;  
}

/*sidebar*/#right_column .controls_section li a {
 background:#00010e!important;
border-bottom:0.1px solid #494949 !important
}

/*color tags*/#right_column .controls_section li a .count {

  color: #494949 !important; 
}

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Posts" !important;    padding-left:28px;
  
}
.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Followers" !important;    padding-left:28px;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Draft" !important;    padding-left:28px;
}.controls_section li .queue:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .queue:before {
  content: "Queue" !important;    padding-left:28px;
}




.new_post_label_icon> I {
display: none !important
}

.new_post_label { 
   
   color: rgba(0,0,0,1) !important; 

}

.new_post_label:hover { 
   color: rgba(0,0,0,0.5) !important; 
}

#new_post {
    background: #fff none no-repeat left bottom !important;
    height: 100px !important; 
}

#new_post .arrow {
    display: none !important;
}
        
        


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(236,191,201, 0.50) !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(236,191,201, 0.50)  !important;
}



#content_top {
  width: 0px !important;
  height: 0px !important;


}


#content_bottom {
  width: 0px !important;
  height: 0px !important;




}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  padding-top: 25px !important; 
  padding-bottom: 25px !important; 
   -moz-border-radius: 10px !important; 
}


LABEL {
  color: rgba(0,0,0, 1.0) !important; 
}



.radar {
  display: none !important; 
}



#footer {
  display: none !important; 
}
.selected .following,.selected .followers,.selected .settings,.selected .posts,.selected .messages,.selected .drafts,.selected .queue,.selected .likes,.selected .activity  {
  color: rgba(255,255,255, 0.9) !important; 
  background: transparent !important; 
}

img.content_bottom {
display:none !important;
}

.l-content.clearfix {
padding: 20px;
background: transparent;
  }

.notification  {
background:transparent !important;
border:none !important;

}

.notification .notification_sentence{
color:#494949!important;
}

.notification :link, .notification :active, .notification :visited {
color:#494949 !important;
text-decoration:none !important;
border:none !important;
}

#pagination a {
background: #00010e!important;
color: #eee !important;
  box-shadow: 0px 10px 8px -9px #000 !important;
}

#pagination a:hover {
background: #666 !important;
  box-shadow: 0px 10px 8px -9px #000 !important;
}


#new_post .arrow {
  opacity: 0.50 !important;
  margin-left: -3px !important; 
}}
