.ad_adsense{display:none;}
#BloMotion {display:none;}
#Footer    {display:none;}


#Main,
#BodyWrapper,
#localnav,
#localnav ul,
.OneTopic,
.OneMsg,
.MsgBody{
width:auto !important;
}

#Main {display:block !important;}

.Main-hd,
.OneMsg,
.Main-contents,
.UserThumb32,
.UserThumb,
.Main-ft,
.ttlLv1{background-image:none !important;}

.OneMsg        {background-color:#fff !important;}
.Main-contents {border:1px solid #efefef !important;}
#Main .Update form textarea {width:auto !important;}

#Main,.MsgBody,#Side {float:none !important;}
.movie   {text-align:left !important;}

/* firefox */
.OneMsg       {border-top:1px solid #efefef !important;}
.OneMsg:hover {background-color:#ffffc0;}

/* Opera CSS3 
.OneMsg:nth-child(2n+1),
#My .OneMsg:nth-child(1),
#My .OneMsg:nth-child(3),
#My .OneMsg:nth-child(2n+4){background-color:#eeeeee;}

#My .OneMsg:nth-child(2),
#My .OneMsg:nth-child(2n+5){background-color:#ffffff !important;}
*/

img {vertical-align:bottom !important;}

#My #BodyMain            {width:auto !important; display:block !important; float:left !important;}
#My ul.UserList          {width:auto !important;}
#My ul.UserList li       {width:auto !important; display:block !important;}
#My ul.UserList li:hover {background-color:#f1f2c4 !important;}

#My         #Side,
#User       #Side,
#Channel    #Side,
#Todo       #Side,
#Config     #Side,
#Tools      #Side,
#Help       #Side,
#UsersReply #Side{position:absolute !important; top:120px !important; right:20px !important;}

#My         #Main,
#User       #Main,
#Channel    #Main,
#Todo       #Main,
#Config     #Main,
#Tools      #Main,
#Help       #Main,
#UsersReply #Main{margin-left:1em !important; margin-right:200px !important;}


}
