css:
/* AGENT_SHEET */
@-moz-document domain("boards.4chan.org") {
    body {
      background: #edeef0 !important;
      font-family: "Helvetica" !important;
      font-size: 10pt !important;
    }
    
    /*General colors*/
    a.useremail[href*='sage']:last-of-type::after,
    a.useremail[href*='Sage']:last-of-type::after,
    a.useremail[href*='SAGE']:last-of-type::after {
        content: " (sage)" !important;
        color: #d48e94 !important;
        font-size: 9pt !important;
        font-weight: bold !important;
    }
        
    .useremail .name {
        color: #5c6066 !important;
        text-decoration: none !important;
        font-weight: bold !important;
    }

    .useremail .name:hover {
        color: #2e3033 !important;
    }
    
    a.useremail {
        color: #999faa !important;
        font-style: italic !important;
        text-decoration: none !important;
    }
    
    a.useremail:hover {
        color: #7a7f88 !important;
    }
    
    #spoilerLabel {
        color: #0c192b !important;
        text-shadow: 0px 1px 1px #619bf2 !important;
    }

    .name {
        color: #5c6066 !important;
        font-weight: bold !important;
        text-decoration: none !important;
    }
           
    .postertrip {
        color: #5c6066 !important;
    }

    .dateTime {
        color: #999faa !important;
    }
    
    .fileText {
        color: #999faa !important;
    }

    .postNum a, .inline .postNum a {
        color: #7c818a !important;
    }

    .postNum a:hover, .inline .postNum a:hover {
        color: #5c6066 !important;
    }

    .fileInfo a, .fileText a, .inline .fileInfo a, .inline .fileText a {
        color: #7c818a !important;
    }

    .fileText a:hover, .fileInfo a:hover, .inline .fileInfo a:hover, .inline .fileText a:hover {
        color: #5c6066 !important;
    }
    
    .fileInfo a {
        font-style: italic !important;
    }
    
        
    .fileText a {
        font-style: normal !important;
    }
    
    a.replylink {
        color: #ccc !important;
        text-decoration: none !important;
        font-size: 9pt !important;
    }

    a.replylink:hover {
        color: #999faa !important;
    }

    .post.op .postNum > span {
        color: transparent !important;
        font-size: 0px !important;
    }

    .backlink, .inline .backlink {
        color: #7c818a !important;
        text-decoration: none !Important;
    }
    
    .backlink:hover, .inline .backlink:hover {
        color:#5c6066 !important;
    }
    
    .postMessage {
        color: #34363a !important;
    }
    
    .warning > a, .warning, #errmsg {
        color: #0c192b !important;
        text-shadow: 0px 1px 1px #619bf2 !important;
        text-align: center !important;
    }
    
    .linkifyplus a, .postMessage a, #credits a {
        color: #3a78d4 !important;
        text-decoration: none !important;
    }
    .linkifyplus a:hover, .postMessage a:hover, #credits a:hover {
        color: #254d87 !important;
    }

    .quote {
        color: #28915d !important;
    }
    
    .subject {
        color: #7a7f88 !important;
        font-weight: bold !important;
    }
    
    .summary {
        color: #999faa !important;
        float: right !important;
        margin-right: 2px !important;
        padding-bottom: 0px !important;
        font-size: 9pt !important;
    }
    
    .summary:hover {
        color: #7a7f88 !important;
    }

    .abbr {
        font-size: 9pt !important;
        color: #999faa !important;
    }

    .quotelink.filtered {
        color: #999faa !important;
        text-decoration: line-through !important;
    }

    .nameBlock .posteruid {
        color: #999faa !important;
    }

    .nameBlock > .posteruid > .hand:hover {
        color: #7a7f88 !important;
    }

    .nameBlock strong {
        color: #d43a47 !important;
    }

    .deadlink {
        color: #999faa !important;
    }

    /*Things to hide*/
    hr,
    .deleteform,
    .postingMode,
    .stylechanger,
    .boardBanner > img.title,
    .postForm,
    .move > span > select,
    .extButton,
    h1,
    #imageType,
    select#imageType,
    div.center,
    #hideBtn {
        display: none !important;
    }
    
    /* Hide ugly outline on clicked elements */
    *:active, *:focus {
        outline: none !important;
    }
    
    .postNum a[title="Highlight this post"] {
        font-size: 0 !important;
        color: transparent !important;
    }
    
    .boardBanner {
        margin-bottom: 0px !important;
        background: #fff !important;
        width: 94% !important;
        margin: 0 auto !important;
        padding-top: 28px !important;
        padding-bottom: 5px !important;
        margin-bottom: -25px !important;
        box-shadow: 0px 1px 1px rgba(0,0,0,0.1) !important;
        border-radius: 1px !important;
    }
    
    .boardTitle {
        color: #5c6066 !important;
        font: normal 400 34pt Georgia !important;
        letter-spacing: 0px !important;
        margin-bottom: 4px !important;
    }

    .boardSubtitle {
        margin-top: -2px !important;
        color: #5c6066 !important;
        font: normal 8pt Lucida Grande !important;
    }

    .boardSubtitle a {
        color: #3a78d4 !important;
        text-decoration: none !important;
    }
    
    .boardSubtitle a:hover {
        color: #3369ba !important;
    }

    #boardNavDesktopFoot, .absBotText {
        font-size: 0px !important;
        color: transparent !important;
    }
    
/*Top bar & ribbon (To do: ribbon sides with CSS instead of images)*/
    #boardNavDesktop {
        position: fixed !important;
        right: 3% !important;
        width: 94% !important;
        left: 3% !important;
        top: 0px !important;
        padding-top: 2px !important;
        height: 18px !important;
        background: #3a78d4 !important;
        z-index: 2 !important;
        text-align: center !important;
        
        border-top: 5px solid #eceef0 !important;
        box-shadow: 0px 3px 3px rgba(0,0,0,0.2) !important;
        font-size: 0 !important;
        color: transparent !important;
    }
    
    #boardNavDesktop::before {
        float: left !important;
        content: url('http://i.imgur.com/PADEO.png') !important;
        margin-top: -2px !important;
        margin-left: -26px !important;
        z-index: 0 !important;
    }

    #boardNavDesktop::after {
        float: right !important;
        content: url('http://i.imgur.com/x8y9k.png') !important;
        margin-top: -2px !important;
        margin-right: -26px !important;
        z-index: 0 !important;
    }
    
    #boardNavDesktop a {
        font-size: 9pt !important;
        color: #1a355e !important;
        text-shadow: 0px 1px 1px #619bf2 !important;
        margin-left: 2px !important;
        font-weight: bold !important;
    }
    
    #boardNavDesktop a[class="current"]{
        color: rgba(255,255,255,0.95) !important;
        font-weight: bold !important;
        text-shadow: none !important;
    }
    
    #boardNavDesktop > * {
        display: none;
    }
    
    #boardNavDesktop a::before {
        content: none !important;
    }
    
    #boardNavDesktop a::after {
        content: "\A0/" !important;
        color: #1a355e !important;
        text-shadow: 0px 1px 1px #619bf2 !important;
    }
     
   

    #boardNavDesktop a[href*="/a/"],
    #boardNavDesktop a[href*="/b/"],
    #boardNavDesktop a[href*="/g/"],
    #boardNavDesktop a[href*="/v/"],
    #boardNavDesktop a[href*="/ck/"]{
        display: inline !important;
    }

    .navLinks {
        font-size: 0px !important;
        color: transparent !important;
    }
    
    /*Global message integration into the top bar*/
    #globalToggle {
        display: inline !important;
        position: fixed !important;
        top: 7px !important;
        right: 4% !important;
        z-index: 4 !important;
        margin-right: 114px !important;
        cursor: default !important;
        text-decoration: none !important;
        cursor: pointer !important;
        
        color: transparent;
        font-size: 0;
    }

    
    #globalToggle::after {
        content: url('http://i.imgur.com/fUlWM.png');
    }
    
    a#globalToggle.button.redButton.mobile.shown::after{
        content: url('http://i.imgur.com/iIlVN.png');
    }
    
    .globalMessage {
        position: fixed !important;
        width: 60% !important;
        height: auto !important;
        top: 25px !important;
        right: 20% !important;
        padding-bottom: 4px !important;
        padding-top: 4px !important;
        z-index: 1;
        
        box-shadow: 0px 1px 1px rgba(0,0,0,0.2) !important;
        border-bottom-left-radius: 2px !important;
        border-bottom-right-radius: 2px !important;
        background: #3369ba !important;
    }
    
    .globalMessage.hideMobile {
        display: none;
    }
    
    #globalMessage h3 {
        font-size: 10pt !important;
        color: #eceef0 !important;
        font-weight: normal !important;
        margin: 2px !important;
    }
    
    #globalMessage strong, h2 {
        margin: 2px !important;
        font-size: 10pt !important;
        color: #eceef0 !important;
        font-weight: normal !important;
    }
    
    .globalMessage a {
        color: #fff !important;
        font-weight: bold !important;
        text-decoration: none !important;
    }
    
    .globalMessage a:hover {
        color: #ddd !important;
    }
    /*Thread statistics and thread updater integration into the navbar*/
    #stats {
        float: left;
        position: fixed !important;
        top: 7px !important;
        left: 4% !important;
        margin-left: 50px !important;
        bottom: auto !important;
        right: auto !important;
        border: none !important;
        z-index: 4 !important;
        width: auto !important;
        
        font-size: 9pt !important;
        color: #1a355e !important;
        text-shadow: 0px 1px 1px #619bf2 !important;
        background-color: transparent !important;
    }
    
    #updater {
        float: left;
        display: inline !important;
        position: fixed !important;
        left: 4% !important;
        right: auto !important;
        top: 7px !important;
        bottom: auto !important;
        z-index: 3 !important;
        height: 12px !important;
        margin-left: 0px !important;
        
        font-size: 9pt !important;
        color: #1a355e !important;
        text-shadow: 0px 1px 1px #619bf2 !important;
        background-color: transparent !important;
    }
    
    #updater div, #stats div {
        cursor: default !important;
    }
    
    #updater:hover {
        border: none !important;
    }
    
    #updater:hover > div {
        background: #3a78d4 !important;
        text-align: left !important;
        border-bottom-left-radius: 2px !important;
        border-bottom-right-radius: 2px !important;
        box-shadow: 0px 1px 1px rgba(0,0,0,0.2) !important;
    }
    
    #updater input[name="Interval"] {
        font-size: 9pt !important;
        margin-left: 2px !important;
    }
    
    #count.new {
        background: transparent !important;
        color: rgba(255,255,255,0.95) !important;
    }

    input[value="Update Now"], input[value="Previous"], input[value="Next"] {
        font-size: 9pt !important;
    }
    
    
    .postInfo .subject:not(:empty)::after,
    .nameBlock::after,
    .container::before {
        color: #e4e8ee !important;
        content: "\A0\26AB\A0" !important;
        font-weight: normal !important;
        font-size: 8px !important;
    }
    
    .postInfo .subject:not(:empty)::after,
    .dateTime::after {
        color: #e4e8ee !important;
        content: "\A0\A0\26AB\A0" !important;
        font-weight: normal !important;
        font-size: 8px !important;
    }
    
    /*Settings integration into the navbar*/
    a.settingsWindowLink {
        position: fixed !important;
        top: 6px !important;
        right: 4% !important;
        z-index: 4 !important;
        
        font-family: Helvetica Neue !important;
        font-size: 0 !important;
        color: transparent !important;
    }
    
    a.settingsWindowLink {
        font-size: 9pt !important;
        color: #ccc !important;
        cursor: pointer !important;
        font-family: Helvetica Neue !important;
        font-size: 0 !important;
        color: transparent !important;
    }

    a.settingsWindowLink::before {
        content: url('http://i.imgur.com/UJdlZ.png');
    }

    /*Expand control integration into the navbar*/
    #imgControls {
        font-size: 0px !important;
        color: transparent !important;
    }
    
    input[type="checkbox"]#imageExpand {
        display: none !important;
    }
    
    #imgControls > *::before {
        position: fixed !important;
        content: url('http://i.imgur.com/utyjI.png');
        top: 7px !important;
        right: 4% !important;
        margin-right: 85px !important;
        z-index: 4 !important;
        cursor: pointer !important;
    }
    
    #imgControls >*:active::before {
        content: url('http://imgur.com/IDoxo.png');
    }

    .field {
        font-family: Lucida Grande !important;
        font-size: 9pt !important;
        background: #ffffff !important;
    }
    
    .prettyprint {
        background-color: #fdfdfd !important;
        border: 1px #fff solid !important;
        border-radius: 2px !important;
        box-shadow: inset 0px 1px 3px rgba(0,0,0,0.2) !important;
        
        margin-bottom: -6px !important;
        margin-top: 1px !important;
        font-size: 9pt !important;
        padding-bottom: 2px !important;
        padding-top: 4px !important;
    }

    .post.reply {
        background: transparent !important;
        border: none !important;
        margin-top: -1px !important;
        margin-bottom: -1px !important;
        width: 100% !important;
        border-radius: 2px !important;
    }

    .hide_reply_button {
        position: relative !important;
        float: right !important;
        margin-top: 2px !important;
        margin-right: 8px !important;
        z-index: 1;
        
        font-family: Lucida Grande !important;
        font-size: 0px !important;
        color: transparent !important;
    }
    
/*Disable thread hiding. To do: fix the stubs and position the button correctly!*/
    .hide_thread_button {
        display: none !important;
        /*font-size: 10px !important;
font-family: Lucida Grande !important;
color: #ff0000 !important;
float: right !important;
margin-right: 35px !important;*/
    }
    
    .hide_reply_button a::before {
        content: "-" !important;
        font-size: 9pt !important;
        color: #bebebe !important;
        z-index: 5 !important;
    }
    
    /*.hide_thread_button::after {
content: "-" !important;
font-size: 9pt !important;
color: #ff0000 !important;
z-index: 5 !important;
}*/
    
    .hide_reply_button.stub {
        height: 16px !important;
        padding-top: 2px !important;
        padding-bottom: 2px !important;
        margin-top: 0px !important;
        margin-bottom: 0px !important;
        margin-right: 0px !important;
        width: 100% !important;
        background: #f3f3f5 !important;
        border-radius: 2px !important;
    }
    
    .hide_reply_button.stub a {
        margin-left: 0px !important;
        color: #999faa !important;
        font-family: Lucida Grande !important;
        font-size: 9pt !important;
    }
    
    .hide_reply_button.stub a::before {
        display: none !important;
    }
    
    .hide_reply_button.stub > a:hover {
        color: #7a7f88 !important;
    }

    .hide_reply_button.stub span {
        font-size: 0px !important;
        color: transparent !important;
        font-family: Lucida Grande !important;
    }
    
    .hide_reply_button.stub .menu_button {
        font-size: 0px !important;
        color: transparent !important;
        font-family: Lucida Grande !important;
        margin-top: 0px !important;
        margin-right: 3px !important;
    }

    .hide_reply_button.stub .menu_button::before {
        font-size: 0px !important;
    }
        

    .hide_reply_button.stub a:first-of-type::after {
        float: right !important;
        margin-top: 0px !important;
        margin-right: 7px !important;
        content: "+" !important;
        font-size: 9pt !important;
        color: #999faa !important;
        font-family: Lucida Grande !important;
    }

    .favicon {
        display: inline !important;
        float: right !important;
        margin-top: 0px !important;
        width: 16px !important;
        margin-left: 0px !important;
        margin-right: -14px !important;
        height: 16px !important;
        opacity: 0.0 !important;
    }

    .replylink {
        float: right !important;
        margin-left: 5px !important;
        margin-right: 5px !important;
        margin-top: 1px !important;
        color: transparent !important;
    }

    a.replylink {
        font-size: 0px !important;
    }

    .replylink:before {
        content: url("http://imgur.com/WpNCh.png") !important;
    }
    
    /*Icon that overlays the favicon*/
    .opContainer > div .postInfo:before {
        float: right !important;
        content: url('http://i.imgur.com/UNhyj.png') !important;
        margin-left: 0px !important;
        margin-right: 6px !important;
        margin-top: 0px !important;
    }

    .menu_button {
        float: right !important;
        margin-top: 0px !important;
        margin-right: 20px !important;
        margin-left: 10px !important;
        font-family: Lucida Grande !important;
        font-size: 0px !important;
        color: transparent !important;
    }

    .menu_button::after {
        content: "\25BE" !important;
        font-size: 9pt !important;
        color: #bebebe !important;
    }
    
    .post.op .menu_button::after {
        color: #bebebe !important;
    }

    .post.op .menu_button {
        margin-top: 0px !important;
        margin-right: 0px !important;
        margin-left: 0px !important;
    }

    #menu.reply.dialog {
        display: block !important;
        margin-left: 0px !important;
        margin-top: -1px !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 1px rgba(0,0,0,0.2) !important;
    }

    .subMenu {
        left: -110px !important;
        width: 108px !important;
    }

    .entry, .subMenu {
        background: #ffffff !important;
        border-color: #eceef0 !important;
        color: #7a7f88 !important;
        font-family: Lucida Grande !important;
        font-size: 8pt !important;
    }
    
    .entry:hover {
        color: #5c6066 !important;
    }
    
    .opContainer {
        display: table !important;
        height: 100% !important;
        width: 100% !important;
        padding-top: 10px !important;
        margin-top: 10px !important;
    }

    #options.reply.dialog {
        background: #fff !important;
        color: #5c6066 !important;
        border-radius: 2px !important;
        /*box-shadow: 0px 1px 1px rgba(0,0,0,0.3) !important;*/
        outline: 5px solid rgba(0,0,0,0.2) !important;
        border: none !important;
        z-index: 5 !important;
    }
    
    #options.reply.dialog label {
        text-decoration: none !important;
        font-style: italic !important;
        color: #2e3033 !important;
    }
    
    #optionsbar {
        border-bottom: 1px solid #eceef0 !important;
        padding: 5px !important;
        margin: -5px !important;
    }
    
    #overlay {
        z-index: 5 !important;
    }
    
    .dialog {
        background: #fff !important;
        color: #2e3033 !important;
    }
    
    /*Thread watcher integration into the navbar*/
    #watcher {
        position: fixed !important;
        top: -1000px !important;
        right: 0px !important;
        left: auto !important;
        width: 300px !important;
        z-index: 3 !important;
        
        font-size: 10pt !important;
        background: #3369ba !important;
        box-shadow: 0px 6px 5px rgba(0,0,0,0.2) !important;
        border-bottom-left-radius: 3px !important;
        border-bottom-right-radius: 3px !important;
        border: none !important;
    }
    
    #watcher > div {
        max-width: 300px !important;
        width: 300px !important;
        overflow: hidden !important;
    }
    
    #watcher .move {
        font-size: 0px !important;
        height: 10px !important;
        margin-top: -10px !important;
        cursor: default !important;
        z-index: 3 !important;
    }
    
    #watcher::before {
        content: url('http://i.imgur.com/Q35tr.png');
        height: 16px !important;
        max-width: 10px !important;
        min-width: 10px !important;
        
        position: fixed !important;
        top: 7px !important;
        right: 4% !important;
        margin-right: 63px !important;
        padding-bottom: 4px !important;
        z-index: 3 !important;
        cursor: pointer !important;
    }

    #watcher:active::before {
        content: url('http://i.imgur.com/Ckb9n.png');
    }

    #watcher:active {
        position: fixed !important;
        z-index: 3 !important;
        top: 20px !important;
        float: right !important;
        right: 5% !important;
        margin-top: 5px !important;
        cursor: default !important;
        box-shadow: inset 0px 3px 3px -1px rgba(0,0,0,0.3) !important;
    }
    
    #watcher a {
        color: #eceef0 !important;
        text-decoration: none !important;
        margin-left: -2px !important;
    }
    
    #watcher a[href="javascript:;"] {
        color: #132745 !important;
        text-shadow: 0px 1px 1px #619bf2 !important;
    }
    
    #watcher a:hover {
        color: #fff !important;
    }
    
    .fileText {
        margin-left: -5px !important;
    }
    
    .fileText, .fileInfo, .fileInfo a {
        font-size: 9pt !important;
    }
    
    .fileInfo a {
        text-decoration: none !important;
    }
    
    .fileText > a {
        margin-left: 10px !important;
    }
 
    .post.reply > .file > .fileInfo {
        margin-left: -2px !important;
        font-size: 10pt !important;
        background: #f5f5f7 !important;
        margin-right: 2px !important;
        padding-bottom: 2px !important;
    }

    .post.op .file {
        background: transparent !important;
        margin-right: 0px !important;
        margin-top: -10px !important;
        padding-top: 2px !important;
        padding-bottom: 2px !important;
        border-top-left-radius: 2px !important;
        border-top-right-radius: 2px !important;
    }
    
    .post.op .fileText a {
        font-style: normal !important;
    }
    
    .post.op .fileThumb {
        margin-left: 22px !important;
        margin-bottom: 16px !important;
        
    }

    .backlink:first-child::before {
        margin-left: -4px !important;
    }
    
    .backlink {
        font-size: 0 !important;
    }
    
    .backlink:after {
        content: url('http://i.imgur.com/S16zbNg.png') !important;
        font-size: 9pt !important;
        margin-right: 4px !important;
    }
    
    .backlink.filtered:after {
        opacity: 0.55 !important;
    }
    
    .post.op .backlink:first-child {
        margin-left: 0px !important;
    }
 
